(137, '_site_transient_community-events-166ff35f3a4d10d6c7cbdb100a56e080', 'a:4:{s:9:\"sandboxed\";b:0;s:5:\"error\";N;s:8:\"location\";a:1:{s:2:\"ip\";s:13:\"192.168.240.0\";}s:6:\"events\";a:3:{i:0;a:10:{s:4:\"type\";s:8:\"wordcamp\";s:5:\"title\";s:22:\"WordCamp Taiwan Online\";s:3:\"url\";s:33:\"https://taiwan.wordcamp.org/2021/\";s:6:\"meetup\";N;s:10:\"meetup_url\";N;s:4:\"date\";s:19:\"2021-12-11 13:00:00\";s:8:\"end_date\";s:19:\"2021-12-12 00:00:00\";s:20:\"start_unix_timestamp\";i:1639198800;s:18:\"end_unix_timestamp\";i:1639238400;s:8:\"location\";a:4:{s:8:\"location\";s:6:\"Online\";s:7:\"country\";s:2:\"TW\";s:8:\"latitude\";d:25.0329636;s:9:\"longitude\";d:121.5654268;}}i:1;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:39:\"#wpbn No. 56 - Gute Vorsätze für 2022\";s:3:\"url\";s:67:\"https://www.meetup.com/WordPress-Meetup-Bonn-wpbn/events/282391040/\";s:6:\"meetup\";s:27:\"WordPress Meetup Bonn #wpbn\";s:10:\"meetup_url\";s:50:\"https://www.meetup.com/WordPress-Meetup-Bonn-wpbn/\";s:4:\"date\";s:19:\"2021-12-01 19:00:00\";s:8:\"end_date\";s:19:\"2021-12-01 21:00:00\";s:20:\"start_unix_timestamp\";i:1638381600;s:18:\"end_unix_timestamp\";i:1638388800;s:8:\"location\";a:4:{s:8:\"location\";s:6:\"Online\";s:7:\"country\";s:2:\"DE\";s:8:\"latitude\";d:50.729999542236;s:9:\"longitude\";d:7.0999999046326;}}i:2;a:10:{s:4:\"type\";s:6:\"meetup\";s:5:\"title\";s:46:\"Weinachtsmarkt 2021 • WPMeetup Dortmund  #40\";s:3:\"url\";s:66:\"https://www.meetup.com/WordPress-Meetup-Dortmund/events/281568629/\";s:6:\"meetup\";s:25:\"WordPress Meetup Dortmund\";s:10:\"meetup_url\";s:49:\"https://www.meetup.com/WordPress-Meetup-Dortmund/\";s:4:\"date\";s:19:\"2021-12-02 19:00:00\";s:8:\"end_date\";s:19:\"2021-12-02 21:00:00\";s:20:\"start_unix_timestamp\";i:1638468000;s:18:\"end_unix_timestamp\";i:1638475200;s:8:\"location\";a:4:{s:8:\"location\";s:6:\"Online\";s:7:\"country\";s:2:\"DE\";s:8:\"latitude\";d:51.509998321533;s:9:\"longitude\";d:7.4800000190735;}}}}', 'no'),
