(139, '_transient_feed_9bbd59226dc36b9b26cd43f15694c5c3', 'a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:79:\"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:27:\"News –  – WordPress.org\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:26:\"https://wordpress.org/news\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"The latest news about WordPress and the WordPress community\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:13:\"lastBuildDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 29 Nov 2021 13:25:05 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"en-US\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"generator\";a:1:{i:0;a:5:{s:4:\"data\";s:40:\"https://wordpress.org/?v=5.9-alpha-52276\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"item\";a:20:{i:0;a:6:{s:4:\"data\";s:55:\"\n		\n		\n		\n		\n		\n				\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:49:\"WP Briefing: Episode 21: All Things Block Themes!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:70:\"https://wordpress.org/news/2021/11/episode-21-all-things-block-themes/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 29 Nov 2021 12:07:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=11636\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:424:\"In episode 21 of the WordPress Briefing, Executive Director, Josepha Haden Chomphosy, talks all things block themes with developers and theme specialists Maggie Cabrera and Jeff Ong. Have a question you&#8217;d like answered? You can submit them to wpbriefing@wordpress.org, either written or as a voice recording. Credits Editor: Dustin Hartzler Logo: Beatriz Fialho Production: Chloé Bringmann Song: Fearless [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2021/11/WP-Briefing-021.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:26769:\"\n<p>In episode 21 of the WordPress Briefing, Executive Director, Josepha Haden Chomphosy, talks all things block themes with developers and theme specialists Maggie Cabrera and Jeff Ong.</p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2 id=\"credits\">Credits</h2>\n\n\n\n<ul><li>Editor: <a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a></li><li>Logo: <a href=\"https://profiles.wordpress.org/beafialho/\">Beatriz Fialho</a></li><li>Production: <a href=\"https://profiles.wordpress.org/cbringmann/\">Chloé Bringmann</a></li><li>Song: Fearless First by Kevin MacLeod</li></ul>\n\n\n\n<h2 id=\"references\">References </h2>\n\n\n\n<ul><li><a href=\"https://make.wordpress.org/core/2021/11/17/wordpress-5-9-beta-1-delayed/\">5.9 Beta 1 Delay Announcement </a></li><li><a href=\"https://make.wordpress.org/core/2021/11/22/wordpress-5-9-revised-release-schedule/\">5.9 Revised Release Schedule</a></li><li><a href=\"https://sevilla.wordcamp.org/2021/\">WordCamp Sevilla 2021</a></li><li>Charitable Giving: <ul><li><a href=\"https://wordpressfoundation.org/donate/\">WordPress Foundation</a></li><li><a href=\"https://donate.bigorangeheart.org/\">Big Orange Heart</a></li><li><a href=\"https://heropressnetwork.com/support/\">HeroPress</a></li></ul></li></ul>\n\n\n\n<h2 id=\"transcript\">Transcript</h2>\n\n\n\n<span id=\"more-11636\"></span>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:11</p>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing, the podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Josepha Haden Chomphosy. See, here we go!</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:40</p>\n\n\n\n<p>Well, today, folks, in our podcast, I am joined by a couple of special guests. I know it&#8217;s been a bit since I&#8217;ve had a guest, so I&#8217;m very excited to introduce you to who I have with me today. Today, I have <a href=\"https://profiles.wordpress.org/onemaggie/\">Maggie Cabrera</a> and <a href=\"https://profiles.wordpress.org/jffng/\">Jeff Ong</a>. They both are working on themes, and especially the future of themes as we move into this low code, no code block based experience of editing things in WordPress. And there have been so many questions lately about what does the landscape of being a theme developer turns into once we move fully into this excellent promise of user empowerment for Gutenberg? I figured who best to come and talk to us about that than these two. So welcome, Maggie. Welcome, Jeff. I&#8217;m really excited to have this conversation with you today.</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>01:39</p>\n\n\n\n<p>Thank you for having us. Excited to be here.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:45</p>\n\n\n\n<p>I&#8217;m just gonna hop right in, and we will see what happens. The first thing that I want to chat about, I hear so many questions and so much discussion about patterns in a lot of different places. Like obviously, the work that I helped to steward the most is around like the Block Pattern directory and various other user-facing tools. And so I have never really been able to give a really solid answer about like patterns and how they work inside themes. And so I wondered if you all had anything that you could offer to our listeners to help clarify what is the power of patterns inside themes in the future? Implementation of themes?</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>02:34</p>\n\n\n\n<p>I can try to start unless, Maggie? Okay. Well, if you take a look at what I&#8217;ve been doing for the last couple of months working on Twenty Twenty-Two. And if you look at that theme, it&#8217;s mostly just the collection of patterns. Patterns. As you know, if you read the description, the theme, it&#8217;s designed to be the most flexible and kind of like flexible theme ever, dare I say ever created.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:04</p>\n\n\n\n<p>I think you can dare to say it.&nbsp;</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>03:03</p>\n\n\n\n<p>And, you know, I think a huge part of that is because of the Full Site Editing being launched, introduced in 5.9. And also that theme itself ships with all of these patterns in it that work with the overall design, but really can be configured to your own kind of unique liking and kind of taste and ultimately, what you want to accomplish, whether that&#8217;s I want to make a portfolio, I want to make, you know, a single-page website promoting like my podcast, or there are patterns for that kind of shipping with the theme. And they&#8217;ve all been kind of designed and tailored to work with the typography choices at a baseline level with the color choices at a baseline level, but can very easily be tweaked. And you can kind of rely on them to work with the editor. And I guess, kind of zooming out for a little bit, not just about Twenty-Twenty Two. </p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>04:04</p>\n\n\n\n<p>But like patterns as this idea that a theme, hopefully, what it is, it&#8217;s a collection of different design options or layout options that are ultimately presented as patterns to the user, the patterns are just a really easy way to basically say &#8220;I want you this layout, like two columns of text or with like some images here.&#8221; Basically, a theme becomes a way of packaging the patterns together in a way that feels like a coherent piece of a coherent website. And I think that&#8217;s a pretty powerful idea. I know that the patterns directory is also opening up making those patterns pretty widely available. But I think a theme you could think of as like a curation of those patterns in a way that makes sense. And I think Twenty-Twenty Two is a really good example. I mean, I&#8217;m biased.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:03</p>\n\n\n\n<p>I also think it&#8217;s a good example. Maggie, did you have anything that you wanted to add to that?</p>\n\n\n\n<p><strong>Maggie Cabrera&nbsp; </strong>05:08</p>\n\n\n\n<p>Yeah, What I really like about patterns is how it empowers the user, even if they don&#8217;t really have like a deep knowledge of code, or they&#8217;re not used to the more complex blocks. When the theme developer gives you this pattern about using the query block, for example, it lays out your posts in a very compelling manner. And you can edit it if you want it or just use it out of the box. And you have this dynamic blog that it&#8217;s, like, such a big important part of your website. Like if you want to have a page where you have, you have maybe a podcast website, and you want to showcase your podcasts differently than your regular blog posts. So you can use a different gray pattern for that. And it&#8217;s like, really, really easy to use, even if you&#8217;re not familiar with it.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:07</p>\n\n\n\n<p>One of the things that I have found compelling about this new version of themes and kind of the way that themes are planning to look in the future; it&#8217;s going to be like a super throwback, so everyone get ready for me to sound old, my guests and my listeners alike. It reminds me of my original days of blogging on the web. I was not a developer and even though I had this really short stint of working with JavaScript in my career, at some point. Like no one actually would ever look to me and be like, that one is excellent at design and fixing everything with code, like I was just killer at searching for the right pieces of code, right. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:57</p>\n\n\n\n<p>And so I remember sitting there on Zynga, which is, of course, now powered by WordPress, I absolutely just went out and found bundles of code that are now what we would consider themes and modified the small pieces that I needed to change in order to like really suit what I wanted to have happen on the site at the time. And they&#8217;re like, I knew I could break it all. Really easily. But also, it was, it was not scary to think about breaking it. Like it was clear how I could fix it if I really broke it. The content, like what I had written, was separate from everything to do with the way that it was looking. And so like, I wouldn&#8217;t destroy all of my work, just because I didn&#8217;t put a semicolon in the right place, or whatever it was in that moment. And so like, this future of themes really reminds me of this a lot where someone has curated how it can look how it should look. And you can just like add in modular pieces that will augment what was already intended, but still kind of work. And if it&#8217;s not gonna work, it&#8217;s kind of easy to fix too. So like, I&#8217;m excited. That was a really exciting time in my learning of the web and certainly was formative in my career, as we all now see. And so yeah, I think that&#8217;s really exciting. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:20</p>\n\n\n\n<p>I did have actually another question that this conversation has kind of brought up for me. I have, obviously just use the term modular, which no one has ever used in the context of themes for WordPress. And I know that there is a lot there are a lot of terms kind of wandering around about themes right now. And especially as we&#8217;re moving into what themes can look like in the future. There was block based theme as a term for a while. And now it&#8217;s block themes. There was like this floating around the term, universal themes. And now we&#8217;re looking at just like block themes forever. And so I wondered if y&#8217;all could give us a just like a clear understanding of these terms that had been being used and maybe are going out of fashion? Like, are they important for us to keep knowing?</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>09:11</p>\n\n\n\n<p>So yes, the history of terms around themes. And obviously, even my knowledge only goes back so far. But it was around when we started doing the block based themes meeting. And trying to I think that&#8217;s where that term kind of came from is like, oh, let&#8217;s, let&#8217;s start talking about this idea that themes can be completely made up out of blocks. And what does that mean?&nbsp;</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>09:33</p>\n\n\n\n<p>I think over time, it wasn&#8217;t just block themes, because, you know, previously, there were themes and even default themes that used and took into account the fact that blocks existed. So there was some confusion there. Enough time has gone on where we focus on this idea that themes whose templates are ultimately made out of blocks are block themes. And to me, it&#8217;s kind of as simple as that. Its themes that supply a set of templates that previously in the past were a collection of PHP and various template tags and whatnot is all transitioned to themes made up including other blocks, as well as themes that supply styles through theme.json configuration instead of supplying it in raw CSS. To me this idea is really crystallizing around like this is a block theme, one that is really, at its core, supplying a set of templates, and styles through a language that WordPress understands natively, and can allow it to be configured and customized in a really powerful way. And then maybe someday in the future, they&#8217;ll just be called themes again. If we do a good enough they will just be called themes.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>10:57</p>\n\n\n\n<p>I&#8217;m gonna, I&#8217;m gonna take us into a philosophical area now that you&#8217;ve just put us in there. You said, someday they&#8217;ll be called Themes. Again, I&#8217;ve talked about this on this podcast a few times. And for anyone who&#8217;s worked with me for any length of time, like you all probably heard this from me as well. But like, adjectives are so frequently the realm of things that are not what you expect, right? Because like you have coffee, and then decaf coffee, no one&#8217;s like caffeinated coffee, because that&#8217;s what you expect out of it. And so when you&#8217;re like themes, and block themes, it makes it look like block themes are secondary, which at the moment, they are, ish. But in the future, I think you&#8217;re probably right, there will be a time when the modifier isn&#8217;t necessary anymore because it will be hopefully a much better way for people to kind of change the way that their themes work and make it more usable for users and people who are, you know, having to manage their own site without necessarily wanting to or being able to, like, have a Maggie in the room to fix everything that they break. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:08</p>\n\n\n\n<p>Maggie is nowhere near me. And so she&#8217;s never been in the room when I&#8217;ve broken anything. But I believe that Maggie on one occasion, at least, has come in and helped me fix something that I definitely broke. I&#8217;m an excellent breaker of WordPress things. Maggie, did you have anything you wanted to add to that question? </p>\n\n\n\n<p>12:29</p>\n\n\n\n<p>Yeah, I guess, maybe clarify a bit, what universal themes are because, yeah, maybe some people have heard about the term but they don&#8217;t really know what they are. And maybe just clarify that. The term was born when developing block themes wasn&#8217;t something that you could actually do for production websites like you could build them to test some experiments, but they weren&#8217;t really ready for users to use. So universal themes want to grasp the power of love themes while still being ready for users. So the way they do it is they are block based, like we used to call them in the sense that the templates are made of blocks. But they are also able to be customized using the customizer, which is the old way of customizing themes, instead of using the site editor. So they can have a balance between two worlds between the worlds of classic themes and block themes. But they are, at heart, a temporary concept. They are bound to be blocked themes in the future, but with maybe a foot in the past, where they can actually serve users who are not ready for full-on site editor. But they are bound to be full block themes in the future.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>14:00</p>\n\n\n\n<p>There&#8217;s a really interesting concept in there. So universal themes, it sounds like are basically kind of like an on-ramp for people who are not really ready to fully commit to this for any number of reasons. Like we never want to say that we know the reason that people would be a little bit shy to get started with this. But like it&#8217;s kind of like an on-ramp, it&#8217;s a safe way to get back to something that they do know, in the event that what they don&#8217;t know, really hinders their progress makes it hard for them to get the work done.</p>\n\n\n\n<p><strong>Maggie Cabrera&nbsp; </strong>14:31</p>\n\n\n\n<p>I think I wouldn&#8217;t say that they are for people shyer to get into new stuff rather than developers who want to embrace the new stuff before it&#8217;s even really ready. They really want to embrace the power of the blocks instead of doing things the old way. But even if it&#8217;s not fully ready.</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>14:54</p>\n\n\n\n<p>They still need to support the old way of doing things.</p>\n\n\n\n<p><strong>Maggie Cabrera&nbsp; </strong>14:58</p>\n\n\n\n<p>Like being backward compatible and being ready for any kind of user.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>15:05</p>\n\n\n\n<p>So mostly for developers, everyone who heard me just talking about how it was a great thing for users, ignore it. </p>\n\n\n\n<p><strong>Maggie Cabrera&nbsp; </strong>15:12</p>\n\n\n\n<p>It&#8217;s also good for users; If they feel secure in using the customizer.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>15:20</p>\n\n\n\n<p>Well, I think that there&#8217;s something important here that we certainly learned with the adoption of Gutenberg in 5.0. Right, which is that there is certainly one method of helping people to adopt things, which is to go like the art of war style, and kind of smash their rice pots and burn all their boats, like, that&#8217;s one way. Which works for a lot of companies in the world, I&#8217;m sure. But WordPress has always had kind of a commitment, not even kind of, has always had a commitment to backward compatibility. And like, we know that a lot of the work on Gutenberg is going to represent some breaking changes around the around workflows and around the user experience the interface, especially like, we know that. But the opportunity to like have a thing that gives you an early taste of what&#8217;s coming but also the ability to keep kind of working in your old space where you need to, I think it&#8217;s an excellent way to bring people forward into the future of things, I have never been a fan of the just like cut off all avenues and hope that they stay with your method because of course, like you can&#8217;t cut off all the methods. You can&#8217;t cut off all the ways people can get away from you. And even if we could, it wouldn&#8217;t be in line with how WordPress hopes to kind of help people through some tough stuff like making your first website is hard. If you are doing it as part of, an overall campaign that&#8217;s supposed to bring in leads for you or generate revenue like you don&#8217;t want to necessarily play with that in a way that could break things and be risky for you in the long term. So I think all the tools that we offer to help people kind of move forward with the technology move forward with the CMS as it&#8217;s moving forward, I think it&#8217;s really smart. And so universal themes are one of those things, but also not around to stay. As we move into non modified themes, just the word themes that happen to be based in blocks. If I&#8217;ve confused anyone, please email me at wpbriefing@wordpress.org. And tell me how I confused you. And I will do a follow-up to unconfuse everyone. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>17:40</p>\n\n\n\n<p>So speaking of the way that we help people kind of move forward with WordPress and with the technology. So much has been done in the CMS in the past 12 months in the past 18 months to be able to move themes into this same future as the rest of the editor. Right. So like, for folks who have not been listening to me for the last five years, you may not know this. So I&#8217;m going to tell everybody now, like one of the pain points that Gutenberg overall is solving is the fact that you for a long time had to learn five different editing interfaces to get one thing done in WordPress, right. And so like the advent of blocks and moving it into more and more spaces in the CMS is intended to really flatten the editing experience by making the type of user interaction the type of workflow really similar across all of the editing interfaces in the CMS. And so themes are a natural extension of that, where we can take similar user patterns and workflows, and work them out into themes. So over the last 12 months or so probably a little bit more, there&#8217;s been a lot of work on the CMS to move us forward in that that is now enabling the work that we want to be able to do to move things forward ahead. And so, I mean, this is probably our last question. Is there anything that you all want to offer to people who maybe saw themes early on or saw Gutenberg early on and felt like this is just not for me, in that in that context of like, how far it&#8217;s moved ahead in the past 12 months or so.</p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>19:23</p>\n\n\n\n<p>So you&#8217;re asking like, what in the last 12 months has maybe like really surprised me or like sticks out to me as something that like, Wow, look how far we&#8217;ve come?</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>19:36</p>\n\n\n\n<p>Yeah, yeah. So like, if you&#8217;re looking at what is the one thing that you saw in the last 12 months, that changed in the CMS that really enabled something wonderful for themes or from the other side of it. Like if someone had looked at themes or WordPress 18 months ago, and now they&#8217;re looking at it and seeing this new and different way to do things with the look and feel of their site, like what is one thing that they should be aware of? On either side of that question,</p>\n\n\n\n<p><strong>Maggie Cabrera&nbsp; </strong>20:05</p>\n\n\n\n<p>I think there&#8217;s more than one thing that has really evolved through this last year, year and a half. Like the maturity of some of the blocks is astounding now, like navigation blog, for example, was really bare-bones at the start and now it&#8217;s full potential, and it&#8217;s really looking really great. I would say the same thing about those days or the features on fire, like how basic it was at the start, was full potential. But now it&#8217;s really, really mature in terms of how much you can do with it. Like, I think the example, the perfect example of that is the work that <a href=\"https://profiles.wordpress.org/kjellr/\">Kjell [Reigstad]</a> has done on Twenty-Twenty Two with the alternative theme.json files, where just changing that file basically feels like a new theme, with just the configuration and the styles. And without writing any CSS without changing any templates. It&#8217;s really, really amazing how that can turn into a reality. And it&#8217;s so easy for users to tinker with that if they want to. And it&#8217;s much easier than having to delve deep into CSS and changing everything in like 2000 lines of code. </p>\n\n\n\n<p><strong>Jeff Ong&nbsp; </strong>21:23</p>\n\n\n\n<p>Yeah, I probably would echo most of that. What the thing that astounds me is global styles and how the UI can be shipping a theme or default theme with basically like, 20 lines of CSS, and have it be one of them. A beautiful, beautiful, like crisp and sharp, like, experience. It&#8217;s super fast. And it&#8217;s like, what this is a theme, you know, I thought a theme was supposed to supply all the styles like no, like, it&#8217;s just yeah. And workers do for you. Exactly. And like that. That&#8217;s pretty amazing to think in the last 12 months, we can go from, you know, shipping 1000s of lines of CSS to you none, it&#8217;s like, Wow, pretty cool. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>22:10</p>\n\n\n\n<p>Well, my friends, thank you so much for joining me today. This has been a really interesting conversation. I hope that all y&#8217;all out listening. Also find it interesting. As I mentioned, if you have any follow-up questions, absolutely. Send them to me via email. And I collect all of my questions that I get through the year for answering at the end of the year, mostly because I don&#8217;t get lots of questions that people want to be answered on this. Everyone just asked me their questions on Twitter and in Slack, which is fine as well. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>22:41</p>\n\n\n\n<p>So, Maggie, Jeff, thank you both for joining me. And I&#8217;m sure that we&#8217;ll talk to you all again soon.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>22:56</p>\n\n\n\n<p>That brings us now to our small list of big things. In the last episode, I got all excited about being in the beta phase. But today, I&#8217;m rolling that back a little bit. As part of our usual open source processes, a group of contributors did a deep dive review on the WordPress 5.9 release and found a workflow that needed some refinement. So we are delaying the beta. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>23:19</p>\n\n\n\n<p>Since we are in the midst of a major commerce slash/sales season, and of course, a lengthy holiday season, that delay also means that it makes sense to delay WordPress 5.9 final release a little as well. And so we are delaying that all the way into 2022 to January 25. For me, the trade-off works really well there. Every decision that we make in open source, of course, has some balance to it. It&#8217;s great for these aspects, it is less great for these aspects over here. But for myself, the opportunity to make sure that we have a really excellent experience for our users and also an opportunity to kind of avoid all of the chaos and hustle and bustle of the end of the year. Really, it seemed like a no-brainer for me. So in case you want to learn a little bit more about why we made the decision and get some insight into the actual milestones and where they have moved now, I&#8217;ll include some posts in the show notes below in case you want to read more and of course, if you have any additional questions you can always ask.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>24:33</p>\n\n\n\n<p>The second small list of big things is that the first back to people WordCamp. I don&#8217;t think that&#8217;s what we&#8217;re calling it. Our first back to people WordCamp is happening in a couple of weeks actually. WordCamp Sevilla is happening in person on December 11. And I&#8217;m so excited I wish I were local, but I&#8217;m not so if you are local stop by their website and pick up your ticket. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>24:55</p>\n\n\n\n<p>And the third thing on our smallest a big things is that it is, of course, charitable giving season. I don&#8217;t know if you do your charitable giving at the end of the year or if that&#8217;s even part of your general ways of giving back. But I can think of two or three charitable organizations inside the WordPress ecosystem. There&#8217;s of course the WordPress Foundation, but also Big Orange Heart and HeroPress. If there are others out there, I certainly do want to know about them. WordPress Foundation also does additional giving on behalf of just like the open web and open source as a whole. So if you&#8217;re the sort of person who does their charitable giving at the end of the year, just a reminder that you have some options inside the WordPress ecosystem if you were trying to figure out some new places to donate to in 2021. </p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>25:42</p>\n\n\n\n<p>And that is your small list of big things. Thank you so much for tuning in today for the WordPress Briefing. Thank you again to our special guests, Maggie and Jeff. I&#8217;m your host Josepha Haden Chomphosy, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11636\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:68:\"Watch State of the Word at a Watch Party with your WordPress Friends\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:104:\"https://wordpress.org/news/2021/11/watch-state-of-the-word-at-a-watch-party-with-your-wordpress-friends/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 24 Nov 2021 18:30:14 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11641\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:322:\"State of the Word 2021 is just around the corner!&#160; Although attending State of the Word in person would be ideal, not all WordPress community members get to enjoy the experience of attending the speech live with friends.&#160; This year, as State of the Word is streamed live for the second time, we want to [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Hari Shanker R\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:7280:\"\n<p><a href=\"https://wordpress.org/news/2021/11/state-of-the-word-2021/\">State of the Word 2021</a> is just around the corner!&nbsp;</p>\n\n\n\n<p>Although attending State of the Word in person would be ideal, not all WordPress community members get to enjoy the experience of attending the speech live with friends.&nbsp;</p>\n\n\n\n<p>This year, as State of the Word is streamed live for the second time, we want to restore that in person camaraderie through State of the Word watch parties for WordPress Community members around the world.</p>\n\n\n\n<blockquote class=\"wp-block-quote\"><p><strong>We encourage WordPress meetup organizers and community members worldwide to (safely) host State of the Word 2021 watch parties —</strong><a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/\"><strong>read this handbook to learn more</strong></a><strong>.</strong></p></blockquote>\n\n\n\n<h2 id=\"why-organize-a-watch-party\">Why organize a watch party?&nbsp;</h2>\n\n\n\n<ul><li>If you are a <a href=\"https://meetup.com/pro/wordpress\">WordPress meetup</a> organizer, many folks in your meetup may be unaware of the State of the Word, and a watch party could be a great opportunity to introduce or remind them.</li><li>As meetup organizers slowly bid goodbye to a tough year, the watch party could be an excellent opportunity to revitalize your group, especially if you haven’t had many events this year.</li><li>Along with your Meetup group members, you get a platform to ask questions directly to Matt Mullenweg.</li><li>And last but not least, even if you are not a Meetup Organizer, a watch party can be the perfect opportunity to reconnect and have a blast with your WordPress friends!</li></ul>\n\n\n\n<h2 id=\"how-do-i-organize-a-state-of-the-word-watch-party\">How do I organize a State of the Word watch party?</h2>\n\n\n\n<p>You can choose to host a watch party online or in person. Check out <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/#schedule-a-state-of-the-word-watch-party-for-your-meetup\">our handbook</a> for detailed instructions on how to schedule an event (including event templates).</p>\n\n\n\n<div class=\"wp-block-columns\">\n<div class=\"wp-block-column\">\n<h3 id=\"online\">Online</h3>\n\n\n\n<p>The simplest way to organize an online watch party is to schedule an online event for your WordPress group and add the State of the Word YouTube streaming link directly on Meetup.com. Alternatively, you can schedule an online meeting using tools like Zoom and broadcast the live stream over there by screen sharing––thereby facilitating better engagement. </p>\n\n\n\n<div class=\"wp-container-61a6012e3d9d2 wp-block-buttons\">\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/#organizing-an-online-watch-party\" target=\"_blank\" rel=\"noreferrer noopener\">Organize an Online Watch Party</a></div>\n</div>\n</div>\n\n\n\n<div class=\"wp-block-column\">\n<h3 id=\"in-person\">In Person</h3>\n\n\n\n<p>If your region meets <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/2021-returning-to-in-person-meetups/\">the guidelines for in person events</a> (if vaccines and testing are freely available), you can organize an in person watch party event (for fully vaccinated OR recently tested OR recently recovered folks) for your WordPress Meetup! Group members can hang out together (following local safety guidelines of course) and watch State of the Word live.</p>\n\n\n\n<div class=\"wp-container-61a6012e3dd47 wp-block-buttons\">\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/#organizing-an-in-person-watch-party\" target=\"_blank\" rel=\"noreferrer noopener\">Plan an In person Watch Party</a></div>\n</div>\n</div>\n</div>\n\n\n\n<div style=\"height:20px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<p><strong>NOTE:</strong> If State of the Word is happening at an odd hour in your timezone, you can still organize a watch party by organizing a replay of live stream, at a date/time that is convenient for your group.</p>\n\n\n\n<blockquote class=\"wp-block-quote\"><p>If your Local WordPress Meetup is organizing an in person watch party, <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/request-swag-for-in-person-watch-parties/\"><strong>fill out this form</strong></a><strong> so that we can ship some swag for your group to celebrate!</strong><br>Deadline: November 30, 2021</p></blockquote>\n\n\n\n<h2 id=\"what-else-do-i-need-to-know-about-organizing-a-state-of-the-word-watch-party\">What else do I need to know about organizing a State of the Word watch party?</h2>\n\n\n\n<p>Excited? To help you get started, we’ve put together a few resources:</p>\n\n\n\n<ul><li><strong>Check out this </strong><a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/\"><strong>handbook</strong></a><strong> for detailed instructions on how to organize a watch party, </strong>be it online or in person.</li><li>Looking for a Zoom Pro account to host your online watch party?&nbsp;<a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/getting-started/special-virtual-events-zoom-request/\">Request a community zoom pro account</a>&nbsp;for your event right away!</li><li>We have prepared some <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/2021-state-of-the-word-watch-parties/email-templates-for-state-of-the-word/\">email templates</a> that Meetup Organizers can use to spread the word in their Meetup groups.</li><li>Don’t forget to share on social media about your watch party events using the hashtag <strong>#StateOfTheWord</strong> so we can join in on the fun!</li></ul>\n\n\n\n<blockquote class=\"wp-block-quote\"><p><strong>NOTE: </strong> The guidelines in this post are primarily aimed at WordPress Meetup organizers. <strong>However, you do not need to be a Meetup organizer to schedule a watch party!</strong> You can simply hang out together with your friends online or in person (while following local safety guidelines) and catch the event live!</p></blockquote>\n\n\n\n<p>If you are planning a watch party for State of the Word, and have questions, please drop us an email to: <a href=\"mailto:support@wordcamp.org\">support@wordcamp.org</a> if you have any questions. We are happy to help you in the best way possible.</p>\n\n\n\n<p><em>The following folks contributed to this post: <a href=\'https://profiles.wordpress.org/anjanavasan/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>anjanavasan</a> <a href=\'https://profiles.wordpress.org/eidolonnight/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>eidolonnight</a> <em><a href=\'https://profiles.wordpress.org/evarlese/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>evarlese</a></em></em> <em>and <a href=\'https://profiles.wordpress.org/rmartinezduque/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>rmartinezduque</a></em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11641\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:63:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:23:\"A Look at WordPress 5.9\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"https://wordpress.org/news/2021/11/a-look-at-wordpress-5-9/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 23 Nov 2021 21:02:02 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:6:\"Design\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:11:\"Development\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:8:\"Features\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11625\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:322:\"WordPress 5.9 is expected to be a ground-breaking release. It will introduce the next generation of themes with Twenty Twenty-Two joining the fun and over 30 theme blocks to build all parts of your site. In anticipation of the January 25th release, we hope you enjoy this sneak peek of 5.9. New design tools will [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Kelly Hoffman\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:2972:\"\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-4-3 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<iframe class=\"youtube-player\" width=\"632\" height=\"356\" src=\"https://www.youtube.com/embed/XvEG9XWD4JI?version=3&#038;rel=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;fs=1&#038;hl=en-US&#038;autohide=2&#038;wmode=transparent\" allowfullscreen=\"true\" style=\"border:0;\" sandbox=\"allow-scripts allow-same-origin allow-popups allow-presentation\"></iframe>\n</div></figure>\n\n\n\n<p>WordPress 5.9 is expected to be a ground-breaking release. It will introduce the next generation of themes with <a href=\"https://make.wordpress.org/core/2021/10/06/introducing-twenty-twenty-two/\">Twenty Twenty-Two</a> joining the fun and over 30 theme blocks to build all parts of your site. <a href=\"https://make.wordpress.org/core/5-9/\">In anticipation of the January 25th release</a>, we hope you enjoy this sneak peek of 5.9.</p>\n\n\n\n<p>New design tools will allow you to create exactly what you want, from adding filters to all your images to fine-tuning the border radius on all your buttons. With WordPress 5.9 providing more design control along with streamlined access to patterns, you can easily change the entire look and feel of your site without switching themes<strong>.</strong></p>\n\n\n\n<p>No matter what you’re editing, whether it&#8217;s crafting a new post or working on a header, improvements to List View make it simple to navigate content regardless of complexity. More improvements and features for everyone are to come in this release and we can&#8217;t wait to see what you create with WordPress 5.9! </p>\n\n\n\n<h2 id=\"stay-tuned\">Stay Tuned</h2>\n\n\n\n<p>Stay tuned for more updates as the date draws near. If you want to help, the best thing you can do is <a href=\"https://make.wordpress.org/core/handbook/testing/beta-testing/\">test everything</a>! For all the details, <a href=\"https://make.wordpress.org/core/2021/11/22/wordpress-5-9-revised-release-schedule/\">check out this Make Core post</a>.</p>\n\n\n\n<p><em>Video props: <a href=\'https://profiles.wordpress.org/annezazu/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>annezazu</a> (also co-wrote the post) <a href=\'https://profiles.wordpress.org/michaelpick/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>michaelpick</a> <a href=\'https://profiles.wordpress.org/matveb/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>matveb</a> <a href=\'https://profiles.wordpress.org/beafialho/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>beafialho</a> <a href=\'https://profiles.wordpress.org/javiarce/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>javiarce</a> <a href=\'https://profiles.wordpress.org/critterverse/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>critterverse</a> <a href=\'https://profiles.wordpress.org/joen/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>joen</a>.</em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11625\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:56:\"Join us for State of the Word 2021, in person or online!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:90:\"https://wordpress.org/news/2021/11/join-us-for-state-of-the-word-2021-in-person-or-online/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 22 Nov 2021 21:08:15 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11611\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:297:\"As previously announced, State of the Word will be livestreamed from New York City. That means that you can join the fun either online or in person, on December 14, 2021, between 5 and 7 pm EST! To join State of the Word 2021 online, check your Meetup chapter for a local watch party, or [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Josepha\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:1726:\"\n<p>As <a href=\"https://wordpress.org/news/2021/11/state-of-the-word-2021/\">previously announced</a>, State of the Word will be livestreamed from New York City. That means that you can join the fun either online or in person, on <a href=\"https://www.worldtimebuddy.com/?qm=1&amp;lid=5128581,100&amp;h=5128581&amp;date=2021-12-14&amp;sln=17-19&amp;hf=0\">December 14, 2021, between 5 and 7 pm EST</a>!</p>\n\n\n\n<p>To join State of the Word 2021 online, check your Meetup chapter for a local watch party, or simply visit <a href=\"https://wordpress.org/news/\">wordpress.org/news</a>, where the livestream will be embedded.&nbsp;</p>\n\n\n\n<p>If you would like to participate in person in New York City, please request a seat by filling out the registration form by Sunday, November 28. Not all requests will receive a seat due to venue capacity, but everyone who requests one will receive further notification on Tuesday, November 30.&nbsp;</p>\n\n\n\n<p>In person attendees will be asked to show their COVID vaccination card at the venue entrance, and are expected to follow the safety measures in place. Because of these safety measures, there is a maximum of 50 attendees.&nbsp;</p>\n\n\n\n<div class=\"wp-container-61a6012e408e9 wp-block-buttons\">\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://wordcampcentral.survey.fm/request-a-seat-sotw-2021\">Request a seat</a></div>\n</div>\n\n\n\n<p>Whether you participate in person or online, we are so excited to see you on December 14! Don’t forget, State of the Word will be followed by a Question &amp; Answer session. If you have a question for Matt, you can send your question ahead of time to ask-matt@wordcamp.org, or ask during the event in the YouTube chat.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11611\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:55:\"\n		\n		\n		\n		\n		\n				\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:44:\"WP Briefing: Episode 20: WordPress=Blogging+\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:64:\"https://wordpress.org/news/2021/11/episode-20-wordpressblogging/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 15 Nov 2021 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=11556\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:386:\"In this episode, WordPress&#8217;s Executive Director, Josepha Haden Chomphosy, answers two recently asked questions. Tune in to hear what those questions were and her response, in addition to this week&#8217;s small list of big things. Have a question you&#8217;d like answered? You can submit them to wpbriefing@wordpress.org, either written or as a voice recording. Credits [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2021/11/WP-Briefing-020.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:10275:\"\n<p>In this episode, WordPress&#8217;s Executive Director, Josepha Haden Chomphosy, answers two recently asked questions. Tune in to hear what those questions were and her response, in addition to this week&#8217;s small list of big things.</p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2 id=\"credits\">Credits</h2>\n\n\n\n<ul><li>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a></li><li>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/beafialho/\">Beatriz Fialho</a></li><li>Production:&nbsp;<a href=\"https://profiles.wordpress.org/cbringmann/\">Chloé Bringmann</a></li><li>Song: Fearless First by Kevin MacLeod</li></ul>\n\n\n\n<h2 id=\"references\">References </h2>\n\n\n\n<ul><li><a href=\"https://w3techs.com/technologies/details/cm-wordpress\">W3Techs WordPress Usage Statistics </a></li><li><a href=\"https://make.wordpress.org/core/5-9/\">WordPress 5.9 Development Cycle </a></li><li><a href=\"https://make.wordpress.org/updates/2021/10/22/team-rep-nominations/\">Call for Team Rep Nomination</a></li></ul>\n\n\n\n<h2 id=\"transcript\">Transcript</h2>\n\n\n\n<span id=\"more-11556\"></span>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:10</p>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing, the podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Josepha Haden Chomphosy. Here we go!</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:40</p>\n\n\n\n<p>So I was in a meeting recently, which I realize isn&#8217;t saying much for me since I spend a quarter of my time in meetings. But in this particular meeting, I was asked a couple of questions that I absolutely loved. The first question was, “if there were one thing you could change in people&#8217;s minds about WordPress, what would it be?” And my answer, predictable though it may be, was that I want to change the idea that WordPress is just a blogging platform.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:06</p>\n\n\n\n<p>WordPress has grown into a lot more than that. But the idea of a content management system, even now, sometimes gets a mental shorthand where content is a stand-in for the word writing or words. If you&#8217;re using WordPress today in an enterprise context, or as part of a governmental agency, or if you use it in a classroom setting, you know that your content cannot be confined that way. And if you&#8217;re supporting or building anything to hand off to clients, you know that timely, easy-to-ship changes on a site are considered a vital part of any overarching brand and marketing strategy. And when was the last time that any marketing strategy was literally only about the words?&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:51</p>\n\n\n\n<p>So that was the first question. And also my first answer. There is also this kind of annual, not fear, necessarily, but this annual question that is sort of related that is raised to me and has been asked of me recently, that I&#8217;m just going to give you a small answer to. One annual worry that I get every year around November and December is, &#8220;What are we going to do about the fact that the term blog and blogging are declining in search popularity?&#8221; And I was gonna say it&#8217;s been a while since I answered that in any sort of public format. But I think maybe I&#8217;ve never answered it in a public format at all. And so I&#8217;m just going to answer it here. Because I think maybe a lot of people have that same question.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:36</p>\n\n\n\n<p>So number one, I think that the way that people search now is different. There&#8217;s a lot more semantic cognition. This is not the way to answer this &#8212;  search engines are smarter now. So like, it used to be the case with early search engines that yeah, there was a lot of just like, individual search terms that were looked for. But now, people are asking full questions; they have, essentially, an entire sentence that they are searching for. And then, search engines are able to parse that information better and get more high-quality answers and information for them. So like, that&#8217;s one thing that I&#8217;m already not worried about. If people are searching for individual words anymore, it&#8217;s so that they can get a definition of that word. So I&#8217;m not specifically worried about a decline in search volume for the word blog or blogging for that reason. But the answer to my first question, if there is probably the real reason that I&#8217;m not actually super worried about any decline in search volume for the word blog, or blogging, is that WordPress has really moved beyond that. And since we have moved beyond that, then it doesn&#8217;t necessarily make sense for WordPress as an entity for WordPress as a project to get overly hung up on the idea that the term blog has gone out of fashion.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:52</p>\n\n\n\n<p>Okay, now that I did my first question, and the answer, and then an additional question that only ever gets asked in private and is being answered by me for the first time in public, I will tell you now, the second question that I loved, someone asking of me, and that question is this: &#8220;What is one thing you&#8217;d like people to see or experience, right when they first land on wordpress.org?&#8221; Now, I often don&#8217;t get asked questions about the wordpress.org website, like administrative tasks, things that we need to update, move around where they should go. Sure. But like, “Josepha, what&#8217;s the point and purpose of this site?” Never. I&#8217;ve never been asked that, and so I was really excited that someone asked me, and I&#8217;m going to give you a heads up. I think some of you might disagree with my answer.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:40</p>\n\n\n\n<p>So the primary thing that I want people to see or experience when they first get to wordpress.org, the website is the depth of WordPress. Not which audience segment they should belong to or that we believe they should belong to or raw data about the CMS or even how much we care about the freedoms of open source. Now the first thing I want people to see on that site is that WordPress has not only 18 years of learned knowledge that every single new user benefits from, but that it also has 1,000s of really smart people making sure it works and gets better every day, now.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:19</p>\n\n\n\n<p>WordPress is a Goliath in its field. I know that we cite this bit of context. Frequently, we say that we are 42% of the web. And that is true that is the percentage by usage. But in its field, which is websites that are using a content management system, we actually have a 65% market share. This is very easy to find. It&#8217;s on the W3Techs website: I can put a link in the show notes, but you could find it just by searching for it.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:46</p>\n\n\n\n<p>So WordPress is a Goliath in its field of websites that are run using a CMS. Because we have always brought our learnings forward with us with the understanding that knowledge, when shared grows rather than diminishes. But open source, the heart of what defines this project, open source is not a Goliath; it&#8217;s barely even David somedays. Even though the web is built on scads of open source software, there&#8217;s a pervasive public perception that it is built by and for hobbyists or that it is inherently risky, and that if there were if it were worth something, then people would pay something. And I just know that if the first impression of WordPress, we&#8217;re, &#8220;we&#8217;ve got 18 years of experience and learning that brought us to today,&#8221; the rest of the sale to adopt software that protects other people&#8217;s freedoms would take care of itself. And I guess, to quote John Oliver, at this point, &#8220;And now this.&#8221;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:59</p>\n\n\n\n<p>Alright, that brings us now to our small list of big things. There are actually quite a few big things on this small list today. So number one, we have reached the beta phase for the year&#8217;s final release, which means that WordPress 5.9 beta one is happening tomorrow, Tuesday, November 16. And then seven days later, I believe on the 23rd, if I recall correctly, comes beta two.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>07:24</p>\n\n\n\n<p>The second thing on my list is that team rep nominations are happening all over the project right now. I&#8217;ve got a post that I will share in the notes below that I believe all the team reps have put their team&#8217;s nomination posts on. So if you have had an interest in learning more about that and what it means to help keep teams kind of running in the WordPress project, then this is a great opportunity to check those out.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>07:49</p>\n\n\n\n<p>And the third thing, this last thing actually isn&#8217;t in the next two weeks, but it is very important, nonetheless. Matt&#8217;s annual State of the Word is coming up on December 14. So basically a month from today. It&#8217;s going to join the growing list of in-person events that are on the calendar. It will be in New York City but will also be live-streamed across the world as usual. Keep an eye out for additional updates about that for anyone who, like me, really looks forward to this particular presentation from our project co-founder every year.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:25</p>\n\n\n\n<p>And that is your small list of big things. Thank you for tuning in today for the WordPress briefing. I&#8217;m your host, Josepha Haden Chomphosy, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11556\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:5;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:22:\"State of the Word 2021\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"https://wordpress.org/news/2021/11/state-of-the-word-2021/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Sat, 13 Nov 2021 00:38:05 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11567\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:346:\"Howdy, World!&#160; Mark your calendars; it’s almost time for State of the Word 2021! State of the Word is the annual keynote address delivered by the WordPress project’s co-founder, Matt Mullenweg. Every year, the event allows us to reflect on the project’s progress and the future of open source. This year will include that and [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Josepha\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:5054:\"\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large\"><img loading=\"lazy\" width=\"632\" height=\"430\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/11/SOTW-Post-e1636763589410-1024x696.png?resize=632%2C430&#038;ssl=1\" alt=\"State of the Word 2021\" class=\"wp-image-11568\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/11/SOTW-Post-e1636763589410.png?resize=1024%2C696&amp;ssl=1 1024w, https://i2.wp.com/wordpress.org/news/files/2021/11/SOTW-Post-e1636763589410.png?resize=300%2C204&amp;ssl=1 300w, https://i2.wp.com/wordpress.org/news/files/2021/11/SOTW-Post-e1636763589410.png?resize=768%2C522&amp;ssl=1 768w, https://i2.wp.com/wordpress.org/news/files/2021/11/SOTW-Post-e1636763589410.png?w=1200&amp;ssl=1 1200w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /><figcaption>State of the Word 2021 is happening on Dec 14!</figcaption></figure></div>\n\n\n\n<p>Howdy, World!&nbsp;</p>\n\n\n\n<p><a href=\"https://calendar.google.com/calendar/u/0/r/eventedit?text=State+of+the+Word+2021+livestream&amp;dates=20211214T220000Z/20211215T000000Z&amp;details=State+of+the+Word+is+the+annual+keynote+address+delivered+by+the+WordPress+project%27s+co-founder,+Matt+Mullenweg.%0AJoin+Matt+as+he+provides+a+retrospective+of+2021,+celebrates+the+community%27s+amazing+wins,+discusses+the+latest+trends+he%27s+seeing,+and+explores+the+future+of+open+source.%0AState+of+the+Word+will+be+live+streamed+from+New+York+City,+and+is+free+for+all+to+watch.%0Ahttps://wordpress.org/news/2021/11/state-of-the-word-2021/\">Mark your calendars</a>; it’s almost time for State of the Word 2021!</p>\n\n\n\n<p>State of the Word is the annual keynote address delivered by the WordPress project’s co-founder, Matt Mullenweg. Every year, the event allows us to reflect on the project’s progress and the future of open source. This year will include that and more.</p>\n\n\n\n<p>Due to the pandemic, we moved the State of the World online for the first time ever in 2020. This year, the event will be livestreamed from New York City. That will enable us to take as many folks as possible along for the ride!</p>\n\n\n\n<p>Join Matt as he provides a retrospective of 2021, discusses the latest trends he’s seeing, celebrates the community’s amazing wins, and explores the future. Expect to hear about a range of topics, from WordPress 5.9 and Openverse to Web3 and non-fungible tokens (NFTs).</p>\n\n\n\n<p><strong>What:</strong> State of the Word 2021</p>\n\n\n\n<p><strong>When:</strong> <a href=\"https://calendar.google.com/calendar/u/0/r/eventedit?text=State+of+the+Word+2021+livestream&amp;dates=20211214T220000Z/20211215T000000Z&amp;details=State+of+the+Word+is+the+annual+keynote+address+delivered+by+the+WordPress+project%27s+co-founder,+Matt+Mullenweg.%0AJoin+Matt+as+he+provides+a+retrospective+of+2021,+celebrates+the+community%27s+amazing+wins,+discusses+the+latest+trends+he%27s+seeing,+and+explores+the+future+of+open+source.%0AState+of+the+Word+will+be+live+streamed+from+New+York+City,+and+is+free+for+all+to+watch.%0Ahttps://wordpress.org/news/2021/11/state-of-the-word-2021/\">December 14, 2021, between 5 and 7 pm ET/10 &#8211; 12 am (December 15) UTC</a></p>\n\n\n\n<p><strong>How:</strong> If you’re watching from the comfort of your home or <a href=\"https://wordpress.org/news/2021/11/watch-state-of-the-word-at-a-watch-party-with-your-wordpress-friends/\">local watch party</a>, the livestream will be embedded on wordpress.org/news.</p>\n\n\n\n<p><strong>Have a question for Matt?</strong></p>\n\n\n\n<p>State of the Word will be followed by a Question &amp; Answer session. If you want to participate, you can either send your question ahead of time to <a href=\"mailto:ask-matt@wordcamp.org\">ask-matt@wordcamp.org</a>, or ask during the event in the livestream chat on YouTube.</p>\n\n\n\n<p>If you’re new to State of the Word, the previous years’ recordings (below) will help you get a sense of what the event is about. Check them out:</p>\n\n\n\n<ul><li><a href=\"https://wordpress.org/news/2020/12/state-of-the-word-2020/\">State of the Word 2020 (online)</a></li><li><a href=\"https://wordpress.tv/2019/11/03/2019-state-of-the-word/\">State of the Word 2019 – WordCamp US, St. Louis</a></li><li><a href=\"https://wordpress.tv/2018/12/27/state-of-the-word-2018/\">State of the Word 2018 – WordCamp US, Nashville</a></li><li><a href=\"https://wordpress.tv/tag/state-of-the-word/\">All recordings</a></li></ul>\n\n\n\n<p>We hope to see you online on December 14th!</p>\n\n\n\n<p><em>Thanks to <a href=\'https://profiles.wordpress.org/anjanavasan/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>anjanavasan</a> <a href=\'https://profiles.wordpress.org/eidolonnight/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>eidolonnight</a> <a href=\'https://profiles.wordpress.org/rmartinezduque/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>rmartinezduque</a> for their work on this post. The featured image was created by <a href=\'https://profiles.wordpress.org/beafialho/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>beafialho</a></em>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11567\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:6;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:66:\"Take the 2021 WordPress Annual Survey (and view the 2020 results)!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:99:\"https://wordpress.org/news/2021/11/take-the-2021-wordpress-annual-survey-and-view-the-2020-results/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 11 Nov 2021 23:52:12 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:9:\"Community\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:7:\"General\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11551\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:359:\"Each year, members of the WordPress community (users, site builders, extenders, and contributors) provide their valuable feedback through an annual survey. Key takeaways and trends that emerge from this survey often find their way into the annual State of the Word address, are shared in the public project blogs, and can influence the direction and [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Josepha\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:4885:\"\n<p>Each year, members of the WordPress community (users, site builders, extenders, and contributors) provide their valuable feedback through an annual survey. Key takeaways and trends that emerge from this survey often find their way into the annual <a href=\"https://wordpress.tv/tag/state-of-the-word/\"><em>State of the Word</em></a> address, are shared in the public project blogs, and can influence the direction and strategy for the WordPress Project.</p>\n\n\n\n<p>Simply put: this survey helps those who build WordPress understand more about how the software is used, and by whom. The survey also helps leaders in the WordPress open source project learn more about our contributors’ experiences.&nbsp;&nbsp;</p>\n\n\n\n<p>To ensure that <strong><em>your</em></strong> WordPress experience is represented in the 2021 survey results, <a href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-english\" target=\"_blank\" rel=\"noreferrer noopener\">take the 2021 annual survey now.</a></p>\n\n\n\n<div class=\"wp-container-61a6012e45a35 wp-block-buttons\">\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-english\" target=\"_blank\" rel=\"noreferrer noopener\">Take the Annual Survey! (English)</a></div>\n</div>\n\n\n\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<p>You may also take the survey in <a href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-french\" target=\"_blank\" rel=\"noreferrer noopener\">French</a>, <a href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-german\" target=\"_blank\" rel=\"noreferrer noopener\">German</a>, <a href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-japanese\" target=\"_blank\" rel=\"noreferrer noopener\">Japanese</a>, <a href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-russian\" target=\"_blank\" rel=\"noreferrer noopener\">Russian</a>, or <a href=\"https://wordpressdotorg.survey.fm/wordpress-2021-survey-spanish\" target=\"_blank\" rel=\"noreferrer noopener\">Spanish</a>. These are the top five languages (other than English) based on the number of WordPress locale downloads. For 2022, additional languages may be considered for translation.</p>\n\n\n\n<p>The survey will be open through the end of 2021, and the results will be published in a future post on this blog for anyone to view. Next year, there will be a new format for this survey, including which segments and questions are included, so that your valuable time spent responding results in equally valuable information.</p>\n\n\n\n<h2 id=\"2020-survey-results\">2020 Survey Results</h2>\n\n\n\n<p>For the 2020 survey, more than 17,000 responses were collected, representing the highest submission volume in four years, up three times from the prior year. In the inaugural year of the survey (2015), over 50,000 responses were collected. Given the reach and adoption of WordPress, there is a significant number we have not reached. As you take the 2021 survey, consider sharing the link on social media and with other colleagues who use WordPress. Gathering feedback from more folks who benefit from WordPress will strengthen our project.</p>\n\n\n\n<div class=\"wp-container-61a6012e45d93 wp-block-buttons\">\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\" href=\"https://wordpress.org/news/files/2021/11/WordPress.org-2020-Annual-Survey.pdf\" target=\"_blank\" rel=\"noreferrer noopener\">View the 2020 Survey Results (PDF)</a></div>\n</div>\n\n\n\n<div style=\"height:40px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<p>The 2020 survey results show that the pandemic has had a major impact on how we operate as a community. With few in-person events, many community members continue to find it challenging to balance community contributions with their own personal and professional obligations.&nbsp;</p>\n\n\n\n<h2 id=\"footnotes\">Footnotes:</h2>\n\n\n\n<p>Data security and privacy are paramount to the WordPress project and community. With this in mind, all data will be anonymized: no email addresses nor IP addresses will be associated with published results. To learn more about WordPress.org’s privacy practices, view the <a href=\"https://wordpress.org/about/privacy/\">privacy policy</a>.</p>\n\n\n\n<p>Like last year, the 2021 survey will be promoted via a banner on WordPress.org, and throughout the make blogs. However, taking a moment to amplify these posts through your own social media and Slack accounts will ensure broader participation. Each of the translated surveys will be promoted through banners on their associated localized-language WordPress.org sites.</p>\n\n\n\n<p><em>Thanks to <a href=\"https://profiles.wordpress.org/dansoschin/\">@dansoschin</a> for the initial draft of this post, and to <a href=\"https://profiles.wordpress.org/annezazu/\">@annezazu</a> &amp; <a href=\"https://profiles.wordpress.org/zackkrida/\">@zackkrida</a> for review!</em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11551\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:7;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:48:\"WordPress 5.8.2 Security and Maintenance Release\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"https://wordpress.org/news/2021/11/wordpress-5-8-2-security-and-maintenance-release/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 10 Nov 2021 18:20:45 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:8:\"Releases\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Security\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11546\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:365:\"WordPress 5.8.2&#160;is now available! This security and maintenance release features 2 bug fixes in addition to 1 security fix. Because this is a security release, it is recommended that you update your sites immediately. All versions since WordPress 5.2 have also been updated. WordPress 5.8.2 is a small focus security and maintenance release. The next [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:19:\"Jonathan Desrosiers\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:3030:\"\n<p><strong>WordPress 5.8.2&nbsp;</strong>is now available!</p>\n\n\n\n<p>This security and maintenance release features <a href=\"https://core.trac.wordpress.org/query?id=54129%2C54323%2C54207&amp;milestone=5.8.2&amp;group=component&amp;col=id&amp;col=summary&amp;col=owner&amp;col=type&amp;col=status&amp;col=priority&amp;col=milestone&amp;order=priority\">2 bug</a> fixes in addition to <a href=\"https://core.trac.wordpress.org/ticket/54207\">1 security</a> fix. Because this is a <strong>security release</strong>, it is recommended that you update your sites immediately. All versions since WordPress 5.2 have also been updated.</p>\n\n\n\n<p>WordPress 5.8.2 is a small focus security and maintenance release. The next major release will be version&nbsp;<a href=\"https://make.wordpress.org/core/5-9/\">5.9</a>.</p>\n\n\n\n<p>You can download WordPress 5.8.2 by downloading from WordPress.org, or visit your Dashboard → Updates and click Update Now. If you have sites that support automatic background updates, they’ve already started the update process.</p>\n\n\n\n<p>For more information, browse the <a href=\"https://core.trac.wordpress.org/query?id=54129%2C54323%2C54207&amp;milestone=5.8.2&amp;group=component&amp;col=id&amp;col=summary&amp;col=owner&amp;col=type&amp;col=status&amp;col=priority&amp;col=milestone&amp;order=priority\">full list of changes</a> on Trac, or check out the <a href=\"https://wordpress.org/support/wordpress-version/version-5-8-2/\">version 5.8.2 HelpHub documentation page</a>.</p>\n\n\n\n<h2 id=\"thanks-and-props\">Thanks and props!</h2>\n\n\n\n<p>The 5.8.2 release was led by&nbsp;<a href=\"https://profiles.wordpress.org/desrosj/\">Jonathan Desrosiers</a>&nbsp;and&nbsp;<a href=\"https://profiles.wordpress.org/circlecube/\">Evan Mullins</a>.</p>\n\n\n\n<p>In addition to the release squad members mentioned above, thank you to everyone who helped make WordPress 5.8.2 happen:</p>\n\n\n\n<a href=\"https://profiles.wordpress.org/aristath/\">Ari Stathopoulos</a>, <a href=\"https://profiles.wordpress.org/bradleyt/\">Bradley Taylor</a>, <a href=\"https://profiles.wordpress.org/davidwebca/\">davidwebca</a>, <a href=\"https://profiles.wordpress.org/circlecube/\">Evan Mullins</a>, <a href=\"https://profiles.wordpress.org/gziolo/\">Greg Ziółkowski</a>, <a href=\"https://profiles.wordpress.org/desrosj/\">Jonathan Desrosiers</a>, <a href=\"https://profiles.wordpress.org/jrf/\">Juliette Reinders Folmer</a>, <a href=\"https://profiles.wordpress.org/mukesh27/\">Mukesh Panchal</a>, <a href=\"https://profiles.wordpress.org/sergeybiryukov/\">Sergey Biryukov</a>, <a href=\"https://profiles.wordpress.org/shimon246/\">shimon246</a>, and <a href=\"https://profiles.wordpress.org/fierevere/\">Yui</a>.\n\n\n\n<p class=\"has-text-align-right has-small-font-size\"><em>Props </em><a href=\'https://profiles.wordpress.org/circlecube/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>circlecube</a><em> and </em><a href=\'https://profiles.wordpress.org/pbiron/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>pbiron</a> <em>for peer review.</em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11546\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:8;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:36:\"The Month in WordPress: October 2021\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"https://wordpress.org/news/2021/11/the-month-in-wordpress-october-2021/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 04 Nov 2021 23:35:37 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:18:\"Month in WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11501\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:344:\"October 2021 brought a lot of new things to WordPress, from release updates to new versions of Gutenberg. More notably, in the latest episode of WP Briefing, Executive Director Josepha Haden reminded us about the importance of freedom in open source platforms like WordPress. Free speech has with it a lot of responsibility, just like [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:12:\"Anjana Vasan\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:24011:\"\n<p>October 2021 brought a lot of new things to WordPress, from release updates to new versions of Gutenberg. More notably, in the <a rel=\"noreferrer noopener\" href=\"https://wordpress.org/news/2021/10/episode-18-the-economics-of-wordpress/\" target=\"_blank\">latest episode</a> of <a rel=\"noreferrer noopener\" href=\"https://wordpress.org/news/podcast/\" target=\"_blank\">WP Briefing</a>, Executive Director <a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/chanthaboune/\" target=\"_blank\">Josepha Haden</a> reminded us about the importance of freedom in open source platforms like WordPress.</p>\n\n\n\n<blockquote class=\"wp-block-quote\"><p>Free speech has with it a lot of responsibility, just like being a part of a community. Governments or communities, or in our case, <strong>this software is built by the people who show up. </strong><br><br>For WordPress, at 42% of the web, every small choice we make can cause huge changes in the way that people experience the web today and tomorrow.</p><cite>Josepha Haden, Executive Director of the WordPress project</cite></blockquote>\n\n\n\n<p>Last month was yet another chapter in this journey. So keep reading to learn what&#8217;s new. </p>\n\n\n\n<hr class=\"wp-block-separator\" />\n\n\n\n<h2>WordPress 5.9: All proposed updates will likely be included</h2>\n\n\n\n<ul><li>We announced the<strong> </strong><a href=\"https://make.wordpress.org/core/2021/10/15/wordpress-5-9-feature-go-no-go-october-14-2021/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>go/no-go for WordPress 5.9 features</strong></a><strong> </strong>in mid-October. The release will probably include all the proposed features—though not all of them are ready yet, they should be by the <a href=\"https://make.wordpress.org/core/2021/09/03/wordpress-5-9-planning-roundup/\" target=\"_blank\" rel=\"noreferrer noopener\">proposed release date</a> (December 14, 2021).</li><li><strong style=\"color: initial\">WordPress 5.9 will include a new default theme called </strong><a href=\"https://make.wordpress.org/core/2021/10/06/introducing-twenty-twenty-two/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>Twenty Twenty-Two</strong></a><strong style=\"color: initial\">! </strong><span style=\"color: initial\">The theme&#8217;s foundation will be strong, and the unpredictable yet reliable behavior of birds inspired the intentionally subtle design. </span></li></ul>\n\n\n\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large\"><img loading=\"lazy\" width=\"632\" height=\"433\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?resize=632%2C433&#038;ssl=1\" alt=\"WordPress Default Theme Twenty Twenty-Two released with WordPress Version 5.9\" class=\"wp-image-11509\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?resize=1024%2C701&amp;ssl=1 1024w, https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?resize=300%2C205&amp;ssl=1 300w, https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?resize=768%2C526&amp;ssl=1 768w, https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?resize=1536%2C1051&amp;ssl=1 1536w, https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?resize=2048%2C1402&amp;ssl=1 2048w, https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?w=1264&amp;ssl=1 1264w, https://i2.wp.com/wordpress.org/news/files/2021/11/WordPress-Twenty-TwentyTwo-Theme.jpg?w=1896&amp;ssl=1 1896w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /></figure></div>\n\n\n\n<ul><li>5.9 bug scrub sessions began in September and will continue each week until the release on December 14, 2021. <strong>View the full </strong><a href=\"https://make.wordpress.org/core/2021/10/18/bug-scrub-schedule-for-5-9/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>bug scrub schedule</strong></a><strong>. </strong></li><li>Many of you requested a few <a href=\"https://make.wordpress.org/core/2021/10/25/wordpress-5-9-editor-update-26-october/\" target=\"_blank\" rel=\"noreferrer noopener\">&#8220;must have&#8221; enhancements</a> for the 5.9 release—follow the <a href=\"https://github.com/WordPress/gutenberg/projects/62\" target=\"_blank\" rel=\"noreferrer noopener\">5.9 Must-Haves project board</a> to track their status.</li><li>The Test Team <a href=\"https://make.wordpress.org/test/2021/10/27/overview-of-the-5-9-test-leads-approach/\" target=\"_blank\" rel=\"noreferrer noopener\">shared an overview</a> of the team’s approach to testing WordPress 5.9.</li></ul>\n\n\n\n<blockquote class=\"wp-block-quote has-text-align-center is-style-default\"><p><strong>Interested in contributing to WordPress core?</strong> Join the <a href=\"https://wordpress.slack.com/archives/C02RQBWTW\" target=\"_blank\" rel=\"noreferrer noopener\">#core</a> channel, follow the <a href=\"https://make.wordpress.org/core/\" target=\"_blank\" rel=\"noreferrer noopener\">Core Team blog</a>, and check out the <a href=\"https://make.wordpress.org/core/handbook/\" target=\"_blank\" rel=\"noreferrer noopener\">team handbook</a>. Also, don&#8217;t miss the Core Team’s weekly developer chat on Wednesdays at <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?hour=20&amp;min=00&amp;sec=0\" target=\"_blank\" rel=\"noreferrer noopener\">8 PM</a> UTC.</p></blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2>Gutenberg releases: 11.6, 11.7, and 11.8 are here</h2>\n\n\n\n<p>We released three new versions of the Gutenberg block editor between the end of September and October:</p>\n\n\n\n<ul><li><a href=\"https://make.wordpress.org/core/2021/10/01/whats-new-in-gutenberg-11-6-29-september/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>Version 11.6</strong></a><strong> </strong>brings site logo cropping and rotation, block-level locking, improvements to Query Pagination block, support for child themes and <a href=\"https://make.wordpress.org/core/2021/10/29/thunks-in-gutenberg/\" target=\"_blank\" rel=\"noreferrer noopener\">thunks</a>, template focus mode, and enhancements to the writing flow.</li><li><a href=\"https://make.wordpress.org/core/2021/10/14/whats-new-in-gutenberg-11-7-october-13th/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>Gutenberg version 11.7</strong></a><strong> </strong>includes bug fixes and polishes to the navigation block and editor, global styles and full site editing (including a handy back button that lets you quickly return to the site editor), and columns block support, among other improvements.</li><li>Finally, <a href=\"https://make.wordpress.org/core/2021/10/28/whats-new-in-gutenberg-11-8-0-27-october/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>Gutenberg 11.8</strong></a> is the second to last version to make it into the <a href=\"https://make.wordpress.org/core/5-9/\" target=\"_blank\" rel=\"noreferrer noopener\">WordPress 5.9</a> release, and it comes with plenty of developments to the editing experience: featured Block Patterns, new animations for a few elements (like Dropzone and Insertion Point), spacing tools for heading blocks, and a lot more.</li></ul>\n\n\n\n<blockquote class=\"wp-block-quote\"><p><strong>Want to get involved in developing Gutenberg? </strong>Follow <a href=\"https://make.wordpress.org/core/\" target=\"_blank\" rel=\"noreferrer noopener\">the Core Team blog</a>, contribute to <a href=\"https://github.com/WordPress/gutenberg/\" target=\"_blank\" rel=\"noreferrer noopener\">Gutenberg on GitHub</a>, and join the <a href=\"https://wordpress.slack.com/archives/C02QB2JS7\" target=\"_blank\" rel=\"noreferrer noopener\">#core-editor</a> channel in the <a href=\"https://make.wordpress.org/chat/\" target=\"_blank\" rel=\"noreferrer noopener\">Make WordPress Slack</a>. For details on the latest updates, follow the &#8220;<a href=\"https://make.wordpress.org/core/2021/09/17/whats-next-in-gutenberg-mid-september-2021/\" target=\"_blank\" rel=\"noreferrer noopener\">What&#8217;s next in Gutenberg</a>&#8221; post.</p></blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2>WordCamp US returned virtually this year with more than 3,600 attendees</h2>\n\n\n\n<p><a href=\"https://us.wordcamp.org/\" target=\"_blank\" rel=\"noreferrer noopener\">WordCamp US</a> 2021 was on October 1, and it was online for the first time. The event drew more than 3,600 attendees, 27 sponsors, and 18 remarkable speakers on topics ranging from accessibility and sustainability to e-commerce, to name a few. </p>\n\n\n\n<p>Matt Mullenweg&#8217;s “State of the Word” is expected to be held as a separate event later this year, rather than being part of WordCamp US.</p>\n\n\n\n<blockquote class=\"wp-block-quote has-text-align-center\"><p>If you missed the live event, <strong>you can still watch the <a href=\"https://www.youtube.com/watch?v=0Vkq4BFZbhI\" target=\"_blank\" rel=\"noreferrer noopener\">Yukon Track</a> and the <a href=\"https://www.youtube.com/watch?v=_6-yAZRXY8k\" target=\"_blank\" rel=\"noreferrer noopener\">Columbia Track</a> of WordCamp US 2021.</strong></p></blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2>Team Updates: Polyglots monthly newsletter, a new Performance team, and more</h2>\n\n\n\n<ul><li>The <a href=\"https://make.wordpress.org/polyglots/2021/10/15/polyglots-monthly-newsletter-october-2021/\" target=\"_blank\" rel=\"noreferrer noopener\">first edition</a> of the <a href=\"https://make.wordpress.org/polyglots/2021/09/27/sign-up-for-the-new-polyglots-monthly-newsletter/\" target=\"_blank\" rel=\"noreferrer noopener\">Polyglots monthly newsletter</a> went out on October 15, 2021.</li><li>The following teams opened up their call for team representatives last month, and more are coming soon: <a href=\"https://make.wordpress.org/core/2021/10/26/nominations-for-core-team-reps-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">Core</a>, <a href=\"https://make.wordpress.org/community/2021/10/11/community-team-rep-nominations-for-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">Community</a>, <a href=\"https://make.wordpress.org/themes/2021/10/27/nominations-call-for-the-themes-team-representatives/\" target=\"_blank\" rel=\"noreferrer noopener\">Themes</a>, <a href=\"https://make.wordpress.org/accessibility/2021/10/30/call-for-team-rep-nominations-3/\" target=\"_blank\" rel=\"noreferrer noopener\">Accessibility</a>, <a href=\"https://make.wordpress.org/support/2021/10/team-representative-nominations/\" target=\"_blank\" rel=\"noreferrer noopener\">Support</a>, <a href=\"https://make.wordpress.org/training/2021/11/02/nominations-for-training-team-reps-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">Training</a>, and <a href=\"https://make.wordpress.org/hosting/2021/10/30/nominations-for-hosting-team-reps-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">Hosting</a>.</li><li>A draft of the <a href=\"https://make.wordpress.org/community/2021/10/08/proposal-2022-global-community-sponsorship-program/\" target=\"_blank\" rel=\"noreferrer noopener\">2022 Global Community Sponsorship program proposal</a> was released on October 8, 2021. </li><li>The October 2021 edition of <a href=\"https://wordpress.org/news/2021/10/people-of-wordpress-ecommerce-ronald-gijsel/\" target=\"_blank\" rel=\"noreferrer noopener\">People of WordPress</a>, featuring WordPress e-commerce specialist Ronald Gijsel, went live.</li><li>Following excellent responses to the <a href=\"https://make.wordpress.org/core/2021/10/12/proposal-for-a-performance-team/\" target=\"_blank\" rel=\"noreferrer noopener\">initial proposal</a>, members of the Core Team announced the next steps in <a href=\"https://make.wordpress.org/core/2021/10/28/wordpress-performance-team-kick-off/\" target=\"_blank\" rel=\"noreferrer noopener\">launching a performance team</a> that aims to increase the performance (speed) of WordPress.</li><li>The Polyglots Team kicked-off <a href=\"https://make.wordpress.org/polyglots/2021/10/12/new-glotpress-meetings/\" target=\"_blank\" rel=\"noreferrer noopener\">bi-weekly Glotpress meetings</a> aimed at improving the functionality of the plugin that powers <a href=\"https://translate.wordpress.org/\" target=\"_blank\" rel=\"noreferrer noopener\">translate.wordpress.org</a>.</li><li>A <a href=\"https://make.wordpress.org/training/2021/10/27/documentation-sprint/\" target=\"_blank\" rel=\"noreferrer noopener\">sprint</a> is planned on November 10-12 to explore a <a href=\"https://make.wordpress.org/training/2021/10/04/a-dedicated-volunteer-program-for-the-training-team/\" target=\"_blank\" rel=\"noreferrer noopener\">dedicated volunteer program</a> for the Training Team, similar to the Deputy program in the Community Team.</li><li>WordPress.org profiles now display <a href=\"https://make.wordpress.org/meta/2021/10/11/wordpress-org-profiles-now-show-github-activity/\" target=\"_blank\" rel=\"noreferrer noopener\">activity from the WordPress GitHub organization</a>.</li><li>The Test Team <a href=\"https://make.wordpress.org/test/2021/10/21/fse-program-pattern-party-summary/\" target=\"_blank\" rel=\"noreferrer noopener\">shared results</a> of the tenth Full Site Editing (FSE) Outreach Program: Pattern Party. </li><li>Answers from the <a href=\"https://make.wordpress.org/core/2021/10/28/fse-program-answers-from-round-three-of-questions/\" target=\"_blank\" rel=\"noreferrer noopener\">third round of questions</a> from the FSE Outreach Program are now out!</li><li>The Docs Team is<a href=\"https://make.wordpress.org/docs/\" target=\"_blank\" rel=\"noreferrer noopener\"> consulting with independent legal experts</a> to explore whether there are issues with having Gutenberg (which is dual-licensed GPL + MPL) coexist with other contents in WordPress (our documentation has a CC0 license and the code is GPL). </li><li>The Themes Team shared a recap of work happening on <a href=\"https://make.wordpress.org/themes/2021/10/14/theme-tools-and-requirements-recap/\" target=\"_blank\" rel=\"noreferrer noopener\">automation and improvement processes</a> for the WordPress Themes directory. The Team also shared a p2 post highlighting the performance impact of<a href=\"https://make.wordpress.org/themes/2021/10/04/the-performance-impact-of-using-jquery-in-wordpress-themes/\" target=\"_blank\" rel=\"noreferrer noopener\"> jQuery on WordPress themes</a>.</li><li>The Marketing Team participated in a <a href=\"https://make.wordpress.org/marketing/2021/10/21/make-marketing-workshop-contributor-ladder/\" target=\"_blank\" rel=\"noreferrer noopener\">contributor ladder workshop</a> to help plan their future activities.</li><li>The feature<a href=\"https://wptranslationday.org/blog/celebrating-wordpress-translation-day-2021/\" target=\"_blank\" rel=\"noreferrer noopener\"> recap of the WordPress Translation Day 2021</a> is out from the Polyglots and Marketing teams, with the results of the <a href=\"https://wptranslationday.org/wptd-2021/polyglots-appreciation-nominees/\" target=\"_blank\" rel=\"noreferrer noopener\">Polyglots appreciation nominations 2021</a>.</li></ul>\n\n\n\n<blockquote class=\"wp-block-quote has-text-align-center\"><p><strong><a href=\"https://wordpressfoundation.us17.list-manage.com/subscribe?u=0f93eb6000a019fcbd53d10be&amp;id=04c6fa5c90\" target=\"_blank\" rel=\"noreferrer noopener\">Sign up </a>for the Polyglots monthly newsletter</strong>, if you haven’t already.</p></blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2>Feedback/Testing requests: Deadline for redesigned Gutenberg landing page is November 5, 2021</h2>\n\n\n\n<figure class=\"wp-block-image size-large\"><img loading=\"lazy\" width=\"632\" height=\"396\" src=\"https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?resize=632%2C396&#038;ssl=1\" alt=\"WordPress.Org redesigned Gutenberg page\" class=\"wp-image-11519\" srcset=\"https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?resize=1024%2C642&amp;ssl=1 1024w, https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?resize=300%2C188&amp;ssl=1 300w, https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?resize=768%2C482&amp;ssl=1 768w, https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?resize=1536%2C963&amp;ssl=1 1536w, https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?resize=2048%2C1284&amp;ssl=1 2048w, https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?w=1264&amp;ssl=1 1264w, https://i1.wp.com/wordpress.org/news/files/2021/11/WordPress_Gutenberg_Page.jpg?w=1896&amp;ssl=1 1896w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<ul><li><strong>The Design Team is </strong><a href=\"https://make.wordpress.org/design/2021/10/29/redesign-of-the-gutenberg-page/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>working on redesigning</strong></a><strong> the </strong><a href=\"https://wordpress.org/gutenberg/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>wordpress.org/gutenberg</strong></a><strong> landing page, and has already shared a </strong><a href=\"https://www.figma.com/proto/Gl653sYvO3RBhVtWSFwMcD/Gutenberg-Page?page-id=1865%3A12926&amp;node-id=1919%3A14707&amp;viewport=241%2C48%2C0.15&amp;scaling=min-zoom&amp;starting-point-node-id=1919%3A14707\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>prototype</strong></a><strong>. </strong></li><li>The team has also shared a few other concepts where they have requested feedback from contributors:<ul><li>Site Editing iA Concepts: <a href=\"https://make.wordpress.org/design/2021/10/15/site-editing-ia-concepts-how-to-surface-and-access-new-features/\" target=\"_blank\" rel=\"noreferrer noopener\">Part1</a> and <a href=\"https://make.wordpress.org/design/2021/10/22/site-editing-ia-concepts-part-2/\" target=\"_blank\" rel=\"noreferrer noopener\">Part 2</a></li><li><a href=\"https://make.wordpress.org/design/2021/10/08/adventures-in-block-theme-switching/\" target=\"_blank\" rel=\"noreferrer noopener\">Block theme switching</a> concepts</li></ul></li><li>Version 18.5 of WordPress for <a href=\"https://make.wordpress.org/mobile/2021/10/18/call-for-testing-wordpress-for-ios-18-5/\" target=\"_blank\" rel=\"noreferrer noopener\">iOS</a> and <a href=\"https://make.wordpress.org/mobile/2021/10/19/call-for-testing-wordpress-for-android-18-5/\" target=\"_blank\" rel=\"noreferrer noopener\">Android</a> are now available for testing!</li><li>The Accessibility Team is <a href=\"https://make.wordpress.org/accessibility/2021/10/30/accessibility-team-meeting-notes-october-22-2021-2/\" target=\"_blank\" rel=\"noreferrer noopener\">requesting feedback</a> on a new Gutenberg Pull request that <a href=\"https://github.com/WordPress/gutenberg/pull/35896\" target=\"_blank\" rel=\"noreferrer noopener\">announces formatting changes or screen readers</a>.</li></ul>\n\n\n\n<blockquote class=\"wp-block-quote has-text-align-center\"><p><strong>Share your feedback on the new Gutenberg landing page design in </strong><a href=\"https://meta.trac.wordpress.org/ticket/5600\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>Trac</strong></a><strong> or by </strong><a href=\"https://make.wordpress.org/design/2021/10/29/redesign-of-the-gutenberg-page/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>commenting on the blog post</strong></a><strong> by November 5, 2021.</strong></p></blockquote>\n\n\n\n<h2>Keep an eye out for WordCamp Spain, Sâo Paulo, and Taiwan 2021</h2>\n\n\n\n<p>We had several WordPress events in October, and several more to look forward to the rest of the year:</p>\n\n\n\n<ul><li>We’re seeking a new host city for <a href=\"https://us.wordcamp.org/2021/wordcamp-us-update-city-search-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">WordCamp US 2022</a>.</li><li>WordCamp Europe 2022 has extended its <a href=\"https://europe.wordcamp.org/2022/porto-is-calling/\" target=\"_blank\" rel=\"noreferrer noopener\">call for organizers</a>.</li><li><a href=\"https://italia.wordcamp.org/2021/\" target=\"_blank\" rel=\"noreferrer noopener\">WordCamp Italia 2021</a> was held online on October 22-23, 2021. The event had 23 speakers, 17 sponsors, and more than 1,000 registrants. Check out its <a href=\"https://www.youtube.com/channel/UCGdoJtlsv9BhODCQ71cOFnQ\" target=\"_blank\" rel=\"noreferrer noopener\">YouTube channel</a> to watch the event replay.</li><li>Another highlight of the month was <a href=\"https://nicaragua.wordcamp.org/2021/\" target=\"_blank\" rel=\"noreferrer noopener\">WordCamp Nicaragua 2021</a> which was held on October 8-9, 2021.</li><li>The <a href=\"https://make.wordpress.org/community/tag/wpdiversity/\" target=\"_blank\" rel=\"noreferrer noopener\">#WPDiversity</a> working group of the Community Team organized an <a href=\"https://make.wordpress.org/community/2021/10/15/workshops-from-wpdiversity-october-and-november-2021/\" target=\"_blank\" rel=\"noreferrer noopener\">Allyship workshop for event organizers</a> on October 28. <a href=\"https://make.wordpress.org/community/2021/10/15/workshops-from-wpdiversity-october-and-november-2021/\" target=\"_blank\" rel=\"noreferrer noopener\">Sign up now</a> for the diversity events planned by the team for November! </li><li>A <a href=\"https://make.wordpress.org/training/2021/10/29/contribute2wp-learn-training-contributor-day/\" target=\"_blank\" rel=\"noreferrer noopener\">two-day contributor event</a> focused on <a href=\"https://learn.wordpress.org/\" target=\"_blank\" rel=\"noreferrer noopener\">Learn WordPress</a>, took place on October 29 and 31, 2021. There was also another <a href=\"https://yoast.com/about-us/events/yoast-contributor-day/\" target=\"_blank\" rel=\"noreferrer noopener\">contributor day</a> on October 8, 2021, which involved working groups across the board, from accessibility to user testing.</li><li>The Test Team organized the following Hallway Hangouts this week—check out their recaps:<ul><li><a href=\"https://make.wordpress.org/test/2021/10/06/hallway-hangout-pattern-party-testing-walkthrough-6-october/\" target=\"_blank\" rel=\"noreferrer noopener\">Pattern Party Testing Walkthrough </a></li><li><a href=\"https://make.wordpress.org/test/2021/10/20/hallway-hangout-5-9-go-no-go-site-editor-ia-and-more/\" target=\"_blank\" rel=\"noreferrer noopener\">5.9 Go/No go, Site Editor Internal Audit, and more</a></li></ul></li></ul>\n\n\n\n<blockquote class=\"wp-block-quote has-text-align-center\"><p><strong>Don’t miss the following upcoming online WordCamps: </strong><a href=\"https://spain.wordcamp.org/2021/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>WordCamp Spain 2021</strong></a><strong>, </strong><a href=\"https://saopaulo.wordcamp.org/2021/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>WordCamp Sâo Paulo 2021</strong></a><strong>, and </strong><a href=\"https://taiwan.wordcamp.org/2021/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong>WordCamp Taiwan 2021</strong></a><strong>!</strong></p></blockquote>\n\n\n\n<hr class=\"wp-block-separator\" />\n\n\n\n<p><strong><em>Have a story that we could include in the next ‘Month in WordPress’ post? Let us know by filling out </em></strong><a href=\"https://make.wordpress.org/community/month-in-wordpress-submissions/\" target=\"_blank\" rel=\"noreferrer noopener\"><strong><em>this form</em></strong></a><strong><em>.</em></strong></p>\n\n\n\n<p><em>The following folks contributed to October 2021’s Month in WordPress: </em><a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/anjanavasan/\" target=\"_blank\"><em>@anjanavasan</em></a><em>, </em><a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/harishanker/\" target=\"_blank\"><em>@harishanker</em></a><em>, </em><a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/rmartinezduque/\" target=\"_blank\"><em>@rmartinezduque</em></a><em>, <a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/callye/\" target=\"_blank\">@callye</a></em>, <em><a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/webcommsat/\" target=\"_blank\">@webcommsat</a></em>, and <em><a rel=\"noreferrer noopener\" href=\"https://profiles.wordpress.org/chaion07/\" target=\"_blank\">chaion07</a></em>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11501\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:9;a:6:{s:4:\"data\";s:55:\"\n		\n		\n		\n		\n		\n				\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:48:\"WP Briefing: Episode 19: The People of WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:70:\"https://wordpress.org/news/2021/11/episode-19-the-people-of-wordpress/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 01 Nov 2021 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=11368\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:463:\"In this nineteenth episode, WordPress&#8217;s Executive director, Josepha Haden Chomphosy, discusses and expresses gratitude for the inspiration behind the People of WordPress series, HeroPress. Have a question you&#8217;d like answered? You can submit them to wpbriefing@wordpress.org, either written or as a voice recording. Credits Editor:&#160;Dustin Hartzler Logo:&#160;Beatriz Fialho Production:&#160;Chloé Bringmann Song: Fearless First by Kevin [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2021/10/WP-Briefing-019.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:8207:\"\n<p>In this nineteenth episode, WordPress&#8217;s Executive director, Josepha Haden Chomphosy, discusses and expresses gratitude for the inspiration behind the People of WordPress series, HeroPress.</p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2>Credits</h2>\n\n\n\n<ul><li>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a></li><li>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/beafialho/\">Beatriz Fialho</a></li><li>Production:&nbsp;<a href=\"https://profiles.wordpress.org/cbringmann/\">Chloé Bringmann</a></li><li>Song: Fearless First by Kevin MacLeod</li></ul>\n\n\n\n<h2>References </h2>\n\n\n\n<p><a href=\"https://wordpress.org/news/category/interviews/\">People of WordPress series</a></p>\n\n\n\n<p><a href=\"https://heropress.com/\">HeroPress</a></p>\n\n\n\n<p><a href=\"https://heropressnetwork.com/\">HeroPress Network</a></p>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/5-9/\">WordPress 5.9 Development Cycle</a></p>\n\n\n\n<p><a href=\"https://make.wordpress.org/updates/2021/10/22/team-rep-nominations/\">Call for Team Rep Nomination</a></p>\n\n\n\n<h2>Transcript</h2>\n\n\n\n<span id=\"more-11368\"></span>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:11</p>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing. The podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Josepha Haden Chomphosy. Here we go!</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:29</p>\n\n\n\n<p>The month of November includes the Thanksgiving holiday in my part of the world and in my familial traditions. And one of the things that November always brings up for me is the concept of gratitude. I have a gratitude practice that lasts throughout the year. But this time of year always kind of lets me look outside what makes me feel routinely grateful and explore areas that I don&#8217;t always notice.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:05</p>\n\n\n\n<p>This year, I&#8217;m taking some time to do that in the context of the WordPress project, and it&#8217;s reminded me of one of the foundational things I talk about a lot that may be a little harder for folks to see. I am routinely grateful, of course, for the people who show up to maintain WordPress, the people who keep the back office work going, the contributors who contribute directly to the WordPress CMS and project, the folks who routinely go out and tell other people about WordPress and make sure others know how to use it.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:35</p>\n\n\n\n<p>But there&#8217;s another group that I talk about a lot that is a little harder to grasp. And that&#8217;s this conceptual group of people whose lives have been changed by WordPress. I used to run into people like this all the time when I was organizing WordCamps. And the small but meaningful successes that they share year after year made me realize that my small contribution of organizing these events was really quite valuable; valuable to them, and I assumed also valuable to just other people in the WordPress project.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:09</p>\n\n\n\n<p>Now, if you&#8217;re contributing in a way that doesn&#8217;t let you have contact with the same users consistently over time, or if contributing doesn&#8217;t quite balance out in your ledger at the moment, it can be hard to put names to faces in a way that lets you see how your contributions are making an impact. To help with that, a few years ago, the WordPress project partnered with the community Marketing team and the HeroPress team to share the People of WordPress series which you can find monthly on wordpress.org/news. For me, this series provides a glimpse into the humanity of the people who use WordPress, and for me, that&#8217;s enough. If all the series ever did was to remind us that WordPress was made for people whose needs matter, that would be enough to continue to have that series forever. But fortunately, the series does quite a bit more than that.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:04</p>\n\n\n\n<p>The People of WordPress series also lifts up the stories of people who we don&#8217;t always hear from in technology. It highlights the wide array of origin stories that lead people to WordPress. It reminds us of the global nature of what we&#8217;re doing. It reconnects us to the hopefulness of our own first WordPress successes. And it draws a direct line to why we should care about refreshing the commons of this open ecosystem. I&#8217;m sure that there are more things that do as well. But those are just the things off the top of my head, we draw all of the people of WordPress stories that are inside that series directly from the HeroPress essays that have been submitted by users. And I was gonna say users like you in a kind of Reading Rainbow way. But I actually don&#8217;t know if any of you are everyday users who are also using WordPress but maybe not contributing yet. If you are then like I&#8217;m talking to you. And if you&#8217;re not, I&#8217;m probably still talking to you. Because all of these stories that go to HeroPress are very valuable and very important for us to know. Anyway, I digress.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:14</p>\n\n\n\n<p><a href=\"https://profiles.wordpress.org/topher1kenobe/\">Topher DeRosia</a>, who founded HeroPress, is among the first people I met in the community lo these many years ago. And I think the impact that HeroPress has on the WordPress community as a whole is not always well recognized. So in the spirit of expanding the scope of my gratitude, and in the context of refreshing the commons, I would encourage you to head over to HeroPress.com and their newly launched HeroPress Network and see if there&#8217;s any little way that you can contribute to their commons, submitting your story donating to their network offering a small bit of volunteer time if you happen to have any spare time lying around. I know that the team would be delighted to hear from you.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:01</p>\n\n\n\n<p>That leads us now to our small list of big things. We are about a week away from the feature freeze for WordPress 5.9, the final release of the year. Feature freeze caused a little confusion last time. So I just want to clarify there won&#8217;t be a package released with this milestone. But no more enhancements or features will be worked on at that point. And bug fixing will become the primary focus. That will be on November 9, which I believe is next Tuesday. It&#8217;s definitely next week whenever the calendar turns to nine of November 2021. That&#8217;s the day that we&#8217;re going into feature freeze.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:38</p>\n\n\n\n<p>The second big thing in our small list of big things is that team rep nomination season is upon us. If you are a team rep or want to learn more about being one, now is the time to kind of look at what&#8217;s going on and how to keep everything moving in that way. I&#8217;ll include a link to the announcement post of that in the notes below.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:57</p>\n\n\n\n<p>And finally, Daylight Saving Time movements are also upon us. Some parts of the world are ending Daylight Saving Time others are starting it and some have already started or stopped that already. So no matter where you are, don&#8217;t forget to check and double-check the timing of things across time zones for the next few weeks.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:21</p>\n\n\n\n<p>And that is your small list of big things. Thank you for tuning in today for the WordPress Briefing. I&#8217;m your host, Josepha Haden Chomphosy, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11368\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:10;a:6:{s:4:\"data\";s:78:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"People of WordPress: Ronald Gijsel\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:79:\"https://wordpress.org/news/2021/10/people-of-wordpress-ecommerce-ronald-gijsel/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Sat, 30 Oct 2021 12:21:53 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:8:{i:0;a:5:{s:4:\"data\";s:9:\"Community\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Features\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:10:\"Interviews\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:17:\"Contributor Story\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:9:\"HeroPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:7:\"meetups\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:6;a:5:{s:4:\"data\";s:20:\"WordPress e-commerce\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:7;a:5:{s:4:\"data\";s:17:\"WordPress journey\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11370\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:132:\"Ronald Gijsel on his path from chef to WordPress e-commerce specialist, in the latest People of WordPress feature. #ContributorStory\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"webcommsat AbhaNonStopNewsUK\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:11750:\"\n<p>In this series, we share some of the inspiring stories of how WordPress and its global network of contributors can change people&#8217;s lives for the better. This month we feature a WordPress e-commerce specialist on the difference it makes.</p>\n\n\n\n<h2>Empowered to make a change</h2>\n\n\n\n<p><strong>For WordPress contributor Ronald Gijsel, open source is a lifeline and a perfect place for people with creative minds. It led him on a transformational journey from chef to WordPress e-commerce specialist. Originally from the Netherlands, where he trained in hospitality, he was to find a restorative and energizing power within the WordPress local and global community. </strong></p>\n\n\n\n<p>Ten years ago, life took a sad turn for Ronald and his wife Nihan when their baby daughter passed away only a few days after she was born. At that time, Ronald was a restaurant owner in the UK, working hard in a challenging economic environment.&nbsp;Discovering open source was in many ways his lifeline and helped him and his wife through their considerable heartache. Through this community, a journey to understand the opportunities of the web and new career paths began.</p>\n\n\n\n<figure class=\"wp-block-image size-full\"><img src=\"https://i0.wp.com/wordpress.org/news/files/2021/10/ronaldgijsel2-1.jpg?w=632&#038;ssl=1\" alt=\"Portrait picture of Ronald Gijsel\" class=\"wp-image-11403\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>Ronald believes that working together in WordPress and other open source communities can lead to massive benefits for a large number of users. Not least, an online presence has been essential to the survival of many businesses during the Covid-19 pandemic.&nbsp;</p>\n\n\n\n<p>During recent years, he has visited open source events worldwide as a partnership manager at a WordPress e-commerce plugin company and community supporter. His enthusiasm for WordPress has steered him to being part of local support, solutions and collaboration as a co-organizer of <a href=\"https://central.wordcamp.org/\">WordCamp</a> Bristol, the <a href=\"https://www.meetup.com/Cheltenham-WordPress-Meetup/\">WordPress Cheltenham Meetup</a> and more.</p>\n\n\n\n<h2>Moving forward&nbsp;</h2>\n\n\n\n<p>When Nihan enrolled in the UK&#8217;s Open University to complete her computer science degree, Ronald found her course materials stirring his own interest. He started to follow the lectures with her and even attempted some of the course work for himself &#8211; all whilst he continued to work as a chef in various local pubs.&nbsp;</p>\n\n\n\n<p>Through this, he discovered how to generate affiliate commission earnings through blogging on different platforms. “Creating websites was slowly becoming a passion. In these first few years, I enjoyed every part of the steep learning curve, from tackling the basics to more advanced coding and designs,” said Ronald.</p>\n\n\n\n<h2>The Start of a Web Career</h2>\n\n\n\n<p>Ronald reduced his hours as a chef and devoted more time to online courses learning coding, e-commerce, SEO, and online marketing. Yet when he applied for a job as a WordPress designer, he had only heard of the platform in the context of blogging. This was all to change when an online tutor on one of the training sites revealed the many functions available with WordPress. It was the start of a new career and life journey. This tutor was Topher DeRosia, who went on to create <a href=\"https://heropress.com/\">HeroPress</a>.</p>\n\n\n\n<figure class=\"wp-block-image size-full\"><img loading=\"lazy\" width=\"632\" height=\"467\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/10/with-topher.jpg?resize=632%2C467&#038;ssl=1\" alt=\"Ronald Gijsel and Topher De Rosia at \na WordCamp\" class=\"wp-image-11398\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/10/with-topher.jpg?w=900&amp;ssl=1 900w, https://i2.wp.com/wordpress.org/news/files/2021/10/with-topher.jpg?resize=300%2C222&amp;ssl=1 300w, https://i2.wp.com/wordpress.org/news/files/2021/10/with-topher.jpg?resize=768%2C567&amp;ssl=1 768w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /><figcaption>Ronald with Topher at WordCamp London in 2019</figcaption></figure>\n\n\n\n<p>To learn WordPress, Ronald ‘binge-watched’ webinars on various development topics and over time he became more familiar with it. Securing a job as a designer was only the beginning of his journey into the WordPress ecosystem.</p>\n\n\n\n<p>A year later in 2015, after landing the job as a WordPress designer, Ronald&#8217;s boss asked him to consider taking on the business and its clients. With his wife, Ronald decided to take on the firm and to expand their work in WordPress e-commerce and online marketing.</p>\n\n\n\n<p>As an advocate for learning new skills and practicing them, Ronald encourages others to continue to expand their knowledge through study, attending talks at Meetups and WordCamps, and using the new <a href=\"https://learn.wordpress.org/\">Learn WordPress resource</a>. </p>\n\n\n\n<p>“WordPress has evolved in so many branches that require different skills. There are hundreds of areas of expertise, roles, and jobs that complement WordPress to make it what it is.”</p>\n\n\n\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\"><figure class=\"wp-block-media-text__media\"><img loading=\"lazy\" width=\"528\" height=\"652\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/10/meetup3-1.jpg?resize=528%2C652&#038;ssl=1\" alt=\"Ronald talking about WordPress and e-commerce solutions\" class=\"wp-image-11414 size-full\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/10/meetup3-1.jpg?w=528&amp;ssl=1 528w, https://i2.wp.com/wordpress.org/news/files/2021/10/meetup3-1.jpg?resize=243%2C300&amp;ssl=1 243w\" sizes=\"(max-width: 528px) 100vw, 528px\" data-recalc-dims=\"1\" /></figure><div class=\"wp-block-media-text__content\">\n<p class=\"has-large-font-size\">“WordPress is an essential tool in my box.”<br><em><strong>Ronald Gijsel</strong></em></p>\n</div></div>\n\n\n\n<p>Ronald believes WordPress thrives on diversity, with many <a href=\"https://make.wordpress.org/\">contributor opportunities</a> and jobs in the ecosystem that require a wide range of skills.&nbsp;</p>\n\n\n\n<p>“A big part of this is that each person’s personal background complements their skill sets. Who you are and what you do is influenced by what you have done and learned. We need to cherish this. These things also add to our culture, language, experience, and knowledge,” he said.<br></p>\n\n\n\n<h2>A journey into WordPress e-commerce</h2>\n\n\n\n<figure class=\"wp-block-image size-large is-resized\"><img loading=\"lazy\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/10/meetup2.jpg?resize=632%2C442&#038;ssl=1\" alt=\"Ronald presenting on WordPress and e-commerce at an event\" class=\"wp-image-11400\" width=\"632\" height=\"442\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/10/meetup2.jpg?zoom=2&amp;resize=900%2C630&amp;ssl=1 1800w, https://i2.wp.com/wordpress.org/news/files/2021/10/meetup2.jpg?zoom=3&amp;resize=900%2C630&amp;ssl=1 2700w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /><figcaption>Ronald shares his enthusiasm for building WordPress and e-commerce websites at WordCamp London in 2019</figcaption></figure>\n\n\n\n<p>Ronald initially extended his interest in the&nbsp;WordPress ecosystem through representing a plugin company at WordCamps in the UK. He became hooked and went on to attend events in many different countries.&nbsp;</p>\n\n\n\n<p>In 2018, he realized he could do more with his connections and create meaningful partnerships. Within a few weeks, he had crafted his dream job and sent a proposal to the CEO of a WordPress e-commerce firm.</p>\n\n\n\n<p>But pitching to strangers wasn’t an easy task, as he did not know if they would understand his vision. </p>\n\n\n\n<p>Ronald said: “The doubts went through my head for months. ‘Do I give up my business and work for the benefit of another company? What if I don’t get on? What do I do with my customers?’ But I decided to take the leap.&#8221; His pitch proved successful, joining his current firm in 2019.<br><br>In the firm’s CEO, Ronald found a mentor, supporter, and a friend. He explained: “Nando Pappalardo never tells me what to do, but instead, he asks questions to make me realize what is achievable, or could be even better. He simply makes suggestions that I read something and reach my own conclusions.”</p>\n\n\n\n<h2>Looking back at the journey&nbsp;</h2>\n\n\n\n<p>Taking risks or changing directions in mid-career often involves a giant leap. In Ronald&#8217;s view, through WordPress, you don&#8217;t need to be alone. He believes its community can offer support and help to process thinking.</p>\n\n\n\n<p>Ronald said: “I often think back to the moment my daughter passed away. She only lived for a few days. Every day, I wonder how events would have unfolded if she had survived. Maybe her memory lives on in every decision I make and the paths I decide to take.”</p>\n\n\n\n<p>From his experience, he found that&nbsp;changing a career can sometimes take a few years and have a period of transition. He said: “Only looking back do I realize that each small step slowly made a difference in my life.”</p>\n\n\n\n<figure class=\"wp-block-pullquote\"><blockquote><p>“It was WordPress that made the online world easier to navigate and empowered me to make a change”&nbsp;</p><cite>Ronald Gijsel</cite></blockquote></figure>\n\n\n\n<p>He added: “Feeling welcomed into the WordPress community through Meetups and WordCamps added a human dimension and confidence that I can do ‘this’ too.”</p>\n\n\n\n<p>Ronald’s wish is that his story will offer support to others who may have experienced tragedy in their lives. “I hope that I can give you the hope and strength to try and put your energy into something else that can lead to more significant changes in your life. Try to take it as one positive decision at a time.”</p>\n\n\n\n<h2>Share the stories</h2>\n\n\n\n<p>Help us share these stories of open source contributors and continue to grow the community.&nbsp;Meet more WordPressers in the <a href=\"https://wordpress.org/news/category/newsletter/interviews/\">People of WordPress series.</a>&nbsp;#ContributorStory.</p>\n\n\n\n<h2>Contributors</h2>\n\n\n\n<p>Thank you to Abha Thakor (<a href=\"https://profiles.wordpress.org/webcommsat/\">@</a><a href=\"https://profiles.wordpress.org/webcommsat/\" data-type=\"URL\" data-id=\"https://profiles.wordpress.org/webcommsat/\">webcommsat</a>), and Surendra Thakor (<a href=\"https://profiles.wordpress.org/sthakor/\">@sthakor</a>) for the interviews and writing this feature, and to Ronald Gijsel (<a href=\"https://profiles.wordpress.org/just2ronald/\">@just2ronald</a>) for sharing his story.</p>\n\n\n\n<p>Thanks to Meher Bala (<a href=\"https://profiles.wordpress.org/meher/\">@meher</a>), Chloé Bringmann (<a href=\"https://profiles.wordpress.org/cbringmann/\">@cbringmann</a>), Anjana Vasan (<a href=\"https://profiles.wordpress.org/anjanavasan/\">@anjanavasan</a>), Collieth Clarke (<a href=\"https://profiles.wordpress.org/callye/\">@callye</a>), and Reyes Martinez (<a href=\"https://profiles.wordpress.org/rmartinezduque/\">@rmartinezduque</a>) for their content contributions, and Josepha Haden Chomphosy (@<a href=\"https://profiles.wordpress.org/chanthaboune/\">chanthaboune</a>), and Topher DeRosia (<a href=\"https://profiles.wordpress.org/topher1kenobe/\">@topher1kenobe</a>) for their support for the series.</p>\n\n\n\n<p><em>This People of WordPress feature is inspired by an essay originally published on </em><a href=\"https://heropress.com/\"><em>HeroPress.com</em></a><em>, a community initiative created by Topher DeRosia. It highlights people in the WordPress community who have overcome barriers and whose stories might otherwise go unheard.&nbsp;</em>#HeroPress</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11370\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:11;a:6:{s:4:\"data\";s:55:\"\n		\n		\n		\n		\n		\n				\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:51:\"WP Briefing: Episode 18: The Economics of WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"https://wordpress.org/news/2021/10/episode-18-the-economics-of-wordpress/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 18 Oct 2021 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=11351\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:364:\"In episode 18 of WP Briefing, Josepha Haden Chomphosy reflects on a recent lecture that she gave to students at Hendrix College in which she explored the economics of WordPress and the principles that sustain the project&#8217;s ecosystem. Have a question you&#8217;d like answered? You can submit them to wpbriefing@wordpress.org, either written or as a [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2021/10/WP-Briefing-018.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:20806:\"\n<p>In episode 18 of WP Briefing, Josepha Haden Chomphosy reflects on a recent lecture that she gave to students at Hendrix College in which she explored the economics of WordPress and the principles that sustain the project&#8217;s ecosystem.</p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2>Credits</h2>\n\n\n\n<ul><li>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a></li><li>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/beafialho/\">Beatriz Fialho</a></li><li>Production:&nbsp;<a href=\"https://profiles.wordpress.org/cbringmann/\">Chloé Bringmann</a></li><li>Song: Fearless First by Kevin MacLeod</li></ul>\n\n\n\n<h2>References </h2>\n\n\n\n<p><a href=\"https://wordpress.org/showcase/\">WordPress Showcase</a></p>\n\n\n\n<p><a href=\"https://wpengine.com/resources/value-of-wordpress-worlds-first-study-of-wordpress-economy/#An_Expansive_WordPress_Ecosystem\">The Value of WordPress: The World&#8217;s First Study of the WordPress Economy </a></p>\n\n\n\n<p><a href=\"https://wordpress.org/five-for-the-future/\">Five for the Future</a></p>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/2021/10/15/wordpress-5-9-feature-go-no-go-october-14-2021/\">WordPress 5.9 Feature Go/No-Go </a></p>\n\n\n\n<h3>Digital Citizenship Resources</h3>\n\n\n\n<p><a href=\"https://wordpress.org/news/2019/10/becoming-better-digital-citizens-through-open-source/\">Becoming Better Digital Citizens Through Open Source</a></p>\n\n\n\n<p><a href=\"https://wordpress.org/news/2019/10/empowering-generations-of-digital-natives/\" data-type=\"post\" data-id=\"7413\">Empowering Generations of Digital Natives</a></p>\n\n\n\n<p><a href=\"https://wordpress.org/news/2019/10/responsible-participation-in-online-communities/\" data-type=\"post\" data-id=\"7386\">Responsible Participation in Online Communities</a></p>\n\n\n\n<h2>Transcript</h2>\n\n\n\n<span id=\"more-11351\"></span>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:11</p>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing, the podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Joseph Haden Chomphosy. Here we go!</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:40</p>\n\n\n\n<p>So today marks the start of Digital Citizenship week. This year in 2021, it is the week of October 18th, and to kick it off, I want to share with you a bit of a lecture that I gave to a college class last week.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:55</p>\n\n\n\n<p>An economics professor teaches this class. And so, a little bit, I went to talk about how WordPress is essentially a microcosm of global societies. That was mostly why I was invited to come and give the talk. But one of the students asked a really interesting question about economics, and especially the economics of WordPress. And so, I&#8217;m going to take some time to do my best to answer that question here as well. But first, we&#8217;re going to start with some big picture information about WordPress that I shared with the students, and then maybe you also don&#8217;t necessarily know.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:00</p>\n\n\n\n<p>So the first place that we started was with the question of what WordPress is. Most of them had heard of it but didn&#8217;t necessarily have a good handle on what it is. And so this is the definition that I gave to them. This is a bit of each of the facets of WordPress that we kind of see right now.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:57</p>\n\n\n\n<p>Firstly, WordPress is, of course, a content management system, which means it&#8217;s a piece of software that uses a copyleft license, which means that it&#8217;s open source, and no one individual necessarily owns the rights to the code. If you get very specific, WordPress is, of course, a FOSS project or a free and open source software project. I know that there are discussions around when a piece of software is free, then what are you using to pay for that piece of software or to pay for your access to that software? And often, the answer is that you&#8217;re using your personally identifying information to pay for something commonly referred to as the data economy. If you&#8217;re not familiar with that, it&#8217;s a little bit of a three-pronged effort there. So you submit your data to get access to the software, or you can join the platform, depending on what it is that you&#8217;re working with. That platform or that software collects your data so that they can build a profile about what sorts of content engages you and so that they can share, essentially, audience cohorts like groups of people that kind of are talking about what you&#8217;re talking about, agree with what you agree about, and find interesting, the same sorts of things that you find interesting. And then those companies sell access to your attention. This is something that I refer to as the attention economy. I think there are a lot of startups at the moment that refers to the attention economy. The most iconic recent example was when the folks over at Netflix said that they weren&#8217;t competing against other streaming services; they were competing against sleep.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:54</p>\n\n\n\n<p>And I think that might ring true for quite a few of us at the moment. But anyway, to scoot us back one level, I had, as I said, WordPress is free software. But the difference with WordPress is that to get a copy of WordPress, you are not necessarily required to hand over any data. You don&#8217;t need to give anyone data to get a copy. You don&#8217;t need to give anyone money or data to open up a copy that you own or to build a website in it if you&#8217;re just doing it locally, but especially around that data side because WordPress is not collecting any data, we&#8217;re also not brokering access to anyone&#8217;s attention.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:37</p>\n\n\n\n<p>Another answer to the question of &#8220;What is WordPress?&#8221; WordPress software and the project enable nearly half a trillion dollars of revenue in the global digital economy, as we learned from a recent study from one of the hosting companies inside the WordPress project&nbsp; &#8211; oh, Inside the WordPress ecosystem anyway. And WordPress also runs 42% of the web, with some of the biggest sites we know using WordPress. Such sites like the New York Times, Rolling Stone, many, many others, which you can see in the WordPress showcase as well. I don&#8217;t necessarily want to just like call out all the big groups that use it. But it&#8217;s a lot.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:22</p>\n\n\n\n<p>&nbsp;To recap what we just learned about WordPress and what WordPress is. It is software that one uses a copyleft license, meaning no single entity owns it. Two, it is available at no cost, meaning no money changes hands for you to own a copy. Three does not track you, which means that you don&#8217;t have to give WordPress any personally identifying information to get a copy. Four is an enabler of a massive digital economy massive portion of the digital economy even. That, you know, means their services, extensions, themes, plugins, all of that stuff. And five, it is software that supports 42% of the web. Practically every other site you visit uses the software. So that&#8217;s WordPress.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:10</p>\n\n\n\n<p>With this massive free software, you might be thinking to yourself a couple of things. The first thing you might be thinking is, &#8220;Well, that sounds impossible.&#8221; But if that&#8217;s not what you&#8217;re thinking, you may be thinking, &#8220;how is it possible that a software that is technically owned by no one and takes no money or data to obtain &#8211;&nbsp; how is it that a software like that can power 42% of the web.&#8221; This brings me, of course, to a topic that I specialize in, which is how WordPress is made.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:45</p>\n\n\n\n<p>WordPress is, by now you know, built by a global community of contributors. And it&#8217;s partially contributors that are self-sponsored or giving of their own time freely, and partly sponsored volunteers, people who are paid by Five for the Future initiatives inside various companies. I have said many times, most recently at WordCamp US, that I have an ideal ratio of about two to one in that kind of volunteer or self-sponsored versus corporately sponsored set of voices. And also, as I said, at WordCamp US, we’re not necessarily getting to that ideal ratio right now because of the nature of the way the world is at the moment. However, this community functions almost like a tiny little digital society. There are ways to get your voice heard ways to take on leadership roles; there is a little bit of light governmental structure. And as with any society, any community like this, there&#8217;s a relationship inherent there. There&#8217;s almost a transactional element that takes place when you&#8217;re participating fully. Although I know that for the WordPress project, especially for open source in general, there is a reminder that we should always be contributing without the expectation of reciprocity. But even if you are contributing without the expectation of reciprocity, you still are engaging in an existing community, which means that you are fostering a relationship with the community or with the people who are also participating in the community with you. It&#8217;s just inescapable if you have people around and working together.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:38</p>\n\n\n\n<p>So two concepts really informed my work to future-proof the WordPress project. And the first one is digital citizenship, which is why this particular WordPress Briefing fits today. And the other is the Tragedy of the Commons, especially how we go about refreshing those commons. So digital citizenship, I talked about it a bit in the last WP Briefing. But it&#8217;s still true today that that&#8217;s important. And the thing that really matters to me as we are working through how to make sure that WordPress is moving forward together. But most discussions of citizenship are location-specific, which makes plenty of sense. However, with so many mobile devices and broader access to some level of internet, we have increasing opportunities to be connected because of interests or skill sets. And even the things that we aspire to or aspire to be with a community like WordPress, which has no home office or main location. Of course, it&#8217;s very important that all of the leaders in WordPress, all of the team reps in WordPress, are proactively engaging with the people in their teams and in their portions of the community instead of waiting for like happenstance running into people. We also use a bunch of social stuff to get people together: twitch screams, not twitch screams because it&#8217;s not that Halloweeny! Twitch streams, hallway hangouts, events that are both online and off. Everything that the Learn section of the community is doing with their discussion groups. There are so many ways that WordPress proactively engages with each other and engages with itself. And that work is all really important to keeping the community involved, which is key to any organization&#8217;s long-term success, and certainly is true for WordPress as long-term success. Because when your community or your organization or your society is engaged and invested in your collective success, then you are (get ready for a list) one, more likely to see obstacles early and be able to overcome them, two more likely to see upcoming trends and prepare for them, three more likely to see what is broken and be able to repair it before it is a major issue. And four, you are more likely to be a resilient and fruitful organization long after any single member has stopped participating.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>11:10</p>\n\n\n\n<p>The more connected to the community you are and the more active, the greater your influence and the ability to affect changes also become. And so, not only is it important for us to remain connected as a community just to make sure that everything is going well. But also, it&#8217;s one of the primary ways that people who are giving back can sort of have more of an opportunity in this particular ecosystem. In open source, anyone can gain influence by helping others by helping the software and by keeping the trains on the tracks. And so, one of the only lasting limits to long-term potential in an open source project is how good you are at seeing beyond yourself and seeing what good action can make beyond just your own benefits. I understand that there are also specific hurdles to contribution in your free time that I have dedicated a couple of different podcasts to, so I don&#8217;t want to neglect that reality.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:19</p>\n\n\n\n<p>I have found in my experience that one of the limiting factors that sometimes cannot be unlearned is not being able to see kind of a broader scope, a broader perspective than what you&#8217;re already bringing into it.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:34</p>\n\n\n\n<p>So being a good digital citizen, that&#8217;s a really important part of keeping any society together any organization together and moving forward. But especially is in the context of digital citizenship, is true for WordPress.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:48</p>\n\n\n\n<p>The second thing that is really important to me is this concept of the Tragedy of the Commons, which is, of course, an economics term, open source has borrowed. We have talked about it in this podcast in the past. And today, I specifically am talking about how WordPress works to consistently be refreshing the commons. So the theory of the Tragedy of the Commons says that all societies can withstand a certain volume of free-riders. A certain number of people who reap the benefits of that community’s society without necessarily putting anything back into it. And that is no different in open source than in any other place where you would apply this concept. The particular catch for WordPress is of course, that it is free, freely available and has no specific copyright holder. And so in those circumstances, it is incredibly easy for a small group of people to maintain the software while the whole world uses it for free. And not only is it possible for a small group of people to maintain it, but if you&#8217;re not careful, it&#8217;s also easy for a small group of unpaid people and unacknowledged people to maintain something forever. And at the volume that WordPress operates at, that&#8217;s a really risky choice. It doesn&#8217;t help to support those people; it puts the long-term stability of the software in jeopardy. And at the end of the day, it just does not help to replenish the commons to make sure that everybody keeps getting to have the benefits of this ecosystem in the long term.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>14:39</p>\n\n\n\n<p>As many of you know, there is this program called Five for the Future. And that program is essentially modeled after a tithe. So the invitation to this program when it was first introduced in 2014 was that if you or your company or your community is made better or made possible by WordPress, you should give back to the WordPress project 5% of your resources.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>15:07</p>\n\n\n\n<p>It is an aspirational 5%, of course, and some groups give back their time while others give back with their money. But almost anyone who wants to give back has some skill that WordPress needs. That particular program has really grown in leaps and bounds since it was introduced. I don&#8217;t know exactly how many team members were considered Five for the Future contributors when it was first introduced in 2014; it was a little before my time. But at the moment, it&#8217;s about 150 people that I am routinely aware of or in contact with, which is still a small number considering how many people use WordPress.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>15:52</p>\n\n\n\n<p>However, many corporations who have seen the most benefit, including, you know, the company that I work for (Automattic), and a lot of other hosting companies in the ecosystem, all do a really good job of refreshing the commons so that WordPress is still usable for businesses of all levels.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>16:11</p>\n\n\n\n<p>I want to leave us with one final big picture thought about open source. This is going to be a callback to one of my earliest episodes in this podcast. But I think that it&#8217;s always worth remembering. We&#8217;re going to talk about this really popular phrase in open source projects &#8220;free as in speech, not free as in beer.&#8221; And for WordPress, as you know, it&#8217;s a little bit of both. It&#8217;s literally free. But also, since it&#8217;s open source, it has a free as in speech component to it as well.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>16:46</p>\n\n\n\n<p>The four freedoms of open source as laid out in the 90s. So 10 years into the open source movement, are basically that you have the freedom to run the program for any purpose, the freedom to study how the program works, and change it so that it can do your computing as you wish, the freedom to redistribute copies so that you can help your neighbor and the freedom to distribute copies of your modified versions, giving the community a chance to learn and benefit from the changes that you put into the software.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>17:22</p>\n\n\n\n<p>I don&#8217;t remember if I said this in my original podcast, but I&#8217;m going to say it today, free speech has a lot of responsibility, just like being a part of a community. Governments or communities, or in our case, this software is built by the people who show up. For WordPress at 42% of the web, every small choice we make can cause huge changes in how people experience the web today and tomorrow. And that, to my mind, is really no different than participating in any sort of civic infrastructure. The changes that are made today, or the errors that we prevent, set the tone and the circumstance and the potential to thrive for everyone who comes after us.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>18:17</p>\n\n\n\n<p>That leads us into our smallest of big things. I just have a couple of things for you all today. Firstly, at the time of this recording, this is prior to the Go/No-Go demo meeting for WordPress 5.9. So I have no idea what&#8217;s happened. But by the time this podcast has been released, we will have had the Go/No-Go meeting and also have shipped the post that summarizes what happened, what we hope to change in the next essentially sprint so that we can confidently move forward with a clear set of features for the 5.9 release. I will include a link to the notes below in the show notes. Kudos to everyone, props to everyone who participated in that meeting. And also props to all the people who have been helping us as we head into this final release of the year and all of you who are going to help us make it successfully to the end.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>19:15</p>\n\n\n\n<p>The second thing I already kind of alluded to at the top of the podcast, it is Digital Citizenship week; two or three years ago, the WordPress blog had a series about digital citizenship and what it means to be a good digital citizen, what it means to do that in the context of WordPress. I will drop a link to those posts in the show notes as well. They are very well written and very important, and fairly evergreen content. If I think that there is something worth updating, I will update it before putting it in the show notes. And that is your small list of big things.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>19:57</p>\n\n\n\n<p>Thank you all for tuning in today for the WordPress sprint. I&#8217;m your host, Joseph Haden Chomphosy, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11351\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:12;a:6:{s:4:\"data\";s:57:\"\n		\n		\n		\n		\n		\n				\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:38:\"The Month in WordPress: September 2021\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"https://wordpress.org/news/2021/10/the-month-in-wordpress-september-2021/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 05 Oct 2021 20:01:02 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:18:\"Month in WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11327\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:351:\"There’s a lot of tolerance in open source software for shipping slightly imperfect work. And that’s good. When we ship software that’s a little bit imperfect, it makes it clear how everyone can participate, how everyone could participate, if they could find this WordPress community that supports the CMS. That was Josepha Haden on the [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Hari Shanker R\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:16575:\"\n<blockquote class=\"wp-block-quote\"><p>There’s a lot of tolerance in open source software for shipping slightly imperfect work. And that’s good. When we ship software that’s a little bit imperfect, it makes it clear how everyone can participate, how everyone could participate, if they could find this WordPress community that supports the CMS.</p></blockquote>\n\n\n\n<p class=\"has-drop-cap\">That was <a href=\"https://profiles.wordpress.org/chanthaboune/\">Josepha Haden</a> on the “<a href=\"https://wordpress.org/news/2021/09/episode-16-a-sneak-peek-at-wordpress-5-9/\">A Sneak Peek at WordPress 5.9</a>” episode of the <a href=\"https://wordpress.org/news/podcast/\">WP Briefing Podcast</a>, talking about what goes into a WordPress release like version 5.9. Read on to find out more about updates on the latest release and the latest WordPress news from September 2021.</p>\n\n\n\n<hr class=\"wp-block-separator\" />\n\n\n\n<h2>WordPress Translation Day 2021 Celebrations ran for 30 days</h2>\n\n\n\n<figure class=\"wp-block-image size-large\"><img loading=\"lazy\" width=\"632\" height=\"332\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/10/image.png?resize=632%2C332&#038;ssl=1\" alt=\"WP Translation Day Matt Mullenweg Quote. Quote text: “Translation is so magical because it multiplies the work of all the other contributors of WordPress. If you care about freedom and the future of the internet, translating WordPress is one of the best things you can do for people who speak your language.”\" class=\"wp-image-11328\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/10/image.png?resize=1024%2C538&amp;ssl=1 1024w, https://i2.wp.com/wordpress.org/news/files/2021/10/image.png?resize=300%2C158&amp;ssl=1 300w, https://i2.wp.com/wordpress.org/news/files/2021/10/image.png?resize=768%2C403&amp;ssl=1 768w, https://i2.wp.com/wordpress.org/news/files/2021/10/image.png?w=1200&amp;ssl=1 1200w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>WordPress Contributor teams, led by the Polyglots and Marketing teams, organized <a href=\"https://wptranslationday.org/\">WordPress Translation Day celebrations</a> for the entire month of September. Contributors from across the world joined the celebrations by translating WordPress into their own languages. Additionally, the team organized a host of <a href=\"https://wptranslationday.org/wptd-2021/global-events/\">global</a> and <a href=\"https://wptranslationday.org/wptd-2021/local-events/\">local events</a>. Translation sprints were organized by the <a href=\"https://make.wordpress.org/community/2021/09/20/kicking-off-the-community-documentation-translation-sprint-from-september-20-to-24/\">Community</a> and <a href=\"https://make.wordpress.org/training/2021/09/30/wptranslationday-subtitling-videos-on-learn/\">Training</a> teams, as well as local groups.</p>\n\n\n\n<p>As part of the celebrations, nominations were invited for contributors who had made a significant impact on the translation of WordPress and its availability in so many languages worldwide. <a href=\"https://wptranslationday.org/blog/call-for-nominations-polyglots-appreciation/\">More than 30 notable polyglot contributors were nominated for their contributions</a>. They will be featured in the coming month on the <a href=\"https://wptranslationday.org/\">WP Translation Day website, together with event recaps and more news.</a></p>\n\n\n\n<p>Read the latest <a href=\"https://wordpress.org/news/2021/09/people-of-wordpress-yordan-soares/\">People of WordPress feature on polyglots contributor Yordan Soares</a>, from South America.</p>\n\n\n\n<h2>WordPress Release updates</h2>\n\n\n\n<ul><li>WordPress security and maintenance release – <a href=\"https://wordpress.org/news/2021/09/wordpress-5-8-1-security-and-maintenance-release/\">Version 5.8.1</a> came out on September 9, 2021. The release fixes three major issues and includes 60 bug fixes. You can update to the latest version directly from your WordPress dashboard or by <a href=\"https://wordpress.org/download/\">downloading</a> it from WordPress.org. The <a href=\"https://make.wordpress.org/core/2021/10/04/wordpress-5-8-2-deferred/\">5.8.2 release has been deferred</a> and if needed a minor release will be added before 5.9.&nbsp;</li><li>Work is progressing towards WordPress 5.9, and the team released a <a href=\"https://make.wordpress.org/core/2021/09/03/wordpress-5-9-planning-roundup/\">planning round-up</a> along with a <a href=\"https://make.wordpress.org/core/2021/09/03/wordpress-5-9-planning-roundup/\">call for release leads</a>. October 14 is the go/no-go date of the release, which is planned to be <a href=\"https://make.wordpress.org/core/2021/09/03/wordpress-5-9-planning-roundup/\">launched on December 14, 2021</a>.</li></ul>\n\n\n\n<p>Want to contribute to WordPress core? Join the <a href=\"https://wordpress.slack.com/archives/C02RQBWTW\">#core</a> channel, follow the <a href=\"https://make.wordpress.org/core/\">Core Team blog</a>, and check out the <a href=\"https://make.wordpress.org/core/handbook/\">team handbook</a>. Don’t miss the Core Team chats on Wednesdays at <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?hour=5&amp;min=00&amp;sec=0\">5 AM</a> and <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?hour=20&amp;min=00&amp;sec=0\">8 PM</a> UTC.&nbsp;</p>\n\n\n\n<h2>Say hi to Gutenberg Versions 11.4 and 11.5</h2>\n\n\n\n<p>We launched Gutenberg <a href=\"https://make.wordpress.org/core/2021/09/01/whats-new-in-gutenberg-11-4-1-september/\">version 11.4</a> and <a href=\"https://make.wordpress.org/core/2021/09/17/whats-new-in-gutenberg-11-5-16-september/\">version 11.5</a> this month. <a href=\"https://make.wordpress.org/core/2021/09/01/whats-new-in-gutenberg-11-4-1-september/\">Version 11.4</a> adds image blocks to the gallery block, duotone filters for featured images, and padding support for Button Blocks. <a href=\"https://make.wordpress.org/core/2021/09/17/whats-new-in-gutenberg-11-5-16-september/\">Version 11.5</a> adds flex layout support to the group and social icon blocks along with <a href=\"https://make.wordpress.org/core/2021/09/22/core-editor-improvement-introducing-the-widget-group-block/\">widget group blocks</a>. It will support the addition of a site logo or title directly into menus.</p>\n\n\n\n<p>Want to get involved in developing Gutenberg? Follow <a href=\"https://make.wordpress.org/core/\">the Core Team blog</a>, contribute to <a href=\"https://github.com/WordPress/gutenberg/\">Gutenberg on GitHub</a>, and join the <a href=\"https://wordpress.slack.com/archives/C02QB2JS7\">#core-editor</a> channel in the <a href=\"https://make.wordpress.org/chat/\">Make WordPress Slack</a>. The <a href=\"https://make.wordpress.org/core/2021/09/17/whats-next-in-gutenberg-mid-september-2021/\">What’s next in Gutenberg</a> post gives details on the latest updates.</p>\n\n\n\n<h2>New Guidelines for in-person WordCamps</h2>\n\n\n\n<p>The Community Team <a href=\"https://make.wordpress.org/community/2021/09/13/announcement-updated-guidelines-for-in-person-wordcamps/\">published new guidelines</a> for returning to in-person WordCamps in regions where in-person events are allowed by the local public health authorities.&nbsp;</p>\n\n\n\n<p>Community members can now organize in-person WordCamps for fully vaccinated, recently tested negative, or recently recovered folks (in the last three months) — provided their region passes the in-person <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/2021-returning-to-in-person-meetups/in-person-meetup-decision-checklist/\">safety checklist</a> OR if vaccines and/or COVID testing are accessible to all. Organizers can continue to plan online WordCamps if their region does not meet the guideline.&nbsp;</p>\n\n\n\n<p>New guidelines are also available on the <a href=\"https://make.wordpress.org/community/2021/09/27/announcement-returning-to-in-person-do_action-charity-hackathons/\">return of in-person do_action hackathons</a>.</p>\n\n\n\n<p>Want to get involved in the Community Team and help bring back in-person WordPress events? <a href=\"https://make.wordpress.org/community/\">Follow the Community Team blog</a> and join the <a href=\"https://wordpress.slack.com/archives/C02RQC7RN\">#community-events</a> channel in <a href=\"https://make.wordpress.org/chat/\">the Make WordPress Slack</a>! Check out the following <a href=\"https://central.wordcamp.org/\">upcoming WordCamps</a> and <a href=\"https://make.wordpress.org/community/events/\">meetups</a>.</p>\n\n\n\n<h2>Important Team announcements/updates</h2>\n\n\n\n<ul><li>The Polyglots Team <a href=\"https://make.wordpress.org/polyglots/2021/09/27/sign-up-for-the-new-polyglots-monthly-newsletter/\">opened sign-ups</a> for their new monthly newsletter. The first edition goes out on October 15!</li><li>The<a href=\"https://make.wordpress.org/project/2021/09/10/suggested-iterations-for-the-five-for-the-future-program-and-tool/\"> Five for the Future program is being revisited</a> to identify opportunities for growth.&nbsp;</li><li>The Docs Team formally <a href=\"https://make.wordpress.org/docs/2021/09/15/announcement-new-workflow-for-reporting-documentation-issues/\">launched a new repository</a> for tracking issues with WordPress documentation: <a href=\"https://github.com/WordPress/Documentation-Issue-Tracker\">https://github.com/WordPress/Documentation-Issue-Tracker</a></li><li>The Plugins Team <a href=\"https://make.wordpress.org/plugins/2021/09/20/change-to-how-long-active-reviews-remain-open/\">reduced the time period to complete active review requests for plugins</a> from six to three months.</li><li>The Community Team started work on the <a href=\"https://make.wordpress.org/community/2021/09/10/2022-global-sponsorship-working-group/\">2022 Global Sponsorship program</a>.</li><li>The Design Team is <a href=\"https://make.wordpress.org/design/2021/09/16/document-status-and-visibility/\">working on a redesign proposal of the Inspector sidebar</a> in the WordPress post/page editor.</li><li>The Themes Team announced the results of the <a href=\"https://make.wordpress.org/themes/2021/09/13/theme-upload-survey-results-next-steps/\">six-week Theme author survey</a>.</li><li>The Training Team also shared <a href=\"https://make.wordpress.org/training/2021/09/14/learn-wordpress-needs-assessment-results/\">results of a third-party needs assessment exercise for Learn WordPress</a> which offers some excellent insights.&nbsp;</li></ul>\n\n\n\n<h2>Feedback/Testing requests from Contributor Teams</h2>\n\n\n\n<ul><li>Version 18.3 of WordPress for <a href=\"https://make.wordpress.org/mobile/2021/09/20/call-for-testing-wordpress-for-ios-18-3/\">iOS</a> and <a href=\"https://make.wordpress.org/mobile/2021/09/21/call-for-testing-wordpress-for-android-18-3/\">Android</a> are now available for testing!</li><li>Join the <a href=\"https://make.wordpress.org/test/2021/09/29/fse-program-testing-call-10-pattern-party/\">10th challenge</a> of the <a href=\"https://make.wordpress.org/core/2020/12/11/the-fse-outreach-program-is-officially-starting/\">Full Site Editing (FSE) Outreach program</a> &#8211; “<a href=\"https://make.wordpress.org/test/2021/09/29/fse-program-testing-call-10-pattern-party/\">Pattern Party</a>”. Check out the <a href=\"https://make.wordpress.org/test/2021/09/29/fse-program-testing-call-10-pattern-party/\">announcement post</a> for testing instructions and join the fun in the <a href=\"https://wordpress.slack.com/archives/C015GUFFC00\">#fse-outreach-experiment</a> Slack channel. Deadline: October 13, 2021</li><li>The Training Team is requesting feedback on:<ul><li><a href=\"https://make.wordpress.org/training/2021/09/27/proposal-ensuring-high-quality-video-contributions-to-learn-wordpress/\">Strategies to ensure high-quality video contributions for Learn WordPress</a>. Deadline: October 6, 2021</li><li><a href=\"https://make.wordpress.org/training/2021/08/10/proposal-learner-achievements-on-profiles/\">Learner achievements on profiles</a>: no current deadline</li></ul></li><li>Help the Test Team <a href=\"https://make.wordpress.org/test/2021/10/01/test-team-chat-summary-28-september-2021/\">reach out to extenders to check if their test suites are compatible with the modernization of the WordPress Core PHP Test Suites</a>. Information on what the changes mean and step-by-step instructions are available over in the following post: <a href=\"https://make.wordpress.org/core/2021/09/27/changes-to-the-wordpress-core-php-test-suite/\">Changes to the WordPress Core PHP Test Suite</a>.</li><li>The Polyglots Team is <a href=\"https://make.wordpress.org/polyglots/2021/09/19/how-to-handle-block-pattern-translations/\">exploring two different mechanisms on translating user-submitted block patterns</a>: GlotPress v/s forking patterns to locales and is requesting feedback from community members.</li></ul>\n\n\n\n<h2>WordPress Events updates</h2>\n\n\n\n<ul><li><a href=\"https://galicia.wordcamp.org/2021/\">WordCamp Galicia 2021</a> was held from September 30 &#8211; October 2, 2021 and <a href=\"https://us.wordcamp.org/2021/\">WordCamp US 2021</a> ran on October 1.</li><li>Don’t miss these upcoming WordCamps: <a href=\"https://nicaragua.wordcamp.org/2021/\">WordCamp Nicaragua 2021</a> (October 8-9, 2021) and <a href=\"https://italia.wordcamp.org/2021/\">WordCamp Italia 2021</a> (October 22-23, 2021) </li><li>The Diverse Speakers Training group (<a href=\"https://make.wordpress.org/community/tag/wpdiversity/\">#WPDiversity</a>) of the Community Team organized an <a href=\"https://make.wordpress.org/community/2021/09/15/wpdiversity-events-in-september-attention-india/\">allyship workshop, along with dedicated workshops focused on India in September</a>.</li><li>The Test Team organized a <a href=\"https://make.wordpress.org/test/2021/09/17/hallway-hangout-discussion-on-adoption-pathways-for-full-site-editing-16-september/\">Hallway Hangout on adoption pathways for Full Site Editing</a> where attendees mentioned how they use FSE and shared ideas for improvement. </li></ul>\n\n\n\n<hr class=\"wp-block-separator\" />\n\n\n\n<h2>Further reading</h2>\n\n\n\n<ul><li>The Hosting Team recommended that all web hosts <a href=\"https://make.wordpress.org/hosting/2021/09/15/wordpress-5-8-1-security-release-is-time-sensitive/\">test and push the WordPress 5.8.1 security update through to their users</a> as soon as possible.</li><li>The Training Team <a href=\"https://make.wordpress.org/training/2021/09/21/should-learn-wordpress-contributors-be-gpl-compliant/\">clarified GPL compliance guidelines</a> for Learn WordPress contributions, and the <a href=\"https://learn.wordpress.org/meta/handbook/workshops/eligibility-of-workshop-presenters/\">guidelines for workshop presenters are available in the handbook</a>.</li><li>The codebase for Openverse, a search engine for openly-licensed media,&nbsp; i<a href=\"https://make.wordpress.org/openverse/2021/09/03/call-for-translations-i18n-in-openverse/\">s now available for translation</a>.</li><li>The Core Team implemented a <a href=\"https://make.wordpress.org/core/2021/09/28/implementing-a-webfonts-api-in-wordpress-core/\">Webfonts API in WordPress core</a> for simplifying the process of enqueuing external fonts on WordPress sites.&nbsp;</li><li>The Test Team rescheduled <a href=\"https://make.wordpress.org/test/2021/09/23/5-9-end-to-end-e2e-working-strategy-session-agenda-for-29-sep-2021/\">the End-to-End (e2e) Test working strategy session</a> to October 6. More details <a href=\"https://make.wordpress.org/test/2021/09/23/5-9-end-to-end-e2e-working-strategy-session-agenda-for-29-sep-2021/\">in the announcement post</a>.</li></ul>\n\n\n\n<p><em>Have a story that we could include in the next ‘Month in WordPress’ post? Please </em><a href=\"https://make.wordpress.org/community/month-in-wordpress-submissions/\"><em>submit it using this form</em></a><em>.&nbsp;</em></p>\n\n\n\n<p><em>The following folks contributed to September’s Month in WordPress: <a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a>, <a href=\'https://profiles.wordpress.org/chaion07/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>chaion07</a>, <a href=\'https://profiles.wordpress.org/dansoschin/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>dansoschin</a>, <a href=\'https://profiles.wordpress.org/harishanker/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>harishanker</a>, <a href=\'https://profiles.wordpress.org/meher/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>meher</a>, and <a href=\'https://profiles.wordpress.org/tobifjellner/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>tobifjellner</a></em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11327\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:13;a:6:{s:4:\"data\";s:55:\"\n		\n		\n		\n		\n		\n				\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:66:\"WP Briefing: Episode 17: WordPressing Your Way to Digital Literacy\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:88:\"https://wordpress.org/news/2021/10/episode-17-wordpressing-your-way-to-digital-literacy/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 04 Oct 2021 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=11316\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:400:\"In episode 17 of the WordPress Briefing, Josepha Haden Chomphosy reflects on her WordCamp US keynote and digs into how participating in open source projects can help you learn 21st Century Skills.&#160; Have a question you&#8217;d like answered? You can submit them to wpbriefing@wordpress.org, either written or as a voice recording. Credits Editor:&#160;Dustin Hartzler Logo:&#160;Beatriz [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2021/09/WP-Briefing-017.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:29421:\"\n<p>In episode 17 of the WordPress Briefing, Josepha Haden Chomphosy reflects on her WordCamp US keynote and digs into how participating in open source projects can help you learn 21st Century Skills.&nbsp;</p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2>Credits</h2>\n\n\n\n<ul><li>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a></li><li>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/beafialho/\">Beatriz Fialho</a></li><li>Production:&nbsp;<a href=\"https://profiles.wordpress.org/cbringmann/\">Chloé Bringmann</a></li><li>Song: Fearless First by Kevin MacLeod</li></ul>\n\n\n\n<h2>References </h2>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/2021/09/03/wordpress-5-9-planning-roundup/\">WordPress 5.9 Planning</a></p>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/2021/08/13/preliminary-road-to-5-9/\">5.9 Target Features</a></p>\n\n\n\n<p><a href=\"https://us.wordcamp.org/2021/\">WordCamp US 2021</a></p>\n\n\n\n<h2>Transcript</h2>\n\n\n\n<span id=\"more-11316\"></span>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing, the podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Josepha Haden Chomphosy. See, here we go!</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:42</p>\n\n\n\n<p>Today I want to talk to you a little bit about the digital divide, where it is, maybe a bit of where it&#8217;s headed, and which parts of the WordPress open source project and CMS can help. This is a focused look, though, so I won&#8217;t touch on some of the hurdles that everyone is aware of when you get outside of in-person environments, things like parental modeling or supervision, education on the relevance of technology, etc. This is a follow-up to the conversation that I had at WordCamp US last week—and so doing a little bit of a deeper dive here. And we&#8217;re gonna start with what exactly is the digital divide.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:23</p>\n\n\n\n<p>So the digital divide is considered those who benefit from the digital age versus those who don&#8217;t; that feels like a really big concept. And the current discussion is primarily about access, or for years that has been about access anyway, especially physical access. So those who have computers versus those who do not have internet in their homes versus those who do not. But I don&#8217;t necessarily agree with that particular, really focused definition of the problem.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:59</p>\n\n\n\n<p>If the only problem we see is access, then the solution becomes to get cheap devices and internet to everyone, which certainly has led to more people being connected than ever before.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:12</p>\n\n\n\n<p>With this proliferation of devices that are considered both smart and mobile (mobile in this context, meaning handheld or pocket-sized), the discussion over the last few years has been shifting. It’s been shifting into more of a discussion around the education around the relevance of internet access, discussions around the quality of access to the Internet, and also discussions around Wi-Fi and dial-up and the surprising cost of data.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:42</p>\n\n\n\n<p>But from my perspective, there are a number of really dangerous assumptions that we make when we boil it all the way down to who has access and the quality of that kind of access.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:54</p>\n\n\n\n<p>The first dangerous assumption is that we run the risk of conflating being tech-savvy with being digitally literate, and they&#8217;re not really the same things. The second assumption that we run the risk of is assuming that access to cellular data equals access to the internet through any other means. And also assuming that cost is always the determining factor.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:19</p>\n\n\n\n<p>And the final fairly dangerous assumption that we&#8217;re making there is that we allow ourselves the ability to mark the digital divide is fixed in our minds. Once we get enough access to everyone, we&#8217;re just done. There is no more divide. But as a way of illustration, if you think about access, not in the context of technology, like high technology, digital technology, and in the context of like writing, you probably own a writing utensil, and you probably have access to paper of some sort, which is great. But just because you have like a pencil and a piece of paper doesn&#8217;t mean that I can send you home right now &#8211; I guess most of you are listening at home.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:07</p>\n\n\n\n<p>It doesn&#8217;t mean that I can send you to your desk right now. And consider you prepared to write a best-selling novel, right? Because giving you physical tools no more makes you a novelist than handing me a computer when I was a teen made me digitally literate.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:24</p>\n\n\n\n<p>So let&#8217;s talk about what it takes to be digitally literate. I&#8217;ve lumped the following skills into three groups. It&#8217;s broadly defined as 21st-century skills, but the groups that I have them in is not a comprehensive list of those 21st-century skills.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:41</p>\n\n\n\n<p>The first group that is a large component of digital literacy is critical thinking skills. So computational thinking and problem-solving. That particular one is not new, exactly. But the computational thinking part certainly is. Not all problems are solved with code, but the basis for thinking through things procedurally is increasingly important.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:04</p>\n\n\n\n<p>The second one in that set is communication through multiple media, consuming communications or content through Twitter, Instagram, Snapchat, or any other format, but also creating the things that communicate—writing blogs, creating videos, both calls and standalone, and forums, things like that.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:27</p>\n\n\n\n<p>The third set in that group of critical thinking skills is around collaboration, which some people will say is more about communication. But I find that collaboration both online and in-person is a skill set all to its own. Communication only gets you so far when you&#8217;re learning to cook to collaborate with people that you don&#8217;t normally work with. And so, I have lumped that into critical thinking skills.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>05:54</p>\n\n\n\n<p>The second big bucket for digital literacy is actually literally digital literacy. So I have three, three things in here as well. Evaluating information is obviously incredibly important in the environment that we&#8217;re in right now, for just information’s sake. But then things like understanding the differences between copyright versus copyleft licenses, understanding the difference between an .org ending URL and a .com ending URL, and evaluating the general veracity of sources that you&#8217;re finding on the web.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:32</p>\n\n\n\n<p>The second part of that group is media use and creation, understanding the difference between folks who consume and folks who create the content we have, how to find information online, and the most sensible places to keep information online. And the third area of digital literacy that I find to be vitally important is the ethics of licenses both around use and access. So again, things like copyright vs. Copy, copyleft. And specifically for WordPress, that means understanding things like the Creative Commons licenses, GPL, MIT, but then also copyright is its own complicated question unto itself. But the other things that show up for us for WordPress that show up for us with technology are also things like open access versus proprietary information and sources. And things like plagiarism versus sampling.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>07:33</p>\n\n\n\n<p>And our third big bucket, which has become increasingly complex, but the third big bucket for digital literacy to my mind is actually considered something that I call life skills. So things like self-direction, knowing what you want to do next, and how to get it done. Time management is also in there, knowing how much time something will take and being able to make sure that you are getting things done over time, as opposed to trying to accomplish everything at the last second.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:03</p>\n\n\n\n<p>A big part of these life skills is cross-cultural and social communication. The internet is tricky, right? Because it&#8217;s simultaneously incredibly insular and increasingly global. Like you can if you wanted only ever read things that already confirm your existing biases. But the very nature of the internet, the very nature of the web, means that the world is much smaller. We have more ready access faster to everyone everywhere in the world than we used to have. This means, of course, that cultural awareness is an absolute must now more than ever. This is for what it&#8217;s worth the time of year when I give this talk. And that&#8217;s because of this last part of the life skills section, which is digital citizenship.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:54</p>\n\n\n\n<p>Digital citizenship generally is the second week of October 2 or the third week of October. And it&#8217;s one of my favorite weeks because it is something that comes up all the time in our ecosystem. It comes up all the time and open source in general, but certainly for WordPress. So those are our three big buckets of digital literacy, a subset of 21st-century skills as a whole.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>09:22</p>\n\n\n\n<p>That&#8217;s a lot of stuff, I know. And it&#8217;s also really hard to figure out how you can learn any of those skills, and from my experience, I really believe that WordPress as an open source project can help people learn those things.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>09:43</p>\n\n\n\n<p>Once upon a time, ages and ages ago, my mother told me that in order for me to become a better writer, I would have to read and in order to become a better communicator, it would probably help if I spent a little bit more time writing, and I have always felt that the same must be true for all the things that we learn, you find a positive example and study it to become better. Or, depending on what you&#8217;re learning and how you&#8217;re trying to learn it, you find a passive example of something that you want to be able to do better from an active standpoint and participate in that so that your active production of the other part is better.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>10:24</p>\n\n\n\n<p>Here are a few parts of the WordPress project and WordPress itself that can help with this.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>10:31</p>\n\n\n\n<p>So there are first a few really specific teams; if you&#8217;re contributing to a team like Themes, or Plugins, or Core, the three areas of those digital literacy skills that you have an opportunity to learn there. For critical thinking, you&#8217;re going to run into problem-solving and computational thinking. You’re also going to run into distributed collaboration, which was really important as just a concept when I first wrote this talk. And now it is currently really important as a reality because we have a bunch of companies that are going to either remote work or partially distributed or fully distributed. And that&#8217;s the way that WordPress has worked for a pretty long time.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>11:19</p>\n\n\n\n<p>So I&#8217;ve always felt like it was important because it was important to WordPress, but it&#8217;s also becoming increasingly an important part of just how to exist in the world at the moment. For those three teams, the things that you can really tap into and practice for the life skill section are digital literacy, nope, digital citizenship, self-direction, and time management obviously comes up in any open source project because you are volunteering your time and it is up to you to kind of decide how much you can commit or not various other parts of time management and directing one&#8217;s own project. Now, but you also get the opportunity to test and practice your cross-cultural communication, social communication and learning what it means to collaborate across cultures in that way.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:15</p>\n\n\n\n<p>On the digital literacy side, you also get a little bit of that information evaluation and synthesis for what it&#8217;s worth. And then obviously get to learn more about the ethics of various types of licenses and how use and access relate to those things.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:35</p>\n\n\n\n<p>We also have a team or two; these are not really teams or twos. These are things that you can do that are either solo activities or group activities. One is working or checking out the support forums, and the other is blogging. We’ll start with support forums.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>12:52</p>\n\n\n\n<p>If you&#8217;re doing this as a group activity, there are a couple of extra things that that you can practice here. But suppose you&#8217;re doing it as a solo, just way to give back to the project sort of thing or way to learn some of these skills sort of thing. In that case, you can get almost all of these digital literacy skills woven into working in the support forums, depending on what&#8217;s happening in the moment and the questions people have brought up. But for critical thinking, obviously, you get some problem solving in there, not as much the computational thinking as the procedural thinking part. But you certainly also get to tap into communication with multiple types of media, collaboration in person and online, depending on whether you&#8217;re doing this as a solo effort or a group effort. And then, of course, research, which I didn&#8217;t really bring up in any of those groupings for digital literacy, is certainly a very important part of it.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>13:49</p>\n\n\n\n<p>For the digital literacy grouping of skills related to digital literacy, you get to work on evaluating information and, depending on how complicated an answer might be. You can also get that opportunity to practice synthesizing complex information and research, a digital literacy skill. And then, once people have responded to a topic you&#8217;ve answered, you also get to tap into that life skill section. You get to be you get the opportunity to practice digital citizenship related to synchronous or asynchronous conversation. You also get to see more about how cross-cultural communication and collaboration works and social communication across those various boundaries that naturally show up when we&#8217;re working across cultures that way. And as I mentioned previously, information synthesis, as well.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>14:53</p>\n\n\n\n<p>The second one that I have mentioned can be like a solo effort or a group effort, depending on what you do. Blogging, for most people who use WordPress, is probably the most common application of how WordPress can help you do stuff. So I often see it as the most relevant and the most immediately accessible to anyone. But you know, you got to meet people where they are. So, I would strongly believe that this is our best way to help people learn these things.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>15:30</p>\n\n\n\n<p>So blogging for the critical thinking sorts of things, you have an opportunity to practice communication across mediums. You will probably get an opportunity to practice your research skills, chances are, you&#8217;re going to get the opportunity to practice some problem solving, and honestly, like if you&#8217;re hosting your site, problem-solving is going to come up when using WordPress as soon as you add in plugins and themes, because you sometimes kind of have to figure out what&#8217;s working and what&#8217;s not, and what&#8217;s playing nicely with other things and what&#8217;s not. It&#8217;s possible that with blogging, you&#8217;re also going to have an opportunity to get to practice some distributed collaboration. But that&#8217;s probably going to depend on what you&#8217;re doing with your site as well.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>16:30</p>\n\n\n\n<p>Licenses and how they apply to the thing that you&#8217;re using feel a bit different when you are the one who&#8217;s creating. And so learning about how those things work and don&#8217;t work and how they can best suit what you&#8217;re trying to do. Blogging is absolutely an excellent opportunity to dig into that a bit and learn a bit more about that. As far as your life skills go,</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>16:56</p>\n\n\n\n<p>that&#8217;s where you, again, get to practice some digital citizenship by figuring out who your audiences are and also when you have to communicate with them via the comments or any other way that you have built up a feedback mechanism there.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>17:11</p>\n\n\n\n<p>Self-direction obviously will come into this, maybe time management if you are blogging on, I was gonna say on a paid basis, but that&#8217;s not really I don&#8217;t know, on a project basis, like some of us are students and have to write things based on deadlines. And so, you know, self-direction, potentially time management, always cross-cultural communication, social communication, research, all of these things show up in there.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>17:41</p>\n\n\n\n<p>And, you know, I really believe in this concept of how blogging and bringing people into maintaining a website can teach you all of these skills. Because when I was younger, I was a bad communicator. And now, I am across the board known for my effective communication and my ability to work across cultures. And so the defining moment, which was like a four-year moment, and so not necessarily a moment, but the thing that really made all the difference was when my mom challenged me to write every single day. She had noted that I was not necessarily great at getting from one point to the next. I wasn&#8217;t necessarily great at building my arguments when I had to explain something to people. And she suggested that writing every day would help me tap into this big thing about communication and working with other people collaboration, all of that. And as an adult on the other side of it. Like, I thought she was super wrong when I was younger, because don&#8217;t we all think our parents are wrong when we&#8217;re younger. But as an adult on the other side of it, not only was that an opportunity for me to literally learn how to communicate better. But it also, when I look at it, gave me access to opportunities to practice all sorts of 21st-century skills and digital literacy skills in an environment that was relatively safe. And so, I am a big proponent of this particular one.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>19:26</p>\n\n\n\n<p>Another team that helps us tap into and practice a lot of our digital literacy skills is the Documentation team. I recommend that you let this be a supervised activity if you&#8217;re doing this with students because, you know, it&#8217;s a wiki. You can put weird things in there on accident or just inaccurate things. So for critical thinking, the primary skill that you&#8217;re going to be able to practice if you&#8217;re working on documentation is collaboration. You would think that it was also like information synthesis and information evaluation. But for a lot of the work that we&#8217;re doing, the documentation exists. And what we&#8217;re looking at is trying to figure out where it no longer matches what is currently in the CMS, or currently in the project or currently in the team, whatever it is that you&#8217;re working on at that moment. And so, it&#8217;s a strong collaborative effort in the WordPress project. You have to have done the general work to figure out what needs to be changed in the documentation. But a lot of times, you need to figure out who has access to make the changes, what has prevented us from making changes in the past, and things like that. And so I say, collaboration is the only one to learn and critical thinking, but it&#8217;s actually a really big one and can take a fair amount of effort in this particular context.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>20:49</p>\n\n\n\n<p>From the digital literacy aspect, of course, there is evaluating information. But this particular type of information evaluation is a little different for documentation. And this actually is true for the Documentation team, for the Training team, and also for the new Learn team. This question is true for all of them. There is a huge difference between presentation versus application of information. The way you present information for people who already know it and just need confirmation of something or are using it for reference material is really different from when people are looking at a piece of documentation that should be telling them how to accomplish something. And they try to apply it either to their own processes at the moment or apply it to teaching other people. And so evaluating information to make sure that what is presented can be applied, and all of the ways that that very complicated journey with managing information can kind of work or not, depending on how things are going in your section of the open source project at the moment.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>21:59</p>\n\n\n\n<p>The third group of skills that you can really dig into in those teams, again, is digital citizenship, basically, everything is digital citizenship in WordPress because we&#8217;re just people online.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>22:24</p>\n\n\n\n<p>And this final grouping that we have this final team and group of skills. So the Community team is a substantial and far-ranging team; they have many things that fall into their area of expertise. And so this has traditionally kind of functioned as a linchpin around education and ensuring that that was all relevant for users and attendees of events. The Community team will remain pivotal to so many things that we do now. But now that we have really awakened the Learn team and re-enlivened that Training team, this will shift a little over time. But yes, so that grain of salt that depending on when you listen to this, if you listen to it in 2050, maybe it&#8217;s not accurate anymore. Hopefully, most of what I say is not accurate in 2050. But you know, still.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>23:27</p>\n\n\n\n<p>&nbsp;Anyway! Critical thinking that&#8217;s where we were. So the critical thinking group of skills inside the Community team, you are going to have an opportunity to practice problem-solving. And frequently also procedural thinking, depending on what you&#8217;re working on in that team. Multimedia communication is absolutely true. And that&#8217;s true, whether you contribute to the team itself and make sure that the team is functioning and doing their basic tasks. Or if you are organizing an entire event, whether it&#8217;s online or offline or however that is being accomplished.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>24:07</p>\n\n\n\n<p>Multimedia communication for this particular team is constant for all of their work and something that everyone who works on there gets to practice all the time. For digital literacy, this comes up a lot if what you&#8217;re doing is working through any sort of like programming plans, making sure that what we have in place for events is really excellent. It&#8217;s a really excellent opportunity for practicing the evaluation of information, learning more about media use and creation, and then naturally, everything to do with licenses copyright copyleft, not only for everything that we produce but then also for everyone in the ecosystem.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>24:55</p>\n\n\n\n<p>This team helps so many plugins, authors and theme developers, and other groups who participate in the ecosystem understand the nuances of the GPL and why it matters so much to WordPress. And then in the life skill section, there&#8217;s the obvious life skill section, life skills that have come up for all of them—so digital citizenship, cross-cultural and social communication. But also you have the opportunity to tap into that self-direction and time management practice, which probably should also be considered in all of these teams. But, you know, things change as we go.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>25:45</p>\n\n\n\n<p>Those are the most likely digital literacy skills that you would end up practicing in the Community team, depending on how you are participating in the Community team at that moment.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>25:59</p>\n\n\n\n<p>So I said that we would talk a little bit about where this is all headed and what to do next. And like I just said, when I got lost in my own reverie, they&#8217;re like, hopefully, everything that I&#8217;ve shared here is out of date by 2050. Like, if we can come back to this particular podcast, or this presentation, or anything I&#8217;ve ever said, about digital literacy over my time with WordPress. And if we could come back to that in 2050, or, you know, I was going to say, 20 years from now, 2041 feels really close. But, you know, come back to it in the future, and say, that was all really excellent information to know at the moment. And we did do those things. And now, WordPress has proven that open source contribution and collaboration can teach all of the necessary 21st-century skills that anyone would need to survive in the world. And we did do it, that would be really cool. But I don&#8217;t think that that&#8217;s where we&#8217;re headed. Not because I don&#8217;t think people believe in what I&#8217;m saying or care about what I&#8217;m saying. But because it&#8217;s very easy to kind of let these things go at some point.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>27:20</p>\n\n\n\n<p>Even if you at some point, were proficient in all of what is considered 21st-century skills, sometimes our skills don&#8217;t get used very much. And so we lose track of them. And we don&#8217;t know, or we don&#8217;t know how to teach them to other people and various ways to do that. So I hope that when we revisit this in the time capsule of the internet 20 years from now, we can say that was a great explanation. And we learned so much. And we made so many changes in such progress that now we need a new version of this. That&#8217;s really all we can do, always striving to leave the world in a better position than when we found it.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>28:07</p>\n\n\n\n<p>All right, that brings us to our small list of big things. I don&#8217;t have a ton to share with you today. But what I do have to share with you is a really big deal. So we are about a week away from the Go/No-Go point of WordPress 5.9. That is the final release of the year. And as soon as we know what is a go or a no go from that meeting on October 12th,&nbsp; everyone is going just to hit the ground running. And so, if you are interested in contributing to that release, either by being a participant in the release squad or leading some part of the release squad. Absolutely. Drop by and let me know because I am interested to know who wants to learn more about doing that. And this is actually something that has gone by. I mentioned at the top that I spoke at WordCamp US.&nbsp; That is still true; I did do it. And so did a bunch of other really excellent presenters. If you missed WordCamp US on Friday, for whatever reason, because you know, life is complicated. Pretty soon, we will have the videos. We&#8217;ll have all the videos up with captions quickly and have those available for everyone to watch and learn more from as their schedule allows and as their attention allows. And that is your small list of big things. Thank you for tuning in today for the WordPress Briefing. I&#8217;m your host, Josepha Haden Chomphos, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11316\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:14;a:6:{s:4:\"data\";s:69:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"People of WordPress: Yordan Soares\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"https://wordpress.org/news/2021/09/people-of-wordpress-yordan-soares/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 30 Sep 2021 13:14:35 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:9:\"Community\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Features\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:9:\"heropress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:10:\"Interviews\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:16:\"wptranslationday\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11279\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:160:\"To coincide with International Translation Day, we feature the story of a WordPress developer and translator Yordan Soares from South America. #ContributorStory\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"webcommsat AbhaNonStopNewsUK\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:14398:\"\n<p>In this series, we share some of the inspiring stories of how WordPress and its global network of contributors can change people&#8217;s lives for the better. This month we feature a South American WordPress developer and community translator on how it brought him a freelance career.</p>\n\n\n\n<p><strong>To coincide with International Translation Day and the final day of the <a href=\"https://wptranslationday.org/\">2021 WordPress Translation celebration</a>, we feature the story of a WordPresser who has made a major impact in the polyglots team. </strong></p>\n\n\n\n<h2>Beyond software, meeting the WordPress community</h2>\n\n\n\n<figure class=\"wp-block-image size-full\"><img loading=\"lazy\" width=\"600\" height=\"600\" src=\"https://i2.wp.com/wordpress.org/news/files/2021/09/yordan-min-300x300@2x.jpg?resize=600%2C600&#038;ssl=1\" alt=\"Yordan Soares pictured in front of a bookcase packed with books\" class=\"wp-image-11299\" srcset=\"https://i2.wp.com/wordpress.org/news/files/2021/09/yordan-min-300x300@2x.jpg?w=600&amp;ssl=1 600w, https://i2.wp.com/wordpress.org/news/files/2021/09/yordan-min-300x300@2x.jpg?resize=300%2C300&amp;ssl=1 300w, https://i2.wp.com/wordpress.org/news/files/2021/09/yordan-min-300x300@2x.jpg?resize=150%2C150&amp;ssl=1 150w\" sizes=\"(max-width: 600px) 100vw, 600px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>For Yordan Soares, finding WordPress also meant discovering friends, community, and opportunities. He had not expected to find a whole global movement behind the web development software.</p>\n\n\n\n<p>Yordan, who is from Venezuela, South America, said: “The first time I used WordPress, I had no idea what was going on behind the software or beyond it. I knew there was someone making it all work, but I couldn’t even remotely imagine all the people who were making WordPress not just a tool for developing websites, but a whole movement that comes together to share, build, and help make the world better.”<br></p>\n\n\n\n<h2>Turning your hobby into your job</h2>\n\n\n\n<p>Initially, Yordan earned his income from computer technical support and installing networks for small and medium-sized businesses. Back in 2005, web development and code-writing was just a hobby. When he discovered the concept of CSS and how it could make everything dynamic. This opened up a whole new career pathway for him.</p>\n\n\n\n<p>In 2010, an introduction to WordPress through a friend opened up a future he had not imagined. He stopped using any other content management system (CMS) and wanted to explore how much he could do with the platform.</p>\n\n\n\n<p>Three years later in 2013, he was able to take his WordPress journey further by starting a small advertising agency with some of his friends. During this time, they crafted commercials for local radio and TV stations. Through WordPress, they found it easy to build more than 15 websites for businesses in a market that previously had not valued the importance of having an online presence.</p>\n\n\n\n<p>He said: “We were working for almost four years until at the end of 2017 the economic crisis in Venezuela became too acute, and we decided to close the agency when we stopped making profits. Taking stock of that period, I think we changed the way merchants saw the Internet business in the city.”</p>\n\n\n\n<h2>Working full time as a freelancer</h2>\n\n\n\n<p>One year on in 2018, Yordan began to freelance full-time as a web developer. His first clients were primarily agencies and friends who had emigrated to other countries. Later, he expanded into freelance marketplaces.</p>\n\n\n\n<p>At that time, getting started as a freelancer was quite a challenging task in Venezuela. The first hindrance was getting a fair level of recompense. Additionally, the deteriorating conditions in the country with constant blackouts and internet connection failures complicated matters.</p>\n\n\n\n<p>At the time, Yordan was living in Guarenas, where the situation was relatively better. He managed to get a reasonably uninterrupted electricity supply with adequate internet speed, which was just good enough to complete his projects.</p>\n\n\n\n<h2>Meeting the WordPress community</h2>\n\n\n\n<p>“Surely if I hadn’t met the community, I would have continued to work on my own, like a lone wolf, doing the ordinary work of solving problems for occasional customers and paying my bills,” he said.</p>\n\n\n\n<p>Living in Guarenas, Yordan began to experience stability in his career and made new friends and contacts. He began looking for co-working spaces or technology communities nearby, and that’s when he met the WordPress community in Caracas.</p>\n\n\n\n<p>He quickly signed up for the first face-to-face event. As soon as the event concluded, he went to talk to the co-organizer and offered his help. He was inspired to help with designing promotional pieces, managing social networks, and organizing events.</p>\n\n\n\n<p>Through such events, he met and connected with several people living in nearby cities.</p>\n\n\n\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:32% auto\"><figure class=\"wp-block-media-text__media\"><img loading=\"lazy\" width=\"120\" height=\"300\" src=\"https://i0.wp.com/wordpress.org/news/files/2021/09/WP_Guarenas-Guatire-120x300-1-120x300.png?resize=120%2C300&#038;ssl=1\" alt=\"WordPress Guarenas-Guatire Meetup logo \" class=\"wp-image-11300 size-medium\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2021/09/WP_Guarenas-Guatire-120x300-1.png?resize=120%2C300&amp;ssl=1 120w, https://i0.wp.com/wordpress.org/news/files/2021/09/WP_Guarenas-Guatire-120x300-1.png?w=240&amp;ssl=1 240w\" sizes=\"(max-width: 120px) 100vw, 120px\" data-recalc-dims=\"1\" /></figure><div class=\"wp-block-media-text__content\">\n<p class=\"has-normal-font-size\"> A few months later, with other WordPress users in the area, the idea emerged to start a new Guarenas-Guatire meetup group. The idea became a reality by December 2019, when they applied to the WordPress community team. </p>\n\n\n\n<p>Once the final approvals came in, the team started scheduling the activities. The <a href=\"https://www.meetup.com/wordpress-meetup-guarenas-guatire/\">WordPress Guarenas-Guatire Meetup</a> was officially recognized!</p>\n\n\n\n<p>During the first quarter of 2020, just before the pandemic, Yordan and colleagues organized five face-to-face events. With the worldwide lockdown, in-person community engagements came to a halt. However, the Guarenas-Guatire community was eager to continue meeting.</p>\n</div></div>\n\n\n\n<p>To keep the community active and motivated, and with the support of sponsors, they started organizing online events under a format called “WordPress a la medianoche” (WordPress at midnight).</p>\n\n\n\n<p>The format of these events was inspired by Alexis Arnal, who suggested meeting at midnight to make the most of the internet speed that would usually improve somewhat after that time!</p>\n\n\n\n<p>At the time of writing, Yordan and the team have organized 16 WordPress a la medianoche events with an attendance of up to 50 people per online gathering. An impressive audience which enabled a comparatively small meetup to keep communications flowing at a difficult and unprecedented time.</p>\n\n\n\n<p>One positive of such events, is an opportunity to invite people from other countries and run sessions in partnership with others. An example was a special translation event with Javier Esteban, a member of the translation team from Spain. The meetup also invited contributors from Mexico, Costa Rica, Peru, and Colombia who were happy to participate.</p>\n\n\n\n<h2>Strengthened by the community</h2>\n\n\n\n<figure class=\"wp-block-image size-large\"><img loading=\"lazy\" width=\"632\" height=\"241\" src=\"https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?resize=632%2C241&#038;ssl=1\" alt=\"Screenshot from the WordPress Translation Day 2020 events streamed on YouTube featuring Yordan\" class=\"wp-image-11307\" srcset=\"https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?resize=1024%2C390&amp;ssl=1 1024w, https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?resize=300%2C114&amp;ssl=1 300w, https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?resize=768%2C292&amp;ssl=1 768w, https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?resize=1536%2C585&amp;ssl=1 1536w, https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?w=1889&amp;ssl=1 1889w, https://i1.wp.com/wordpress.org/news/files/2021/09/wp-translation-2020.jpg?w=1264&amp;ssl=1 1264w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /><figcaption>Yordan (pictured bottom left) speaking at the WordPress Translation Day 2020 events</figcaption></figure>\n\n\n\n<p>Yordan believes that he would have continued to work on his own had he not met the WordPress community.</p>\n\n\n\n<p>He said: &#8220;Fortunately, this was not the case and I have been able to live a lot of transforming experiences that have shown me the meaning of the words &#8216;community&#8217; and &#8216;volunteerism&#8217;.&#8221;</p>\n\n\n\n<p>Yordan felt empowered by the community and it led him to volunteer for many other roles and activities, including Locale Manager for <a href=\"https://translate.wordpress.org/locale/es-ve/\">Spanish Venezuela</a>, a moderator for <a href=\"https://make.wordpress.org/tv/\">WordPress.tv</a>, and as a <a href=\"https://make.wordpress.org/support/\">support</a> contributor in the forums helping people with technical issues. He has also developed free plugins for the official WordPress directory and <a href=\"https://wordpress.tv/speakers/yordan-soares/\">spoken at community events</a> including <a href=\"https://spain.wordcamp.org/2020/session/actualizaciones-automaticas-para-tus-plugins-y-temas-personalizados/\">WordCamp Spain 2020</a>.  </p>\n\n\n\n<p>All this has a boomerang effect, Yordan explains. These experiences helped him grow personally and professionally, as it’s always rewarding to know that you’ve helped improve the WordPress ecosystem.</p>\n\n\n\n<p>He also likes being able to directly help people when they need it and feels it is a two-way learning process.</p>\n\n\n\n<p>&#8220;At first, when I told my family and friends about the community and the work I was doing, they would ask me &#8216;what do you get in return?&#8217; Perhaps they expected me to tell them a specific amount of money, but the answer is more complex,&#8221; he said.</p>\n\n\n\n<p>&#8220;It’s rewarding to know that you’ve helped improve the WordPress ecosystem, the tool you use to work and put food on the table.&#8221;</p>\n\n\n\n<p>During his journey, Yordan has met many wonderful people with common interests and values, and the best part is that many of these people are now his friends, business partners, or customers.</p>\n\n\n\n<p>At the same time, he has learned new skills and gained experience, which have given him a significant confidence boost in facing difficult situations that may come in life.</p>\n\n\n\n<figure class=\"wp-block-image size-large\"><img loading=\"lazy\" width=\"632\" height=\"332\" src=\"https://i1.wp.com/wordpress.org/news/files/2021/10/special-acknowledgement-yordansoares.png?resize=632%2C332&#038;ssl=1\" alt=\"Polyglots special appreciation poster featuring Yordan Soares. \" class=\"wp-image-11320\" srcset=\"https://i1.wp.com/wordpress.org/news/files/2021/10/special-acknowledgement-yordansoares.png?resize=1024%2C538&amp;ssl=1 1024w, https://i1.wp.com/wordpress.org/news/files/2021/10/special-acknowledgement-yordansoares.png?resize=300%2C158&amp;ssl=1 300w, https://i1.wp.com/wordpress.org/news/files/2021/10/special-acknowledgement-yordansoares.png?resize=768%2C403&amp;ssl=1 768w, https://i1.wp.com/wordpress.org/news/files/2021/10/special-acknowledgement-yordansoares.png?w=1200&amp;ssl=1 1200w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>As part of the <a href=\"https://wordpress.org/news/2021/09/join-us-for-wordpress-translation-day-global-events-in-september-2021/\">WordPress Translation Day 2021</a> celebrations, Yordan was nominated for his contribution to the work of the Polyglots Team translating WordPress. The full nominations list and stories will be published in October and November on the <a href=\"https://wptranslationday.org/\">WordPress Translation Day website</a>. </p>\n\n\n\n<h2>Share the stories</h2>\n\n\n\n<p>Help us share these stories of open source contributors and continue to grow the community.&nbsp;Meet more WordPressers in the <a href=\"https://wordpress.org/news/category/newsletter/interviews/\">People of WordPress series.</a>&nbsp;#ContributorStory.</p>\n\n\n\n<h2>Contributors</h2>\n\n\n\n<p>Thanks to Abha Thakor (<a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a>), Larissa Murillo (<a href=\'https://profiles.wordpress.org/lmurillom/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>lmurillom</a>), Maedah Batool (<a href=\'https://profiles.wordpress.org/maedahbatool/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>maedahbatool</a>), Chloé Bringmann (<a href=\'https://profiles.wordpress.org/cbringmann/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>cbringmann</a>), and Nalini (<a href=\'https://profiles.wordpress.org/nalininonstopnewsuk/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>nalininonstopnewsuk</a>) for work on this story. Thank you to Yordan Soares (<a href=\'https://profiles.wordpress.org/yordansoares/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>yordansoares</a>) for sharing his Contributor Story, and to Josepha Haden Chomphosy (<a href=\'https://profiles.wordpress.org/chanthaboune/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>chanthaboune</a>) and Topher DeRosia (<a href=\'https://profiles.wordpress.org/topher1kenobe/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>topher1kenobe</a>) for their support of the series. The WordPress Guarenas-Guatire logo on this page was designed by <a href=\"https://profiles.wordpress.org/bragnieljimenez/\">Bragniel Jimenez</a>. </p>\n\n\n\n<p><em>This People of WordPress feature is inspired by an article originally published on <a href=\"https://heropress.com/\">HeroPress.com</a>, a community initiative created by Topher DeRosia. The initiative highlights people in the WordPress community who have overcome barriers and whose stories would otherwise go unheard. </em>#HeroPress</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11279\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:15;a:6:{s:4:\"data\";s:65:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n		\n		\n\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"Upcoming Gallery Block improvements\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"https://wordpress.org/news/2021/09/upcoming-gallery-block-improvements/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 27 Sep 2021 15:36:59 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:6:\"Design\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:11:\"Development\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:8:\"Features\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11258\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:330:\"Thanks to @javiarce &#38; @annezazu for design and copy contributions. An exciting update to the Gallery Block gives you more ways to show off images in your posts and pages. While this change won’t be available for most folks until WordPress 5.9’s launch in January 2022, we wanted to share some of what’s to come [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:2:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:49:\"https://wordpress.org/news/files/2021/09/Main.mp4\";s:6:\"length\";s:8:\"16081214\";s:4:\"type\";s:9:\"video/mp4\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:56:\"https://wordpress.org/news/files/2021/09/Drag-_-Drop.mp4\";s:6:\"length\";s:7:\"1557447\";s:4:\"type\";s:9:\"video/mp4\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:13:\"Kelly Hoffman\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:2695:\"\n<p><em>Thanks to <a href=\'https://profiles.wordpress.org/javiarce/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>javiarce</a> &amp; <a href=\'https://profiles.wordpress.org/annezazu/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>annezazu</a> for design and copy contributions.</em></p>\n\n\n\n<figure class=\"wp-block-video\"><video autoplay controls loop src=\"https://wordpress.org/news/files/2021/09/Main.mp4\"></video></figure>\n\n\n\n<p>An exciting update to the Gallery Block gives you more ways to show off images in your posts and pages. While this change won’t be available for most folks until WordPress 5.9’s launch in January 2022, we wanted to share some of what’s to come to get you excited about the future.</p>\n\n\n\n<h2 id=\"style-individual-images\">Style individual images</h2>\n\n\n\n<p>You can now use the same tools that are available for individual image blocks on <em>each</em> image in the Gallery Block! This added flexibility means you can do more customization &#8211; from adding links to each individual image, inline cropping to edit on the fly, apply unique styles for more visually compelling images, and apply an array of duotone filters.</p>\n\n\n\n<h2 id=\"add-custom-styles\">Add custom styles</h2>\n\n\n\n<p>For more advanced folks who like to go a bit deeper in their customizations, you now have the option to add custom CSS styles per image. This is thanks to the ability to assign CSS classes to each image.</p>\n\n\n\n<h2 id=\"more-accessible-and-intuitive\">More accessible and intuitive&nbsp;</h2>\n\n\n\n<p>With this change comes the benefit of improved keyboard navigation and the ability to add alt text right within the block sidebar. You can also drag and drop to rearrange images.</p>\n\n\n\n<figure class=\"wp-block-video\"><video autoplay controls loop src=\"https://wordpress.org/news/files/2021/09/Drag-_-Drop.mp4\"></video></figure>\n\n\n\n<h2 id=\"next-steps\">Next steps</h2>\n\n\n\n<p>This will be available in January with the release of WordPress 5.9. You can check it out now if you’re using the Gutenberg plugin on any of your sites. Read more about <a href=\"https://wordpress.org/news/2021/04/become-an-early-adopter-with-the-gutenberg-plugin/\">becoming an early adopter</a> if you’d like to get ahead.</p>\n\n\n\n<p>If you’re a plugin or theme author who has built upon the Gallery Block functionality,<a href=\"https://make.wordpress.org/core/2021/08/20/gallery-block-refactor-dev-note/\"> be sure to check out this Dev Note detailing what steps need to be taken for compatibility</a> since this is a breaking change.</p>\n\n\n\n<p>We’re excited to see the new galleries that these options open up and what ideas you have to make creating galleries even better.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11258\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:16;a:6:{s:4:\"data\";s:58:\"\n		\n		\n		\n		\n		\n				\n		\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:54:\"WP Briefing: Episode 16: A Sneak Peek at WordPress 5.9\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:76:\"https://wordpress.org/news/2021/09/episode-16-a-sneak-peek-at-wordpress-5-9/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 20 Sep 2021 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:1:{i:0;a:5:{s:4:\"data\";s:11:\"wp-briefing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=11248\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:423:\"In addition to this episode’s small list of big things, Josepha Haden Chomphosy reviews the upcoming 5.9 WordPress release and its Full Site Editing features. Have a question you&#8217;d like answered? You can submit them to wpbriefing@wordpress.org, either written or as a voice recording. Credits Editor:&#160;Dustin Hartzler Logo:&#160;Beatriz Fialho Production:&#160;Chloé Bringmann Song: Fearless First by [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2021/09/WP-Briefing-016.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:11753:\"\n<p>In addition to this episode’s small list of big things, Josepha Haden Chomphosy reviews the upcoming 5.9 WordPress release and its Full Site Editing features.</p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2>Credits</h2>\n\n\n\n<ul><li>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a></li><li>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/beafialho/\">Beatriz Fialho</a></li><li>Production:&nbsp;<a href=\"https://profiles.wordpress.org/cbringmann/\">Chloé Bringmann</a></li><li>Song: Fearless First by Kevin MacLeod</li></ul>\n\n\n\n<h2>References </h2>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/2021/09/03/wordpress-5-9-planning-roundup/\">WordPress 5.9 Planning&nbsp;</a></p>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/2021/08/13/preliminary-road-to-5-9/\">5.9 Target Features</a></p>\n\n\n\n<p><a href=\"https://make.wordpress.org/core/2021/08/20/gallery-block-refactor-dev-note/\">Gallery Block Refactor Dev Notes</a></p>\n\n\n\n<p><a href=\"https://en.wikipedia.org/wiki/The_Cathedral_and_the_Bazaar\">The Cathedral and the Bazaar, 19 Lessons of Open Source</a></p>\n\n\n\n<p><a href=\"https://wptranslationday.org/\">WordPress Translation Day</a></p>\n\n\n\n<p><a href=\"https://us.wordcamp.org/2021/\">WordCamp US 2021</a></p>\n\n\n\n<p><a href=\"https://andreamiddleton.blog/2021/09/07/letters-to-an-open-source-contributor/\">Letters to an open source contributor, by Andrea Middleton</a></p>\n\n\n\n<h2>Transcript</h2>\n\n\n\n<span id=\"more-11248\"></span>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:10</p>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing. The podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Joseph Haden Chomphosy. Here we go.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>00:40</p>\n\n\n\n<p>Today I&#8217;m going to take you through a quick look at the final WordPress release of 2021. It will be WordPress 5.9. And there will be a ton of things in it, including a fresh new default theme. And there are a few things that you need to know about it right now. The target release date is December 14, 2021, which means some of our milestones happen around Thanksgiving in the US. And a few significant commercial dates globally, days, like Giving Tuesday and Black Friday, etc. I&#8217;ll include a link to the post with all the target dates in the show notes so that you can plan with those in mind. And also in the show notes. I&#8217;ll include a link to Matías Ventura&#8217;s post that includes the target features for the release. When you look at that post, you&#8217;ll notice that you can sort of group things into two big buckets. The two buckets that I grouped them into are themes plus tools, and also better tools.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>01:31</p>\n\n\n\n<p>So bucket number one themes and all their tools. Three things were important for me as I was reading through them. Number one is that there is a default theme. As of the time of this recording, I&#8217;ve seen the early concepts for the theme, and I love them. Hopefully, by the time this podcast is published, the post that showcases the look and feel will also be up on make.wordpress.org/design. If it is, I&#8217;ll include a link in the show notes to make it easy for everyone to reference.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:04</p>\n\n\n\n<p>The second thing is block themes in general. So WordPress 5.8 brought to core WordPress a lot of the infrastructure needed to create block themes. And in this release in WordPress 5.9, much of that infrastructure will be made available for folks who don&#8217;t always feel comfortable working in the code. That&#8217;s mostly UX and UI changes. So user experience and user interface changes are based on user feedback that we&#8217;ve gathered over the last six to eight months. But it also will include the long-awaited navigation block.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>02:37</p>\n\n\n\n<p>The third thing that shows up for me in this first bucket, in the themes and all their tools bucket, is the UX and interface for theme.json. The user interface that we&#8217;re making available for theme.json is a major step forward in this project has been referred to as global styles for a few years. And it kind of is what it sounds like on the box, a way for users to tap into that powerful management tool that we have built through theme.JSON.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:09</p>\n\n\n\n<p>Bucket number two, which I am calling publicly &#8220;tools for days.&#8221; But also, I refer to it as design tools, block tools, and pattern tools. I had this whole vision of a Wizard of Oz, “lions and tigers and bears. Oh my!” moment, but I couldn’t make it work. So “design tools and block tools and pattern tools Hoorah!” That&#8217;s as close as we&#8217;re getting. So that&#8217;s my first big number two bucket for you.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>03:37</p>\n\n\n\n<p>For most of these tools, the best way to describe it is quality of life improvements, lots of streamlining of what&#8217;s there, lots of building what&#8217;s not. But there&#8217;s one that&#8217;s substantial and worth digging into a bit more. And that&#8217;s the gallery block refactor. The dev note for this already exists. Like before we had the planning round-up post, the dev note was created. And so I will put a link to that in the show notes. But the headline is that this refactor will make the creation and maintenance of image blocks and the gallery block work the same way. If you are a theme or plugin developer, head on over to the dev notes that I have linked below and take some time to get familiar with it.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:20</p>\n\n\n\n<p>And then the final thing, which has a bunch of small things in it, but will make a huge impact for all of our users overall, is that we&#8217;re working on more intuitive and responsive tools on blocks. That has come up frequently in our user testing again over the last six to eight months. And we are going to chip away at that long list of needs that we have in those particular toolsets. And that&#8217;s it. So that&#8217;s a really big broad look at what we&#8217;re trying to get into the final release of the year.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>04:58</p>\n\n\n\n<p>I&nbsp; know that when I say like this is our hoped for stuff. This is our best guess at the moment. Sometimes it can feel like we should know that already &#8212; I should know already what&#8217;s going into the release. And on the one hand, yes, I believe in this list of things that we&#8217;re going to put into the release, I think they&#8217;re going to be good. But I always refer to it as like the hoped-for things, the things that are on the roadmap, our best targets, because I know that I don&#8217;t ever want to ship something that is going to be a worse experience for users. And so I always like to save the space to be able to remove a feature or remove an enhancement, a little bit closer to the time of the release, just to make sure that what we are offering is the best that we can offer. However, as it says right there in the 19 learnings of open source, “if there&#8217;s a bug, there&#8217;s a job,” right? There&#8217;s a lot of tolerance in open source software for shipping, slightly imperfect work. And that&#8217;s good. When we ship software that&#8217;s a little bit imperfect, it makes it clear how everyone can participate, how everyone could participate, if they could find this WordPress community that supports the CMS.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:20</p>\n\n\n\n<p>If you&#8217;ve never participated in a release and are interested in learning how it goes, you can always follow along on make.wordpress.org/core. And of course, we do a lot of our meetings in the making WordPress, community Slack, which you can find at chat.wordpress.org if you are not already in that particular instance.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>06:49</p>\n\n\n\n<p>That brings us now to our small list of big things. I have three things on my list for you. The first one is WP Translation day slash month. For folks who&#8217;ve been following along for a bit, you probably noticed that Translation Day has been going on all month long all of September so that we can have small individual local events and bring people into the process of translating WordPress and making WordPress more usable for more people, especially when they don&#8217;t necessarily speak English as their first language. It&#8217;s a wonderful event. There&#8217;s been Translation Day at the end of September for years. And this Translation Month is working its way up to that Translation Day; I will leave a link to the event page in the notes below. And I really encourage you to drop by.</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>07:38</p>\n\n\n\n<p>The second thing is that WordCamp US is coming up on October 1. It is going to be a virtual event, as so many of our events are right now. Tickets are open. The schedule just got published last week. And so we have a good concept of who is talking about what while we&#8217;re there. I suggest you wander over to the schedule. Take a look at anything that might be inspirational to you or anyone who looks like they&#8217;re answering questions that you&#8217;ve had as you have been trying to build your WordPress business.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:08</p>\n\n\n\n<p>And then the third thing on my smallest of big things. Some of you may already be aware that <a href=\"https://profiles.wordpress.org/andreamiddleton/\">Andrea Middleton</a> has left the WordPress project. She has been an absolute fixture in the WordPress open source project for the last ten years. And while we will all miss her terribly, her work has been so influential and so foundational that we actually won&#8217;t really feel much like she&#8217;s gone. We will see the evidence of her work in everything she does and everything she has done while we build a better and more inclusive WordPress after her.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>08:47</p>\n\n\n\n<p>As a final love letter to the community, she published a series of things that she learned about contributing to open source and especially how to contribute to WordPress as an open source project. I&#8217;m going to link those in the show notes as well. For anyone who has worked with Andrea for a long time, when you read it, it will just remind you of her voice and will be like a nice warm, comforting hug as she heads on to her next endeavors. And for folks who have never worked with her before. It&#8217;s still really excellent information that I think translates into all areas of our work, especially right now as people are moving to distributed work and remote work a bit more. Now I encourage everyone to at least give one or two of them a read.&nbsp;</p>\n\n\n\n<p><strong>Josepha Haden Chomphosy&nbsp; </strong>09:38</p>\n\n\n\n<p>That, my friends, is your small list of big things. Thank you for tuning in today for the WordPress Briefing. I&#8217;m your host, Josepha Haden Chomphosy, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11248\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:17;a:6:{s:4:\"data\";s:69:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"Join us for WordPress Translation Day Global Events in September 2021\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:105:\"https://wordpress.org/news/2021/09/join-us-for-wordpress-translation-day-global-events-in-september-2021/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 16 Sep 2021 21:51:33 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:5:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:7:\"Updates\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:9:\"polyglots\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:11:\"translation\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:16:\"wptranslationday\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11215\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:126:\"WordPress contributors around the world are celebrating the sixth Global WordPress Translation Day throughout September 2021! \";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:28:\"webcommsat AbhaNonStopNewsUK\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:8447:\"\n<p>WordPress contributors around the world are <a href=\"https://wptranslationday.org/\">celebrating the sixth Global WordPress Translation Day</a> throughout the entire month of September! That&#8217;s 30 days dedicated to help and encourage the volunteers that translate the software and its related resources. One of the highlights will be a <a href=\"https://wptranslationday.org/wptd-2021/global-events/\">series of exciting global events</a>, starting on September 17 2021 and finishing on the United Nations&#8217; International Translation Day itself on September 30, 2021.</p>\n\n\n\n<p>Everyone is welcome to watch these events live on <a href=\"https://www.youtube.com/c/WordPressMarketingTeam\">YouTube</a> and to share their translation stories which will be featured during the celebrations and beyond. The global events will be in English and include presentations on how and why to you should join the thousands of translators in the project, tips and tools, interviews, and much more. </p>\n\n\n\n<p>There are now 205 locales translating in what is a remarkable open source effort, bringing the opportunities of the software and its community to people in their own native languages.   </p>\n\n\n\n<h2>Inaugural session: Introduction and latest news on WordPress Translation</h2>\n\n\n\n<h3><strong>Friday, September 17, 2021 at </strong><a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210917T1000\"><strong>10:00 UTC</strong></a></h3>\n\n\n\n<p>We will start the <a href=\"https://wptranslationday.org/wptd-2021/global-events/\">global events</a> with a panel featuring the latest update on what is happening in the world of WordPress polyglots. Panellists will include translators and polyglot supporters <a href=\"https://profiles.wordpress.org/petya/\">Petya Raykovska</a> and <a href=\"https://profiles.wordpress.org/evarlese/\">Erica Varlese</a>. There will be a video demonstration on how to translate WordPress, a short presentation on translation statistics, a run down of upcoming events, and more. <br><br>Watch the <a rel=\"noreferrer noopener\" href=\"https://www.youtube.com/watch?v=3HSBB5F_ag0\" target=\"_blank\">event live on YouTube</a> (or click on the play button below) &#8211; sign-up for notifications in the video stream right now so you don&#8217;t miss it when it goes live!&nbsp;</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<iframe loading=\"lazy\" class=\"youtube-player\" width=\"632\" height=\"356\" src=\"https://www.youtube.com/embed/3HSBB5F_ag0?version=3&#038;rel=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;fs=1&#038;hl=en-US&#038;autohide=2&#038;wmode=transparent\" allowfullscreen=\"true\" style=\"border:0;\" sandbox=\"allow-scripts allow-same-origin allow-popups allow-presentation\"></iframe>\n</div></figure>\n\n\n\n<h3><strong>Friday, September 17, 2021 at 11:00 UTC</strong> </h3>\n\n\n\n<p>Right after the livestream, there will be a &#8216;drop-in&#8217; translation sprint on Zoom video-conferencing, open to all. You can join and hang out virtually with your Polyglots friends from all around the world and translate WordPress in your own language! <a href=\"https://www.meetup.com/Portsmouth-WordPress-Meetup/events/280823032/\">RSVP for the session now and get joining links!</a> </p>\n\n\n\n<figure class=\"wp-block-image\"><img src=\"https://lh4.googleusercontent.com/r2rVEDkb39h02LiBg2GdayDk1LpZi6kxZnv1MRzT9h6pgSQE9FMBmyx_kJUA-Pjyy1KD__YlzLYvd1P53o1UIxTEfb0fmQnHm40UU23Wb1slXm-ajdGNP5qOyzHyTI07NBCM-mwY=s0\" alt=\"\" /></figure>\n\n\n\n<h2>Check out the other exciting global events</h2>\n\n\n\n<h3><strong>Sunday, September 19, 2021 <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210919T1200\">12:00 UTC</a></strong></h3>\n\n\n\n<p><a rel=\"noreferrer noopener\" href=\"https://www.youtube.com/watch?v=M7UtEKIp2FY\" target=\"_blank\"><strong>Panel on Polyglots Tools</strong></a><a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210919T1200\"> <br></a>Join <a href=\"https://profiles.wordpress.org/amieiro/\">Jesús Amieiro</a>, <a href=\"https://profiles.wordpress.org/psmits1567/\">Peter Smits</a>, <a href=\"https://profiles.wordpress.org/vladytimy/\">Vlad Timotei</a>, and <a href=\"https://profiles.wordpress.org/vibgyj/\">Vibgy Joseph</a> to talk about the tools they’ve contributed to or developed to help translators and translation editors.</p>\n\n\n\n<h3><strong>Tuesday, September 21, 2021 <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210921T1100\">11:00 UTC</a></strong></h3>\n\n\n\n<p><a href=\"https://www.youtube.com/watch?v=0Ps8byYIwLs\"><strong>Panel on Open Source Translation Communities</strong></a> (YouTube link &#8211; opens in a new tab)<a rel=\"noreferrer noopener\" href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210921T1100\" target=\"_blank\"><br></a>Join <a href=\"https://profiles.wordpress.org/vanillalounge/\">Zé Fontainhas</a> (WordPress), <a href=\"https://profiles.wordpress.org/tokyobiyori/\">Ali Darwich</a> (WordPress), <a href=\"https://www.linkedin.com/in/mstanke?originalSubdomain=cz\">Michal Stanke</a> (Mozilla), and <a href=\"https://www.linkedin.com/in/satomi-tsujita/?originalSubdomain=jp\">Satomi Tsujita</a> (Hyperledger Fabric) to learn about nurturing translation communities.</p>\n\n\n\n<h3><strong>Thursday, September 30, 2021 <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210930T1600\">16:00 UTC</a></strong></h3>\n\n\n\n<p><a href=\"https://www.youtube.com/watch?v=qyQUaiK19js\"><strong>Finale Event – Why do you translate?</strong></a> <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?iso=20210930T1600\"><br></a>Our finale event for 2021 with emcee <a href=\"https://profiles.wordpress.org/webcommsat/\">Abha Thakor</a> and a panel from the WordPress Translation Day Team. It will feature highlights from some of the local and global events during the month and a selection of results. Some of the nominees for this year&#8217;s polyglots appreciation will join the livestream to share their stories.</p>\n\n\n\n<p>The livestream will be followed by an after party celebration for anyone who has taken part in the event or is a WordPress polyglot. <a href=\"https://www.meetup.com/Portsmouth-WordPress-Meetup/events/280823393/\">Book now</a> for the session on Zoom.</p>\n\n\n\n<h2>Ideas on how to get involved this September</h2>\n\n\n\n<p>There&#8217;s lots of ways to take part &#8211; discover this <a href=\"https://wptranslationday.org/blog/ways-to-participate/\">list of ideas</a>.</p>\n\n\n\n<p>You can also nominate <a href=\"https://wptranslationday.org/blog/call-for-nominations-polyglots-appreciation/\">translation contributors</a> to be featured in this year&#8217;s celebrations.</p>\n\n\n\n<h2>Help us spread the word about #WPTranslationDay</h2>\n\n\n\n<ul><li>Share what you are translating using the #WPTranslationDay hashtag on: <a href=\"https://www.facebook.com/WPTranslationDay/\">Facebook</a>,<a href=\"https://twitter.com/TranslateWP\"> Twitter</a>, <a href=\"https://www.instagram.com/wptranslationday/\">Instagram</a>, and <a href=\"https://www.linkedin.com/company/wptranslationday/\">LinkedIn</a>! </li><li>You can also find images and translator badges in our <a href=\"https://wptranslationday.org/wptd-2021/media-kit/\">Media Kit</a> to share during September.</li></ul>\n\n\n\n<p>For more information on the 2021 WordPress Translation Day celebrations, visit <a href=\"https://wptranslationday.org/\">the WordPress Translation Day website</a>.</p>\n\n\n\n<p></p>\n\n\n\n<p><em>Props to <a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a>, <a href=\'https://profiles.wordpress.org/harishanker/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>harishanker</a>, <a href=\'https://profiles.wordpress.org/lmurillom/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>lmurillom</a>, <a href=\'https://profiles.wordpress.org/oglekler/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>oglekler</a>, <a href=\'https://profiles.wordpress.org/meher/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>meher</a>, <a href=\'https://profiles.wordpress.org/nalininonstopnewsuk/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>nalininonstopnewsuk</a>, <a href=\'https://profiles.wordpress.org/evarlese/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>evarlese</a></em> for contributing to this story.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11215\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:18;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:48:\"WordPress 5.8.1 Security and Maintenance Release\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:84:\"https://wordpress.org/news/2021/09/wordpress-5-8-1-security-and-maintenance-release/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 09 Sep 2021 03:11:37 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:8:\"Releases\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Security\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11190\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:363:\"WordPress 5.8.1 is now available! This security and maintenance release features 60 bug fixes in addition to 3 security fixes. Because this is a security release, it is recommended that you update your sites immediately. All versions since WordPress 5.4 have also been updated. WordPress 5.8.1 is a short-cycle security and maintenance release. The next [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:19:\"Jonathan Desrosiers\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:8900:\"\n<p>WordPress 5.8.1 is now available!</p>\n\n\n\n<p>This security and maintenance release features <a href=\"https://core.trac.wordpress.org/query?milestone=5.8.1&amp;group=component&amp;col=id&amp;col=summary&amp;col=milestone&amp;col=owner&amp;col=type&amp;col=status&amp;col=priority&amp;order=priority\">60 bug</a> <a href=\"https://github.com/WordPress/gutenberg/pull/34393\">fixes</a> in addition to 3 security fixes. Because this is a <strong>security release</strong>, it is recommended that you update your sites immediately. All versions since WordPress 5.4 have also been updated.</p>\n\n\n\n<p>WordPress 5.8.1 is a short-cycle security and maintenance release. The next major release will be version <a href=\"https://make.wordpress.org/core/5-9/\">5.9</a>.</p>\n\n\n\n<p>You can download WordPress 5.8.1 by downloading from WordPress.org, or visit your Dashboard → Updates and click Update Now.</p>\n\n\n\n<p>If you have sites that support automatic background updates, they’ve already started the update process.</p>\n\n\n\n<h3><strong>Security Updates</strong></h3>\n\n\n\n<p>3 security issues affect WordPress versions between 5.4 and 5.8. If you haven’t yet updated to 5.8, all WordPress versions since 5.4 have also been updated to fix the following security issues:</p>\n\n\n\n<ul><li>Props <a href=\'https://profiles.wordpress.org/mdawaffe/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>mdawaffe</a>, member of the WordPress Security Team for their work fixing a data exposure vulnerability within the REST API.</li><li>Props to Michał Bentkowski of Securitum for reporting a XSS vulnerability in the block editor.</li><li>The Lodash library has been updated to version 4.17.21 in each branch to incorporate upstream security fixes.</li></ul>\n\n\n\n<p>In addition to these issues, the security team would like to thank the following people for reporting vulnerabilities during the WordPress 5.8 beta testing period, allowing them to be fixed prior to release:</p>\n\n\n\n<ul><li>Props <a rel=\"noreferrer noopener\" target=\"_blank\" href=\"https://evanricafort.com/\">Evan Ricafort</a>&nbsp;for reporting a XSS vulnerability in the block editor discovered during the 5.8 release’s beta period.</li><li>Props <a rel=\"noreferrer noopener\" target=\"_blank\" href=\"https://profiles.wordpress.org/stevehenty/\">Steve Henty</a>&nbsp;for reporting a privilege escalation issue in the block editor.</li></ul>\n\n\n\n<p>Thank you to all of the reporters for <a href=\"https://make.wordpress.org/core/handbook/testing/reporting-security-vulnerabilities/\">privately disclosing the vulnerabilities</a>. This gave the WordPress security team time to fix the vulnerabilities before WordPress sites could be attacked.</p>\n\n\n\n<p>For more information, browse the <a href=\"https://core.trac.wordpress.org/query?milestone=5.8.1&amp;group=component&amp;col=id&amp;col=summary&amp;col=milestone&amp;col=owner&amp;col=type&amp;col=status&amp;col=priority&amp;order=priority\">full list of changes</a> on Trac, or check out the <a href=\"https://wordpress.org/support/wordpress-version/version-5-8-1/\">version 5.8.1 HelpHub documentation page</a>.</p>\n\n\n\n<h2>Thanks and props!</h2>\n\n\n\n<p>The 5.8.1 release was led by <a href=\"https://profiles.wordpress.org/desrosj/\">Jonathan Desrosiers</a> and <a href=\"https://profiles.wordpress.org/circlecube/\">Evan Mullins</a>.</p>\n\n\n\n<p>In addition to the security researchers and release squad members mentioned above, thank you to everyone who helped make WordPress 5.8.1 happen:</p>\n\n\n\n<a href=\"https://profiles.wordpress.org/2linctools/\">2linctools</a>, <a href=\"https://profiles.wordpress.org/zieladam/\">Adam Zielinski</a>, <a href=\"https://profiles.wordpress.org/schlessera/\">Alain Schlesser</a>, <a href=\"https://profiles.wordpress.org/ajlende/\">Alex Lende</a>, <a href=\"https://profiles.wordpress.org/alexstine/\">alexstine</a>, <a href=\"https://profiles.wordpress.org/algala/\">AlGala</a>, <a href=\"https://profiles.wordpress.org/oandregal/\">André</a>, <a href=\"https://profiles.wordpress.org/andraganescu/\">Andrei Draganescu</a>, <a href=\"https://profiles.wordpress.org/azaozz/\">Andrew Ozz</a>, <a href=\"https://profiles.wordpress.org/ankitmaru/\">Ankit Panchal</a>, <a href=\"https://profiles.wordpress.org/antpb/\">Anthony Burchell</a>, <a href=\"https://profiles.wordpress.org/antonvlasenko/\">Anton Vlasenko</a>, <a href=\"https://profiles.wordpress.org/aristath/\">Ari Stathopoulos</a>, <a href=\"https://profiles.wordpress.org/ribaricplusplus/\">Bruno Ribaric</a>, <a href=\"https://profiles.wordpress.org/poena/\">Carolina Nymark</a>, <a href=\"https://profiles.wordpress.org/daisyo/\">Daisy Olsen</a>, <a href=\"https://profiles.wordpress.org/talldanwp/\">Daniel Richards</a>, <a href=\"https://profiles.wordpress.org/dariak/\">Daria</a>, <a href=\"https://profiles.wordpress.org/davidanderson/\">David Anderson</a>, <a href=\"https://profiles.wordpress.org/davidbinda/\">David Biňovec</a>, <a href=\"https://profiles.wordpress.org/dlh/\">David Herrera</a>, <a href=\"https://profiles.wordpress.org/ocean90/\">Dominik Schilling</a>, <a href=\"https://profiles.wordpress.org/ellatrix/\">Ella van&nbsp;Durpe</a>, <a href=\"https://profiles.wordpress.org/enchiridion/\">Enchiridion</a>, <a href=\"https://profiles.wordpress.org/circlecube/\">Evan Mullins</a>, <a href=\"https://profiles.wordpress.org/garyj/\">Gary Jones</a>, <a href=\"https://profiles.wordpress.org/mamaduka/\">George Mamadashvili</a>, <a href=\"https://profiles.wordpress.org/gziolo/\">Greg Ziółkowski</a>, <a href=\"https://profiles.wordpress.org/priethor/\">Héctor Prieto</a>, <a href=\"https://profiles.wordpress.org/ianmjones/\">ianmjones</a>, <a href=\"https://profiles.wordpress.org/audrasjb/\">Jb Audras</a>, <a href=\"https://profiles.wordpress.org/jblz/\">Jeff Bowen</a>, <a href=\"https://profiles.wordpress.org/joedolson/\">Joe Dolson</a>, <a href=\"https://profiles.wordpress.org/joen/\">Joen A.</a>, <a href=\"https://profiles.wordpress.org/johnbillion/\">John Blackbourn</a>, <a href=\"https://profiles.wordpress.org/desrosj/\">Jonathan Desrosiers</a>, <a href=\"https://profiles.wordpress.org/juanmaguitar/\">JuanMa Garrido</a>, <a href=\"https://profiles.wordpress.org/jrf/\">Juliette Reinders Folmer</a>, <a href=\"https://profiles.wordpress.org/kevin940726/\">Kai Hao</a>, <a href=\"https://profiles.wordpress.org/kapilpaul/\">Kapil Paul</a>, <a href=\"https://profiles.wordpress.org/gwwar/\">Kerry Liu</a>, <a href=\"https://profiles.wordpress.org/kevinfodness/\">Kevin Fodness</a>, <a href=\"https://profiles.wordpress.org/mkaz/\">Marcus Kazmierczak</a>, <a href=\"https://profiles.wordpress.org/mark-k/\">Mark-k</a>, <a href=\"https://profiles.wordpress.org/mhuntdesign/\">Matt</a>, <a href=\"https://profiles.wordpress.org/mdawaffe/\">Michael Adams (mdawaffe)</a>, <a href=\"https://profiles.wordpress.org/mikeschroder/\">Mike Schroder</a>, <a href=\"https://profiles.wordpress.org/moch11/\">moch11</a>, <a href=\"https://profiles.wordpress.org/mukesh27/\">Mukesh Panchal</a>, <a href=\"https://profiles.wordpress.org/ntsekouras/\">Nik Tsekouras</a>, <a href=\"https://profiles.wordpress.org/paaljoachim/\">Paal Joachim Romdahl</a>, <a href=\"https://profiles.wordpress.org/swissspidy/\">Pascal Birchler</a>, <a href=\"https://profiles.wordpress.org/pbearne/\">Paul Bearne</a>, <a href=\"https://profiles.wordpress.org/pbiron/\">Paul Biron</a>, <a href=\"https://profiles.wordpress.org/peterwilsoncc/\">Peter Wilson</a>, <a href=\"https://profiles.wordpress.org/walbo/\">Petter Walbø Johnsgård</a>, <a href=\"https://profiles.wordpress.org/radixweb/\">Radixweb</a>, <a href=\"https://profiles.wordpress.org/rtm909/\">Rahul Mehta</a>, <a href=\"https://profiles.wordpress.org/ramonopoly/\">ramonopoly</a>, <a href=\"https://profiles.wordpress.org/ravipatel/\">ravipatel</a>, <a href=\"https://profiles.wordpress.org/youknowriad/\">Riad Benguella</a>, <a href=\"https://profiles.wordpress.org/noisysocks/\">Robert Anderson</a>, <a href=\"https://profiles.wordpress.org/kreppar/\">Rodrigo Arias</a>, <a href=\"https://profiles.wordpress.org/sanketchodavadiya/\">Sanket Chodavadiya</a>, <a href=\"https://profiles.wordpress.org/sergeybiryukov/\">Sergey Biryukov</a>, <a href=\"https://profiles.wordpress.org/sabernhardt/\">Stephen Bernhardt</a>, <a href=\"https://profiles.wordpress.org/netweb/\">Stephen Edgar</a>, <a href=\"https://profiles.wordpress.org/stevehenty/\">Steve Henty</a>, <a href=\"https://profiles.wordpress.org/terraling/\">terraling</a>, <a href=\"https://profiles.wordpress.org/timothyblynjacobs/\">Timothy Jacobs</a>, <a href=\"https://profiles.wordpress.org/tmatsuur/\">tmatsuur</a>, <a href=\"https://profiles.wordpress.org/tobiasbg/\">TobiasBg</a>, <a href=\"https://profiles.wordpress.org/hellofromTonya/\">Tonya Mork</a>, <a href=\"https://profiles.wordpress.org/toro_unit/\">Toro_Unit (Hiroshi Urabe)</a>, <a href=\"https://profiles.wordpress.org/vladytimy/\">Vlad T</a>, <a href=\"https://profiles.wordpress.org/wb1234/\">wb1234</a>, and <a href=\"https://profiles.wordpress.org/wfmattr/\">WFMattR</a>.\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11190\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:19;a:6:{s:4:\"data\";s:63:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"The Month in WordPress: August 2021\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:70:\"https://wordpress.org/news/2021/09/the-month-in-wordpress-august-2021/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 03 Sep 2021 14:00:13 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:18:\"Month in WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:18:\"month in wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:7:\"updates\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=11176\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:338:\"I really believe in WordPress’ mission to democratize publishing. And I, for one, will never stop learning about what gives people more access to the software, and what makes the software more usable, and especially how we can combine usability with accessibility in a way that puts form and function on a level playing field. [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Hari Shanker R\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:16142:\"\n<blockquote class=\"wp-block-quote\"><p>I really believe in WordPress’ mission to democratize publishing. And I, for one, will never stop learning about what gives people more access to the software, and what makes the software more usable, and especially how we can combine usability with accessibility in a way that puts form and function on a level playing field.<br></p></blockquote>\n\n\n\n<p>That was <a href=\"https://profiles.wordpress.org/chanthaboune/\">Josepha Haden</a> on the “<a href=\"https://wordpress.org/news/2021/08/episode-14-the-art-and-science-of-accessibility/\">The Art and Science of Accessibility</a>” episode of the <a href=\"https://wordpress.org/news/podcast/\">WP Briefing Podcast</a>, talking about accessibility and exploring how it applies to the WordPress open source software. You will find that many of our updates from August 2021 tie in closely with the core principles of access, accessibility, and usability. Read on to find out more!</p>\n\n\n\n<hr class=\"wp-block-separator\" />\n\n\n\n<h2>Join the 2021 WordPress Translation Day Celebrations in September</h2>\n\n\n\n<figure class=\"wp-block-image size-large\"><a href=\"https://wptranslationday.org/\" target=\"_blank\" rel=\"noopener\"><img loading=\"lazy\" width=\"632\" height=\"395\" src=\"https://i0.wp.com/wordpress.org/news/files/2021/09/image.jpeg?resize=632%2C395&#038;ssl=1\" alt=\"WordPress Translation Day 2021 September 1 - 30, 2021\" class=\"wp-image-11177\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2021/09/image.jpeg?resize=1024%2C640&amp;ssl=1 1024w, https://i0.wp.com/wordpress.org/news/files/2021/09/image.jpeg?resize=300%2C188&amp;ssl=1 300w, https://i0.wp.com/wordpress.org/news/files/2021/09/image.jpeg?resize=768%2C480&amp;ssl=1 768w, https://i0.wp.com/wordpress.org/news/files/2021/09/image.jpeg?w=1440&amp;ssl=1 1440w, https://i0.wp.com/wordpress.org/news/files/2021/09/image.jpeg?w=1264&amp;ssl=1 1264w\" sizes=\"(max-width: 632px) 100vw, 632px\" data-recalc-dims=\"1\" /></a></figure>\n\n\n\n<p>Join WordPress contributors around the world on <a href=\"https://wptranslationday.org/\">WordPress Translation Day celebrations</a> for the entire month of September! The sixth edition of #WPTranslationDay &#8211; which is a cross-team effort led by the Polyglots and Marketing Teams, has a host of fun programs aimed at helping WordPress speak all languages of the world. Want to join the fun? Here’s how.</p>\n\n\n\n<ul><li><a href=\"https://wptranslationday.org/wptd-2021/local-events/\">Join your local translation event</a> and collaborate on efforts to translate WordPress! Can’t find a <a href=\"https://wptranslationday.org/wptd-2021/local-events/\">local event in your area</a>? Why don’t you <a href=\"https://make.wordpress.org/polyglots/2021/08/11/call-for-local-event-organizers-wordpress-translation-day-2021/\">organize one on your own</a>?</li><li>Join <a href=\"https://wptranslationday.org/wptd-2021/global-events/\">one of our global events</a> from September 17-30!<em> </em>We have exciting sessions on <a href=\"https://wptranslationday.org/wptd-2021/global-events/\">polyglots tools and open source translation communities</a>, to name a few.</li><li>You can help<a href=\"https://translate.wordpress.org/\"> translate WordPress to your own language</a>, in your own time, this month! <a href=\"https://make.wordpress.org/polyglots/handbook/translating/first-steps/\">Here’s how!</a></li><li>Share what you are translating using the #WPTranslationDay hashtag on social media, to build excitement around the event! Don’t forget to amplify posts about the event on <a href=\"https://www.facebook.com/WPTranslationDay/\">Facebook</a>, <a href=\"https://twitter.com/TranslateWP\">Twitter</a>, <a href=\"https://www.instagram.com/wptranslationday/\">Instagram</a>, and <a href=\"https://www.linkedin.com/company/wptranslationday/\">LinkedIn</a>!</li></ul>\n\n\n\n<p>&nbsp;For more information, check out <a href=\"https://wptranslationday.org/\">the translation day website</a> and the <a href=\"https://make.wordpress.org/polylgots/tag/wptranslationday/\">Polyglots blog</a>.</p>\n\n\n\n<h2>WordPress Release Updates</h2>\n\n\n\n<p>The Core Team <a href=\"https://make.wordpress.org/core/2021/08/13/preliminary-road-to-5-9/\">commenced work</a> on the next major release &#8211; WordPress 5.9. The team aims to ship some cool features such as intrinsic web design to blocks, improved block patterns, navigation menus, better design tools, edit flows for block themes, and a new interface for theme.json. Check out the <a href=\"https://make.wordpress.org/core/5-9/\">WordPress 5.9 development cycle</a> to know more. This release is set to go out in December 2021. The team is also <a href=\"https://make.wordpress.org/core/2021/08/24/wordpress-5-8-x-release-team-and-5-8-1-schedule/\">working on shipping a minor release WordPress 5.8.1</a> –– its <a href=\"https://make.wordpress.org/core/2021/09/01/wordpress-5-8-1-rc-1/\">release candidate</a> is already out and the final release will launch on September 8.</p>\n\n\n\n<p>Want to contribute to WordPress core? Join the <a href=\"https://wordpress.slack.com/archives/C02RQBWTW\">#core</a> channel, follow the <a href=\"https://make.wordpress.org/core/\">Core Team blog</a>, and check out the <a href=\"https://make.wordpress.org/core/handbook/\">team handbook</a>. Don’t miss the Core Team chats on Wednesdays at <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?hour=5&amp;min=00&amp;sec=0\">5 AM</a> and <a href=\"https://www.timeanddate.com/worldclock/fixedtime.html?hour=20&amp;min=00&amp;sec=0\">8 PM</a> UTC. You can also help <a href=\"https://translate.wordpress.org/\">translate WordPress</a> to your local language &#8211; and what better time to do it, than in September, <a href=\"https://wptranslationday.org/\">during the translation month celebrations</a>? Another fun way to contribute would be to <a href=\"https://make.wordpress.org/marketing/2021/08/11/share-about-wordpress-5-8-on-social-media/\">share about WordPress 5.8 on social media</a>!</p>\n\n\n\n<h2>Say Hello to Gutenberg Versions 11.2 and 11.3</h2>\n\n\n\n<p>We launched Gutenberg <a href=\"https://make.wordpress.org/core/2021/08/05/whats-new-in-gutenberg-11-2-0-4-august/\">version 11.2</a> and <a href=\"https://make.wordpress.org/core/2021/08/18/whats-new-in-gutenberg-11-3-18-august/\">version 11.3</a> this month. <a href=\"https://make.wordpress.org/core/2021/08/05/whats-new-in-gutenberg-11-2-0-4-august/\">Version 11.2</a> adds customizing/color options to the search block, a flex layout for the group block, and a new button for creating posts as part of the publishing flow. <a href=\"https://make.wordpress.org/core/2021/08/18/whats-new-in-gutenberg-11-3-18-august/\">Version 11.3</a> offers a new dimensions panel (replacing the spacing panel) with more styling options, dimensions control for the feature image block, and significant performance improvements for block inserters.<br></p>\n\n\n\n<p>Want to get involved in building Gutenberg? Follow <a href=\"https://make.wordpress.org/core/\">the Core Team blog</a>, contribute to <a href=\"https://github.com/WordPress/gutenberg/\">Gutenberg on GitHub</a>, and join the <a href=\"https://wordpress.slack.com/archives/C02QB2JS7\">#core-editor</a> channel in the <a href=\"https://make.wordpress.org/chat/\">Make WordPress Slack</a>. The “<a href=\"https://make.wordpress.org/core/2021/07/21/whats-next-in-gutenberg-site-editing-status-check-late-july-august-2021/\">What’s next in Gutenberg</a>” post offers more details on the latest updates.&nbsp;</p>\n\n\n\n<h2>Get Excited about WordCamp US 2021</h2>\n\n\n\n<p>The biggest WordCamp in North America &#8211; <a href=\"https://us.wordcamp.org/2021/\">WordCamp US 2021-</a> is barely a month away. <a href=\"https://us.wordcamp.org/2021/tickets/\">Get your (free) tickets</a>, if you haven’t already! The organizing team has opened up calls for <a href=\"https://us.wordcamp.org/2021/calling-all-musicians/\">musicians</a>, <a href=\"https://us.wordcamp.org/2021/call-for-contributor-stories/\">contributor stories</a>, and <a href=\"https://us.wordcamp.org/2021/call-for-media-partners/\">media partners</a>. Check out <a href=\"https://us.wordcamp.org/2021/\">the event website</a> and follow the event on <a href=\"https://twitter.com/wordcampus\">Twitter</a>, <a href=\"https://www.instagram.com/wordcampus/\">Instagram</a>, and <a href=\"https://www.facebook.com/WordCampUSA\">Facebook</a> to stay updated on all that #WCUS news.</p>\n\n\n\n<h2>Important Announcements/Updates</h2>\n\n\n\n<ul><li>Support for the Classic Editor plugin <a href=\"https://wordpress.org/news/2021/08/an-update-on-the-classic-editor-plugin/\">will be extended for a full year until 2022</a>!</li><li>The Themes Team published a <a href=\"https://make.wordpress.org/themes/2021/08/06/summary-of-the-request-for-feedback-on-requirement-changes/\">feedback summary</a> on the <a href=\"https://make.wordpress.org/themes/2021/07/20/discussion-request-for-feedback-on-requirement-changes/\">proposed requirement changes for WordPress theme review</a>, and will soon be updating the handbook and review tools. You will find more context in their <a href=\"https://make.wordpress.org/themes/2021/08/10/meeting-notes-themes-team-and-theme-authors-regarding-requirements-change/\">meeting notes</a>.</li><li>The Plugins Team <a href=\"https://make.wordpress.org/plugins/2021/08/27/inaccurate-stats-have-been-corrected/\">corrected inaccurate plugin statistics</a> in the plugin directory.</li><li>Check out the <a href=\"https://make.wordpress.org/test/2021/08/17/fse-program-theme-design-survey-results/\">results of the FSE Theme Design (theme.json) survey</a>!</li></ul>\n\n\n\n<h2>Feedback/Testing Requests from Contributor Teams</h2>\n\n\n\n<ul><li>The Testing Team is requesting testing help for a <a href=\"https://make.wordpress.org/test/2021/08/30/week-in-test-30-aug-2021/\">few tickets and patches in WordPress</a>. The team is also looking for volunteers to <a href=\"https://make.wordpress.org/test/2021/08/20/call-for-volunteeers-audit-and-update-testing-instructions-across-the-make-sites/\">audit and update Core testing instructions </a>across different Make sites.</li><li>The Community Team shared a <a href=\"https://make.wordpress.org/community/2021/09/01/proposal-centralized-global-do_action-charity-hackathons/\">proposal on planning online-only recurring global do_action charity hackathons</a>. Share your feedback by September 24.</li><li>The Training Team is also exploring the possibility of adding <a href=\"https://make.wordpress.org/training/2021/08/10/proposal-learner-achievements-on-profiles/\">learner achievements (on learn.wordpress.org) on WP Profiles</a> and is requesting feedback from the community.</li><li>Help the Marketing Team’s outreach research by filling out <a href=\"https://forms.gle/4QFhX8fcNxKAfK8y5\">this 2 question survey</a> on how you search for WordPress release information. If you have any favorite features from the latest release (WordPress 5.8) let the team know by completing this <a href=\"https://docs.google.com/forms/d/e/1FAIpQLSd_2llymf79-h5sgTiprz7Kw4Gr4cbDHh-AAdAQfiArXlHksg/viewform\">short form</a>.</li><li>Version 18.1 of WordPress for <a href=\"https://make.wordpress.org/mobile/2021/08/23/call-for-testing-wordpress-for-ios-18-1/\">iOS</a> and <a href=\"https://make.wordpress.org/mobile/2021/08/23/call-for-testing-wordpress-for-android-18-1/\">Android</a> are now available for testing!</li></ul>\n\n\n\n<h2>WordPress Event Updates</h2>\n\n\n\n<ul><li><a href=\"https://floripa.wordcamp.org/2021/\">WordCamp Florianopolis 2021</a> was held on August 11-12, 2021. The event, which sold 390 tickets, had 11 speakers and 4 sponsors. Catch the <a href=\"https://www.youtube.com/channel/UCh1CeGfuDRH-o13xh7kpgUQ\">event recap on YouTube</a>!</li><li><a href=\"https://galicia.wordcamp.org/2021/\">WordCamp Galicia 2021</a> is being held from September 30 &#8211; October 2, 2021! </li><li><a href=\"https://doaction.org/event/karnataka-2021/\">do_action Karnataka 2021</a> was held from August 7-15, 2021. Check out <a href=\"https://wordpressfoundation.org/2021/do_action-karnataka-2021-a-report/\">the recap</a>!</li><li>The Core Team organized a <a href=\"https://make.wordpress.org/core/2021/08/25/hallway-hangout-summary-compare-and-contrast-the-navigation-screens/\">hallway hangout</a> to compare the ‘experimental’ Gutenberg navigation feature with the built-in core feature. The team decided to wait until feature parity with core nav menus, to move the feature from experiments to the main plugin.</li><li>The Diverse Speakers Training group (<a href=\"https://make.wordpress.org/community/tag/wpdiversity/\">#WPDiversity</a>) of the Community Team held their first “<a href=\"https://make.wordpress.org/community/2021/09/02/report-diverse-speaker-workshops-august-2021/\">Allyship for WordPress Event Organizers</a>” workshop on August 19, 2021. The event had 13 attendees from six countries who reported a 52% increase in preparedness to help create inclusive WordPress events. <a href=\"https://make.wordpress.org/community/tag/wpdiversityworkshops/\">Stay tuned</a> for their next workshop in November!</li></ul>\n\n\n\n<hr class=\"wp-block-separator\" />\n\n\n\n<h2>Further Reading</h2>\n\n\n\n<ul><li>Don’t miss this blog post on <a href=\"https://wordpress.org/news/2021/08/widgets-in-wordpress-5-8-and-beyond/\">Widgets in WordPress 5.8 and beyond</a>!</li><li>The Full Site Editing (FSE) Outreach program wrapped up its ninth challenge –– <a href=\"https://make.wordpress.org/test/2021/08/11/fse-program-testing-call-9-handling-highered-headers/\">Handling HigherEd Headers</a>, on September 1. Keep an eye out for <a href=\"https://make.wordpress.org/test/tag/fse-outreach-program/\">test results and future testing call announcements</a>!</li><li>The Test Team is <a href=\"https://make.wordpress.org/test/2021/08/19/test-team-chat-summary-17-august-2021/\">attempting to modernize PHPUnit tests</a> for WordPress.</li><li>The Accessibility Team is testing the <a href=\"https://make.wordpress.org/accessibility/2021/08/20/accessibility-team-meeting-notes-august-20-2021/\">navigation block</a> for accessibility feedback.</li><li>The Docs Team <a href=\"https://make.wordpress.org/docs/2021/08/31/summary-of-docs-team-meeting-aug-24-2021/\">launched a new repository</a> for tracking issues with WordPress documentation: <a href=\"https://github.com/WordPress/Documentation-Issue-Tracker\">https://github.com/WordPress/Documentation-Issue-Tracker</a></li><li>The Training Team <a href=\"https://make.wordpress.org/training/2021/08/17/proposal-brand-guidelines-for-learn-wordpress-content/#comment-3130\">finalized</a> brand <a href=\"https://make.wordpress.org/training/2021/08/17/proposal-brand-guidelines-for-learn-wordpress-content/\">guidelines</a> for <a href=\"https://learn.wordpress.org\">learn.wordpress.org</a>. The team is also working on setting up <a href=\"https://make.wordpress.org/training/2021/08/12/wordpress-101-microcourses-proposal/\">Micro courses for Learn</a>.</li><li>The Openverse Team is <a href=\"https://make.wordpress.org/openverse/2021/08/16/multi-stage-docker-builds-in-the-openverse-api/\">exploring Multistage docker builds</a> as a solution to better set up waveforms for audio search results in the Openverse tool.&nbsp;</li></ul>\n\n\n\n<p><em>Have a story that we should include in the next “Month in WordPress” post? Please </em><a href=\"https://make.wordpress.org/community/month-in-wordpress-submissions/\"><em>submit it using this form</em></a><em>.&nbsp;</em></p>\n\n\n\n<p><em>The following folks contributed to August’s Month in WordPress:&nbsp; <a href=\'https://profiles.wordpress.org/evarlese/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>evarlese</a> <a href=\'https://profiles.wordpress.org/meher/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>meher</a> <em><a href=\'https://profiles.wordpress.org/nao/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>nao</a></em> <a href=\'https://profiles.wordpress.org/jillbinder/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>jillbinder</a></em> <em><a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a></em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"11176\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}s:27:\"http://www.w3.org/2005/Atom\";a:1:{s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:4:\"href\";s:32:\"https://wordpress.org/news/feed/\";s:3:\"rel\";s:4:\"self\";s:4:\"type\";s:19:\"application/rss+xml\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:44:\"http://purl.org/rss/1.0/modules/syndication/\";a:2:{s:12:\"updatePeriod\";a:1:{i:0;a:5:{s:4:\"data\";s:9:\"\n	hourly	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:15:\"updateFrequency\";a:1:{i:0;a:5:{s:4:\"data\";s:4:\"\n	1	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:4:\"site\";a:1:{i:0;a:5:{s:4:\"data\";s:8:\"14607090\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}}}}}}s:4:\"type\";i:128;s:7:\"headers\";O:42:\"Requests_Utility_CaseInsensitiveDictionary\":1:{s:7:\"\0*\0data\";a:9:{s:6:\"server\";s:5:\"nginx\";s:4:\"date\";s:29:\"Tue, 30 Nov 2021 10:49:19 GMT\";s:12:\"content-type\";s:34:\"application/rss+xml; charset=UTF-8\";s:25:\"strict-transport-security\";s:11:\"max-age=360\";s:6:\"x-olaf\";s:3:\"⛄\";s:13:\"last-modified\";s:29:\"Mon, 29 Nov 2021 13:25:05 GMT\";s:4:\"link\";s:63:\"<https://wordpress.org/news/wp-json/>; rel=\"https://api.w.org/\"\";s:15:\"x-frame-options\";s:10:\"SAMEORIGIN\";s:4:\"x-nc\";s:9:\"HIT ord 2\";}}s:5:\"build\";s:14:\"20201016172007\";}', 'no');
